import { Component, OnInit, AfterViewInit, ViewChild, ElementRef } from '@angular/core';
import { ForgetPasswordService } from './forget-password.service';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { NgbModalRef } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'ic-forget-password',
  templateUrl: './forget-password.component.html',
})
export class ForgetPasswordComponent implements OnInit, AfterViewInit {
  hasRecallQuestion: boolean | undefined;
  needSetRecallQuestion: boolean | undefined;
  error: string | null = null;
  badRequestError: any;
  success: string | null = null;
  @ViewChild('username', { static: false })
  username?: ElementRef;
  recallAns: string | undefined;
  modalRef: NgbModalRef | undefined;

  forgetPwdForm = this.fb.group({
    username: [null, [Validators.required, Validators.minLength(4), Validators.maxLength(50)]],
    email: [],
    recallQuestion: [],
    recallAns: [null, [Validators.minLength(4), Validators.maxLength(50)]],
  });

  constructor(private forgetPasswordService: ForgetPasswordService, private router: Router, private fb: FormBuilder) {}

  ngOnInit(): void {
    this.hasRecallQuestion = false;
  }

  ngAfterViewInit(): void {
    if (this.username) {
      this.username.nativeElement.focus();
    }
  }

  getRecallQuestion(): void {
    this.needSetRecallQuestion = false;
    this.forgetPasswordService.getRecalQuestion({ username: this.forgetPwdForm.get(['username'])!.value }).subscribe(
      responseData => {
        this.forgetPwdForm.patchValue({ email: responseData.email });
        this.forgetPwdForm.patchValue({ recallQuestion: responseData.recallQuestion });
        this.hasRecallQuestion = true;
      },
      error => {
        this.error = error.error;
        if (this.error) {
          if (this.error.includes('question')) {
            this.needSetRecallQuestion = true;
          }
        }
      }
    );
  }

  requestReset(): void {
    this.forgetPasswordService
      .save({
        username: this.forgetPwdForm.get(['username'])!.value,
        recallAns: this.forgetPwdForm.get(['recallAns'])!.value,
      })
      .subscribe(
        response => {
          this.error = null;
          this.success = response.msg;
          document.getElementById('forgetPwdSuccessMsg')!.innerHTML = this.forgetPwdForm.get(['email'])!.value;
          document.getElementById('forgetPwdSuccessMsg')!.style.display = 'block';
        },
        error => {
          this.success = null;
          if (error.status === 400) {
            this.badRequestError = error;
          } else {
            this.error = error.headers.get('error');
            document.getElementById('forgetPwdErrorMsg')!.innerHTML = this.error!;
            document.getElementById('forgetPwdErrorMsg')!.style.display = 'block';
          }
        }
      );
  }

  setRecallQuestion(): void {
    this.router.navigate(['account/password/recall'], {
      queryParams: {
        username: this.forgetPwdForm.get(['username'])!.value,
      },
    });
  }

  goHome(): void {
    this.router.navigate(['']);
  }
}
<div>
  <div class="row justify-content-center">
    <div class="col-md-8">
      <h1 icTranslate="password.forget.title">Forget password</h1>

      <p class="alert alert-success" *ngIf="success">
        <span icTranslate="password.messages.reset.success">
          <strong>Password reset successfully!</strong>The new password has been send to
        </span>
      </p>

      <p class="alert alert-success" style="display: none" id="forgetPwdSuccessMsg"></p>

      <p class="alert-dismissible alert-danger alert" style="display: none" id="forgetPwdErrorMsg"></p>

      <div class="alert alert-danger" *ngIf="badRequestError" icTranslate="password.messages.reset.error">
        <strong>An error has occurred!</strong> The password could not be reset.
      </div>

      <form *ngIf="!success" name="form" role="form" [formGroup]="forgetPwdForm">
        <div class="rounded-pill" *ngIf="!hasRecallQuestion">
          <label class="form-control-label" icTranslate="global.form.username.label">Username</label>
          <input type="text" class="form-control" id="field_username" name="username" formControlName="username" />
        </div>
        <div
          *ngIf="forgetPwdForm.get('username')!.invalid && (forgetPwdForm.get('username')!.dirty || forgetPwdForm.get('username')!.touched)"
        >
          <small
            class="form-text text-danger"
            *ngIf="forgetPwdForm.get('username')?.errors?.required"
            icTranslate="entity.validation.required"
          >
            This field is required.
          </small>
          <small
            class="form-text text-danger"
            *ngIf="forgetPwdForm.get('username')?.errors?.minlength"
            icTranslate="entity.validation.minlength"
            [translateValues]="{ min: 4 }"
          >
            Minimum 4 characters required.
          </small>
          <small
            class="form-text text-danger"
            *ngIf="forgetPwdForm.get('username')?.errors?.maxlength"
            icTranslate="entity.validation.maxlength"
            [translateValues]="{ max: 50 }"
          >
            This field cannot be longer than 50 characters.
          </small>
        </div>

        <div class="rounded-pill" *ngIf="hasRecallQuestion">
          <label class="form-control-label" icTranslate="global.form.username.label">Username</label>
          <input type="text" class="form-control" id="field_user" name="user" formControlName="username" readonly />
        </div>

        <div class="rounded-pill" *ngIf="hasRecallQuestion">
          <label class="form-control-label" icTranslate="global.form.email.label">Email</label>
          <input type="text" class="form-control" id="field_email" name="email" formControlName="email" readonly />
        </div>

        <div class="rounded-pill" *ngIf="hasRecallQuestion">
          <label class="form-control-label" icTranslate="password.form.pwdrecallquestion">Password Recall Question</label>
          <input
            type="text"
            class="form-control"
            id="field_recallQuestion"
            name="recallQuestion"
            formControlName="recallQuestion"
            readonly
          />
        </div>

        <div class="rounded-pill" *ngIf="hasRecallQuestion">
          <label class="form-control-label" icTranslate="password.form.pwdrecallanswer">Password Recall Answer</label>
          <input
            type="text"
            class="form-control"
            id="field_recallAns"
            name="recallAns"
            placeholder="{{ 'password.form.pwdrecallanswer.placeholder' | translate }}"
            formControlName="recallAns"
          />

          <div
            *ngIf="
              forgetPwdForm.get('recallAns')!.invalid && (forgetPwdForm.get('recallAns')!.dirty || forgetPwdForm.get('recallAns')!.touched)
            "
          >
            <small
              class="form-text text-danger"
              *ngIf="forgetPwdForm.get('recallAns')?.errors?.required"
              icTranslate="entity.validation.required"
            >
              This field is required.
            </small>
            <small
              class="form-text text-danger"
              *ngIf="forgetPwdForm.get('recallAns')?.errors?.minlength"
              icTranslate="entity.validation.minlength"
              [translateValues]="{ min: 4 }"
            >
              Minimum 4 characters required.
            </small>
            <small
              class="form-text text-danger"
              *ngIf="forgetPwdForm.get('recallQuestion')?.errors?.maxlength"
              icTranslate="entity.validation.maxlength"
              [translateValues]="{ max: 50 }"
            >
              This field cannot be longer than 50 characters.
            </small>
          </div>
        </div>

        <button
          *ngIf="hasRecallQuestion"
          type="button"
          (click)="requestReset()"
          [disabled]="forgetPwdForm.invalid"
          class="btn btn-primary"
          icTranslate="password.form.submit"
        >
          Submit
        </button>
        <button
          *ngIf="!hasRecallQuestion"
          type="button"
          (click)="getRecallQuestion()"
          [disabled]="forgetPwdForm.invalid"
          class="btn btn-primary"
          icTranslate="password.form.next"
        >
          Next
        </button>
        <button
          *ngIf="needSetRecallQuestion"
          type="button"
          (click)="setRecallQuestion()"
          [disabled]="forgetPwdForm.invalid"
          class="btn btn-primary"
          icTranslate="password.form.setQuestion"
        >
          Set Question
        </button>
      </form>
    </div>
  </div>
</div>
