import {Component, Inject, LOCALE_ID, OnInit} from '@angular/core';
import {ActivatedRoute} from "@angular/router";
import {LatestClaimOwner} from "app/claim/model/latest-claim-owner.model";
import {SEClaimService} from "app/claim/se-claim/service/se-claim.service";
import {HttpParams} from "@angular/common/http";
import {DateUtilsService} from "app/core/util/date-utils.service";
import {Table} from "primeng/table";
import {ExportUtilsService} from "app/core/util/export-utils.service";
import {formatDate} from "@angular/common";
import {LatestItemOwner} from "app/claim/model/latest-item-owner.model";

@Component({
  selector: 'n2-view-latest-owner-se',
  templateUrl: './view-latest-owner-se.component.html',
  styleUrls: ['./view-latest-owner-se.component.scss']
})
export class ViewLatestOwnerSeComponent implements OnInit {

  params: any;
  latestClaimOwner: LatestClaimOwner;
  cols: any[];

  constructor(
    private route: ActivatedRoute,
    private seClaimService: SEClaimService,
    public dateUtilsService: DateUtilsService,
    private exportUtilsService: ExportUtilsService,
    @Inject(LOCALE_ID) protected localeId: string) {
    console.log("test");
  }

  ngOnInit(): void {
    this.route.queryParams.subscribe((params) => {
      this.params = params;

      let httpParams = new HttpParams();
      httpParams = httpParams.append("claimId",this.params.claimId);

      this.seClaimService.retrieveLatestOwners(httpParams).subscribe((res) => {
        if(res.data){
          this.latestClaimOwner = res.data;
        }
      });
    });
  }

  exportToCsv(dataTable: Table): void{
    const columns =
      [
        {field: 'sn', header: 'SN', index: 0},
        {field: 'itemName', header: 'Item', index: 1},
        {field: 'dataEntry', header: 'Data Entry', index: 2},
        {field: 'iras', header: 'Auth IRASLink', index: 3},
        {field: 'verifier', header: 'Verifier', index: 4},
        {field: 'certifier', header: 'Certifier', index: 5},
        {field: 'firstApprover',  header: 'Approver 1', index: 6},
        {field: 'secondApprover', header: 'Approver 2', index: 7},
      ];

    const dtoList:any[] = [];

    this.latestClaimOwner.latestItemOwnerList.forEach((latestItemOwner:LatestItemOwner, index:number) => {
      dtoList.push({
        sn: index + 1,
        itemName: latestItemOwner.itemName,
        dataEntry: latestItemOwner.dataEntryName,
        iras: latestItemOwner.irasName,
        certifier: latestItemOwner.certifierName,
        verifier: latestItemOwner.verifierName,
        firstApprover: latestItemOwner.firstApproverName,
        secondApprover: latestItemOwner.secondApproverName,
      })
      dtoList.push({
        sn: '',
        itemName: '',
        dataEntry: this.dateUtilsService.convertUnixToDateString(latestItemOwner.dataEntryDt,"DD-MM-YYYY"),
        iras: this.dateUtilsService.convertUnixToDateString(latestItemOwner.irasDt,"DD-MM-YYYY"),
        verifier: this.dateUtilsService.convertUnixToDateString(latestItemOwner.verifierDt,"DD-MM-YYYY"),
        certifier: this.dateUtilsService.convertUnixToDateString(latestItemOwner.certifierDt,"DD-MM-YYYY"),
        firstApprover: this.dateUtilsService.convertUnixToDateString(latestItemOwner.firstApproverDt,"DD-MM-YYYY"),
        secondApprover: this.dateUtilsService.convertUnixToDateString(latestItemOwner.secondApproverDt,"DD-MM-YYYY"),
      })
    });

    this.exportUtilsService.exportCSV(dataTable, dtoList,this.latestClaimOwner.latestItemOwnerList.length * 2, columns,
      "latest_owner_data_".concat(formatDate(new Date(),"dd-MM-YYYY", this.localeId)));
  }

}
