import { HttpParams } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { DateUtilsService } from 'app/core/util/date-utils.service';
import { IcCodeService } from 'app/ng-connect';
import { SEClaimService } from '../../service/se-claim.service';

@Component({
  selector: 'n2-view-iras',
  templateUrl: './view-iras.component.html',
  styleUrls: ['./view-iras.component.scss']
})
export class ViewIrasComponent implements OnInit {

  params: any;
  loading!: boolean;
  irasDetailData:any[] = [];
  natureOfTradeList:Map<string, any>= new Map<string, any>();


  constructor(
    public dateUtilsService: DateUtilsService,
    private route: ActivatedRoute,
    private codeService: IcCodeService,
    private seClaimService: SEClaimService
  ) {
    console.log("test")

   }

  ngOnInit(): void {
    this.route.queryParams.subscribe((params) => {
      this.params = params;
      this.getIrasDownloadDetails(this.params.claimId);
    });
    this.loadCodes();
  }

  loadCodes(): void {

    this.codeService.gets('nature_trade')
      .subscribe((codes: any[]) => {
        codes[0].forEach((x:any)=>
          this.natureOfTradeList.set(x.codeId, x.label['en-US'])
        )
      });
  }

  getIrasDownloadDetails(claimId: string):void{

    setTimeout(() => {
      let params = new HttpParams();
      params = params.append('claimId', claimId);
      this.seClaimService.retrieveIrasDownloadDetails({params}).subscribe(resp => {
        if (resp.body.data) {
          this.irasDetailData = resp.body.data;
        }
      });
    });

    this.loading = false;
  }

  getOccupDesc(code: string): string{
    return this.natureOfTradeList?.get(code) as string;
  }

}
