                    </div>
                </th>
                <th scope="col" rowspan="2">
                    <div class="p-d-flex p-jc-between">
                        <div>Emp. Income</div>
                    </div>
                </th>
                <th scope="col" rowspan="2">
                    <div class="p-d-flex p-jc-between">
                        <div>Net Emp. Income</div>
                    </div>
                </th>
                <th scope="col" rowspan="2">
                    <div class="p-d-flex p-jc-between">
                        <div>Occupation</div>
                    </div>
                </th>
                <th scope="colgroup" colspan="4">
                    <div class="p-d-flex p-jc-between nsp-align-center">
                        Form B
                    </div>
                </th>
            </tr>
            <tr>
                <th scope="col">Revenue</th>
                <th scope="col">Net Trade</th>
                <th scope="col">Occupation</th>
                <th scope="col">Acc. Period</th>
            </tr>
        </ng-template>
        <ng-template pTemplate="body" let-data let-rowIndex = "rowIndex" let-columns="columns">
            <tr>
                <td>{{ rowIndex + 1}}</td>
                <td>{{ data.retrievalDate }}</td>
                <td>{{ data.basisDesc }}</td>
                <td>{{ data.noa }}</td>
                <td>{{ data.incomeYr }}</td>
                <td>{{ data.tradeIncome  | number : '1.2-2'}}</td>
                <td>{{ data.empIncome  | number : '1.2-2'}}</td>
