                <td>{{ data.revenue  | number : '1.2-2'}}</td>
                <td>{{ data.netTrade  | number : '1.2-2'}}</td>
                <td><div *ngFor = 'let elem of data.occupationFormB.split("|")' [pTooltip]=getOccupDesc(elem)>{{ elem }}<br></div></td>
                <td><div *ngFor = 'let elem of data.acctgPeriod.split("|")'>{{ elem }}<br></div></td>
