import {Component, OnDestroy, OnInit} from '@angular/core';
import {ActivatedRoute, Router} from '@angular/router';
import {HttpResponse} from '@angular/common/http';
import {NgbActiveModal} from '@ng-bootstrap/ng-bootstrap';
import {AlertService} from 'app/core/util/alert.service';
import {EventManager} from 'app/core/util/event-manager.service';
import {RoleService} from './role.service';
import {RoleAssignPopupService} from 'app/admin/access-control';
import {Group} from '../group/group.model';

@Component({
  selector: 'ic-role-assign-groups-dialog',
  templateUrl: './role-assign-groups-dialog.component.html',
})
export class RoleAssignGroupsDialogComponent implements OnInit {
  isSaving?: boolean;
  resourceId?: string;
  searchedGroups: Group[] = [];
  chooseedGroups: Group[] = [];

  constructor(
    public activeModal: NgbActiveModal,
    private alertService: AlertService,
    private roleService: RoleService,
    private eventManager: EventManager,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.isSaving = false;
  }

  clear(): void {
    this.activeModal.dismiss('cancel');
    this.backToPrevious();
  }

  assign(): void {
    this.isSaving = true;
    if (this.chooseedGroups.length > 0) {
      this.roleService.assignGroups(this.resourceId, this.chooseedGroups).subscribe(() => {
        this.activeModal.dismiss('cancel');
        this.eventManager.broadcast({ name: 'assignedGroupListModification', content: 'OK' });
        this.backToPrevious();
      });
    }
  }

  searchUnAssignGroups(event: any): void {
    this.roleService
      .searchUnAssignedGroups({
        resourceId: this.resourceId,
        groupName: event.query,
        page: 0,
        size: 1000,
        sort: null,
      })
      .subscribe(
        (res: HttpResponse<any>) => {
          this.searchedGroups = res.body;
        },
        (res: HttpResponse<any>) => this.onError(res.body)
      );
  }

  private onError(error: any): void {
    this.alertService.addAlert({ type: 'danger', translationKey: error.message });
  }

  private backToPrevious(): void {
    setTimeout(() => {
      this.router.navigate(['admin/acm/ic-role/' + this.resourceId?.toString()]);
    }, 0);
  }
}

@Component({
  selector: 'ic-role-assign-groups-popup',
  template: '',
})
export class RoleAssignGroupsPopupComponent implements OnInit, OnDestroy {
  routeSub: any;

  constructor(private route: ActivatedRoute, private roleAssignPopupService: RoleAssignPopupService) {}

  ngOnInit(): void {
    this.routeSub = this.route.params.subscribe(params => {
      if (params['id']) {
        this.roleAssignPopupService.open(RoleAssignGroupsDialogComponent as Component, params['id']);
      }
    });
  }

  ngOnDestroy(): void {
    this.routeSub.unsubscribe();
  }
}
<div class="modal-header">
  <h4 class="modal-title" id="myGroupLabel">Assign Groups to Role</h4>
  <button type="button" class="close" data-bs-dismiss="modal" aria-hidden="true" (click)="clear()">&times;</button>
</div>

<div class="modal-body">
  <div class="rounded-pill row">
    <label class="col-sm-3 form-control-label">Search By Group Name</label>
    <div class="col-sm-9">
      <p-autoComplete
        class="ui-fluid"
        [(ngModel)]="chooseedGroups"
        name="chooseedGroups"
        [suggestions]="searchedGroups"
        (completeMethod)="searchUnAssignGroups($event)"
        field="groupName"
        [multiple]="true"
      ></p-autoComplete>
    </div>
  </div>
</div>

<div class="modal-footer">
  <button
    type="button"
    [disabled]="isSaving || !chooseedGroups || chooseedGroups.length === 0"
    class="btn btn-primary btn-sm"
    (click)="assign()"
  >
    <fa-icon [icon]="'save'"></fa-icon>&nbsp;<span>Assign Groups</span>
  </button>
  <button type="button" class="btn btn-secondary btn-sm" data-bs-dismiss="modal" (click)="clear()">
    <fa-icon [icon]="'ban'"></fa-icon>&nbsp;<span icTranslate="entity.action.cancel">Cancel</span>
  </button>
</div>
