/* eslint-disable @typescript-eslint/no-unsafe-return */
import { formatDate } from "@angular/common";
import { Injectable } from "@angular/core";
import { cloneDeep } from "lodash";
import { Table } from "primeng/table";


@Injectable({
    providedIn: 'root',
  })
  export class ExportUtilsService {

    private _dataTable: Table;

    exportCSV(dataTable: Table, dataList:any[], totalElem: number, header: 
        {field: string, header: string, index: number}[], fileName: string): void{
        console.log("exporting csv");
        this._dataTable = cloneDeep(dataTable);        
        this._dataTable.columns = header;
        this._dataTable.value = dataList;
        this._dataTable.rows = totalElem;
        this._dataTable.exportFilename = fileName;
        this._dataTable.exportCSV();
        } 
    }
