  getRealtimeCpf(trgPeriodStartDt) {
    let params = new http_1.HttpParams();
    params = params.append("trgStartDate", trgPeriodStartDt);
    params = params.append("nric", this.empClaim.claimDto.nric);
    params = params.append("csn", this.empClaim.empClaimDto.csn);
    this.ecClaimService.getRealtimeCpf(params).subscribe(res => {
      if (res.body.data) {
        this.realtimeCpf = res.body.data;
        this.realtimeCpf.cpfSnapshotInfoDtoList.sort((a, b) => a.relevantContributionYearMonth > b.relevantContributionYearMonth);
        let firstNonNull = false;
        let realtimeDivisor = 0;
        let momentStartYrMth = (0, moment_1.default)(trgPeriodStartDt, 'YYYY-MM-DD').subtract(11, 'months');
        let momentEndYrMth = (0, moment_1.default)(trgPeriodStartDt, 'YYYY-MM-DD');
        if (this.selectedAWLatestPeriod) {
          momentStartYrMth = (0, moment_1.default)(this.selectedAWLatestPeriod[0], 'MM-YYYY');
          momentEndYrMth = (0, moment_1.default)(this.selectedAWLatestPeriod[1], 'MM-YYYY');
        }
        // compute realtime divisor for AW
        while (momentStartYrMth.isSameOrBefore(momentEndYrMth)) {
          const cpfSnapshotInfo = this.realtimeCpf.cpfSnapshotInfoDtoList.find(info => (0, moment_1.default)(info.relevantContributionYearMonth, 'YYYY-MM-DD').format('MM-YYYY') === momentStartYrMth.format('MM-YYYY'));
          if (cpfSnapshotInfo && cpfSnapshotInfo.awAmount !== null) {
            firstNonNull = true;
            realtimeDivisor += 1;
          } else if (firstNonNull === true) {
            realtimeDivisor += 1;
          }
          momentStartYrMth = momentStartYrMth.add(1, 'months');
        }
        console.log('realtimeDivisor', realtimeDivisor);
        this.realtimeCpf.divisor = realtimeDivisor;
        if (this.owTableRealtime === undefined || this.owTableRealtime === null) {
          this.owTableRealtime = res.body.data;
          this.owTableRealtime.cpfSnapshotInfoDtoList.sort((a, b) => a.relevantContributionYearMonth > b.relevantContributionYearMonth);
        }
        if (this.awTableRealtime === undefined || this.awTableRealtime === null) {
          this.awTableRealtime = this.realtimeCpf;
        }
      }
    });
  }
