  computeRealtimeAW() {
    if (this.awTableRealtime) {
      let totalAmount = 0;
      this.awTableRealtime.cpfSnapshotInfoDtoList.forEach(info => {
        if (this.awTable.some(rowData => rowData.monthYear === (0, moment_1.default)(info.relevantContributionYearMonth, 'YYYY-MM-DD').format('MM-YYYY'))) {
          totalAmount += info.awAmount ? info.awAmount : 0;
        }
      });
      if (this.awTableRealtime.divisor !== 0) {
        return totalAmount / this.awTableRealtime.divisor;
      } else return null;
    } else if (this.realtimeCpf) {
      let totalAmount = 0;
      this.realtimeCpf.cpfSnapshotInfoDtoList.forEach(info => {
        if (this.awTable.some(rowData => rowData.monthYear === (0, moment_1.default)(info.relevantContributionYearMonth, 'YYYY-MM-DD').format('MM-YYYY'))) {
          totalAmount += info.awAmount ? info.awAmount : 0;
        }
      });
      if (this.realtimeCpf.divisor !== 0) {
        return totalAmount / this.realtimeCpf.divisor;
      } else return null;
    } else {
      return null;
    }
  }
