  buildPeriodAmtFormArray(cpfSnapshot) {
    const cpfSnapshotMap = new Map();
    cpfSnapshot.cpfSnapshotInfoDtoList.forEach(ss => {
      cpfSnapshotMap.set((0, moment_1.default)(ss.relevantContributionYearMonth).format('MM-YYYY'), ss);
