      while (this.dateUtilsService.isBefore(momentStartYrMth.toDate(), momentEndYrMth.toDate()) || this.dateUtilsService.isEquals(momentStartYrMth.toDate(), momentEndYrMth.toDate())) {
        const cpfSnapshotInfo = cpfSnapshotInfoList.find(info => (0, moment_1.default)(info.relevantContributionYearMonth, 'YYYY-MM-DD').format('MM-YYYY') === momentStartYrMth.format('MM-YYYY'));
        if (cpfSnapshotInfo && cpfSnapshotInfo.awAmount !== null) {
          firstNonNull = true;
          snapshotDivisor += 1;
          snapshotTotal += cpfSnapshotInfo.awAmount;
        } else if (firstNonNull === true) {
          snapshotDivisor += 1;
          snapshotTotal += cpfSnapshotInfo.awAmount;
        }
        momentStartYrMth = momentStartYrMth.add(1, 'months');
      }
      console.log('updateAWSnapshot - snapshotDivisor', snapshotDivisor);
      console.log('updateAWSnapshot - snapshotTotal', snapshotTotal);
      if (snapshotDivisor !== 0) {
