    const option1 = {
      label: period1,
      value: period1
    };
    this.ecoPeriodOptions.push(option1);
    const period2 = (0, moment_1.default)(new Date(claimStartDate)).subtract(7, 'months').format('MM-YYYY') + ' to ' + (0, moment_1.default)(String(claimStartDate)).subtract(2, 'months').format('MM-YYYY');
