  updateAMD() {
    const computeFormulaDto = this.constructComputeFormulaDto();
    if (computeFormulaDto) {
      this.formulaService.computeFormula(computeFormulaDto, null).subscribe({
        next: res => {
          if (res.body.data) {
            let computationString = null;
            let amtPayable = null;
            if (this.editItemForm.get('formulaCode').value === '13x') {
              computationString = this.editItemForm.get('computedFormula').value;
              amtPayable = this.editItemForm.get('computeAmt').value;
            } else {
              computationString = res.body.data?.computationString;
              amtPayable = res.body.data?.amtPayable;
            }
            if (this.amdItemForm) {
              // update existing item
              let itemStatus = this.amdItemForm.get('itemStatus').value;
              // re-open claim if claim is completed and item is of closed or approved type
              if (this.approveItemStatusList.includes(this.amdItemForm.get('itemStatus').value) || this.closedItemStatusList.includes(this.amdItemForm.get('itemStatus').value)) {
                itemStatus = 'PVE';
                const amdItemDetail = new ClaimItemDetail_model_1.ClaimItemDetail();
                amdItemDetail.itemType = 'AMD';
                amdItemDetail.latestStatus = 'PVE';
                this.loadItemStatusDropdown(amdItemDetail);
                if (this.empClaim.claimDto.latestStatus === 'COM') {
                  this.reopenClaim();
                }
              }
              this.amdItemForm.get('itemStatus').setValue(itemStatus);
              this.amdItemForm.get('itemName').setValue(this.editItemForm.get('itemName').value);
              this.amdItemForm.get('basis').setValue(this.editItemForm.get('basis').value);
              this.amdItemForm.get('apprAmt').setValue(this.editItemForm.get('apprAmt').value);
              this.amdItemForm.get('formulaCode').setValue(this.editItemForm.get('formulaCode').value);
              this.amdItemForm.get('formulaLabel').setValue(this.editItemForm.get('formulaLabel').value);
              this.amdItemForm.get('computedFormula').setValue(computationString);
              this.amdItemForm.get('computeAmt').setValue(amtPayable);
              this.amdItemForm.updateValueAndValidity();
              // sync with actual training section
              if (this.existingClaimItemMap.has(this.amdItemForm.get('claimItemId').value)) {
                this.emitChangesToActualTrgSection(this.amdItemForm);
              }
            } else {
              // create new item
              this.amdItemForm = this.formBuilder.group({
                claimItemId: [null],
                itemStatus: ['PVE'],
                itemName: [this.editItemForm.get('itemName').value],
                basis: [this.selectedBasis],
                apprAmt: [this.editItemForm?.get('apprAmt').value],
                formulaCode: [this.editItemForm?.get('formulaCode').value],
                formulaLabel: [this.editItemForm?.get('formulaLabel').value],
                computedFormula: [computationString],
                computeAmt: [amtPayable]
              });
              this.newItemInserted.set('AMD', true);
              this.itemsCardCollapseMap.set('AMD', true);
              const minItemDetail = new ClaimItemDetail_model_1.ClaimItemDetail();
              minItemDetail.itemType = 'AMD';
              minItemDetail.latestStatus = 'PVE';
              this.loadItemStatusDropdown(minItemDetail);
            }
            // check for specific formula and auto populate rest day
            this.populateRestDayAltSat();
          }
        },
        error: error => this.errorRespHandler(error)
      });
