import { HttpParams } from '@angular/common/http';
import { Component, EventEmitter, Input, OnInit, Output, SimpleChanges, ViewChild } from '@angular/core';
import { AbstractControl, FormArray, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Claim, ClaimItem, ClaimSuppDoc } from "app/claim/model/claim.model";
import { ECClaimService } from 'app/claim/ec-claim/service/ec-claim.service';
import { ClaimService } from 'app/claim/service/claim.service';
import { ViewRemarksComponent } from 'app/claim/pop-up/view-remarks/view-remarks.component';
import { DateUtilsService } from 'app/core/util/date-utils.service';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { DateValidator } from 'app/shared/validator/date-validator.directive';
import moment from 'moment';

import { FileUploadService } from 'app/shared/service/file-upload.service';
import { CommonValidator } from 'app/shared/validator/common-validator.directive';
import { ClaimItemDetail, CpfSnapshotInfo, EmpClaimItemAW, EmpClaimItemDF, EmpClaimItemInfo, EmpClaimItemOverseas, EmpClaimItemOW } from '../../model/ClaimItemDetail.model';
import { CpfSnapshot, EmpClaimRequest } from '../../model/EmpClaimRequest.model';
import { FormulaService } from 'app/shared/service/formula.service';
import { IComputeFormula } from 'app/shared/model/compute-formula.model';
import { FileUploadComponent } from 'app/shared/component/file-upload/file-upload.component';
import { FileUploadValidator2 } from 'app/shared/validator/fileupload2.directive';
import { NSPAppConstants } from 'app/shared/constants/nspapp.constants'
import { ClaimSuppDocComponent } from 'app/claim/pop-up/view-supporting-doc/claim-supp-doc/claim-supp-doc.component';
import claim from '../../../../../i18n/en/claim.json';
import { PublicHolidayService } from 'app/administration/black-out-date/public-holiday/service/public-holiday.service';
import { AccountService } from 'app/core/auth/account.service';
import { format } from 'path';

const resolvedPromise = Promise.resolve(null);
@Component({
  selector: 'n2-claim-section',
  templateUrl: './claim-section.component.html',
  styleUrls: ['./claim-section.component.scss']
})
export class ClaimSectionComponent implements OnInit {

  @Input() empClaim:EmpClaimRequest;
  @Input() claimItemDetail:ClaimItemDetail[];
  @Input() subject:any;
  @Input() existingClaimItemMap:Map<string, FormGroup>;
  @Input() fullItemStatusMap: Map<string, object>;
  @Input() disableInput:boolean;
  @Input() selected:boolean;

  // dropdown box ui purposes
  readonly itemTypes = [
    {label: 'ORDINARY WAGE', value: 'ORD'},
    {label: 'ADDITIONAL WAGE', value: 'ADD'},
    {label: 'DIRECTOR FEE', value: 'DF'},
    {label: 'INTERN', value: 'INT'},
    {label: 'OVERSEAS', value: 'OVE'},
    {label: 'MINISTRY/STATBOARD', value: 'MIN'},
    {label: 'OTHERS', value: 'ECO'}
  ];

  // dropdown box ui purposes (value have to change to ONB for non-basic salary except Others(ECO))
  readonly oldItemTypes = [
    {label: 'BASIC SALARY', value: 'OBS'},
    {label: 'ADMIN', value: 'ONBADMIN'},
    {label: 'ALLOWANCE', value: 'ONBALLOWANCE'},
    {label: 'CAR', value: 'ONBCAR'},
    {label: 'COMMISSION-PERSONAL', value: 'ONBCOMMISSION-PERSONAL'},
    {label: 'COMISSION-TEAM', value: 'ONBCOMISSION-TEAM'},
    {label: 'DIRECTOR FEE', value: 'DIO'},
    {label: 'HANDPHONE', value: 'ONBHANDPHONE'},
    {label: 'INCENTIVE', value: 'ONBINCENTIVE'},
    {label: 'MVC', value: 'ONBMVC'},
    {label: 'OVERTIME', value: 'ONBOVERTIME'},
    {label: 'PERFORMANCE INCENTIVE', value: 'ONBPERFORMANCE INCENTIVE'},
    {label: 'SALES INCENTIVE', value: 'ONBSALES INCENTIVE'},
    {label: 'SHIFT', value: 'ONBSHIFT'},
    {label: 'TRANSPORT', value: 'ONBTRANSPORT'},
    {label: 'WAGES', value: 'ONBWAGES'},
    {label: 'OTHERS', value: 'ECO'}
  ];

  readonly claimBasisOption = [
    {label: 'Per Month', value: 'PM '},
    {label: 'Per Day', value: 'PD '},
    {label: 'Per ICT', value: 'ICT'}
  ]

  readonly directorFeeTypeOption = [
    {label: 'Provisional', value: 'P'},
    {label: 'Actual', value: 'A'},
  ];

  readonly directorFeeFrequencyOption = [
    {label: 'MONTHLY', value: 'DIM'},
    {label: 'ANNUAL', value: 'DIA'}
  ];

  readonly natureOption = [
    {label: 'FIXED', value: 'F'},
    {label: 'VARIABLE', value: 'V'}
  ]

  readonly restDayFormulaLogic = ['8a ', '8b ', '10a', '10b']; // auto populate alternate sat
  readonly approveItemStatusList = ['APP', 'APC', 'ANP', 'ASY']; // all approved item status
  readonly closedItemStatusList = ['COR', 'CNF', 'CDF', 'CNS', 'CCO', 'CPY']; // all closed item status


  // allowable extension for file upload
  readonly ALLOWABLE_EXTENSION: string = ".jpg,.png,.gif,.pdf,.csv,.txt";

  claimJson: any;

  isCurrentClaimOldPolicy = false;
  hasHistoricalPendApproval = false;
  halfdayInd = true;
  currentDate = new Date();

  // formGroup
  claimForm: FormGroup;
  editItemForm: FormGroup;
  itemFormList: FormGroup[] = [];
  owItemForm: FormGroup;
  awItemForm: FormGroup;
  diaItemForm: FormGroup;
  dimItemForm: FormGroup;
  dioItemForm: FormGroup; // migrated directors feef
  intItemForm: FormGroup;
  minItemForm: FormGroup;
  oveItemForm: FormGroup;
  ecoItemFormList: FormGroup[] = [];
  amdItemForm: FormGroup;
  // ****
  // oldPoliciesItemFormList: FormGroup[] = [];
  obsItemForm: FormGroup;
  onbItemFormList: FormGroup[] = [];

  showAllItemDetails = true;
  currHardCopyDetails: any = {};
  occupationList: any[] = [];
  supportDocList: any[] = [];
  selectedDocument: any[] = [];
  totalRecords = 0;
  outsideCurYr = false;
  suppDocList: any[] = [];

  overlappingClaims: any[] = [];
  claimOption = "";
  minRestDate: Date = new Date();
  maxRestDate: Date = new Date();
  minMaxRestDate: any[]= [];
  altSaturdayList: Date[]=[];

  itemStatusOptionMap = new Map();
  itemLatestStatusMap: Map<string, string> = new Map(); // ngModel usage
  cpfSnapshot: CpfSnapshot[];

  // formula
  claimFormulaOption: any[];
  canRecompute = false;

  // edit item
  newItemInserted: Map<string, boolean> = new Map(); // true is new item added, false is already existed in claim
  showEditItemModal = false;
  insertNewItemType = false;
  itemTypeOptions: any[] = [];
  selectedItemType: string;
  selectedFormula: string;
  selectedBasis = 'PM ';
  itemName: string;
  realtimeCpf: any;
  showFileUpload = false;
  selectedNature: string; // other + obs + onb

  // edit-OW
  selectedOWLatestMonth: any;
  owCpfSnapshotInfoMap = new Map();
  owLatestMonthOptions: any[] = [];
  owCpfSnapshotMap = new Map();

  // edit-AW
  selectedAWLatestPeriod: any;
  awCpfSnapshotMap = new Map();
  awLatestPeriodOptions: any[] = [];

  // edit director-fee
  selecteddfFrequency: string;
  diaManualComputeFileForm: FormGroup;
  dimManualComputeFileForm: FormGroup;
  selectedDIMType: string;
  existingDF = false;

  // edit-overseas
  selectedCountry: string;
  countryOptions: any[] = [];
  oveManualComputeFileForm: FormGroup;

  // edit-others
  selectedOtherIndex: number;
  ecoPeriodOptions: any[] = [];
  selectedPeriodECO: string;

  // edit-old policy (non-basic)
  selectedOnbIndex: number;
  onbPeriodOptions: any[] = [];
  selectedPeriodONB: string;

  // items detail
  ecoItemList: ClaimItemDetail[] = [];
  onbItemList: ClaimItemDetail[] = [];

  diaHasManualCompute = false;
  dimHasManualCompute = false;
  oveHasManualCompute = false;

  // OW/AW table realtime
  owTableRealtime: any;
  awTableRealtime: any;

  viewSuppDocParams: any;

  // items table
  // owTableDataMap: Map<string, any>;
  awTable: any[] = [];
  itemsCardCollapseMap: Map<string, boolean> = new Map([]);

  @Output() syncWithActualTrgDetail: EventEmitter<any> = new EventEmitter<any>();
  @Output() trgStartDteEvent: EventEmitter<any> = new EventEmitter<any>();
  @Output() selectedChange = new EventEmitter<boolean>();
  @Output() disableInputChange = new EventEmitter<boolean>();

  @ViewChild('claimSuppDocSaved', {static: false})
