  ngOnInit(): void {
    console.log('Claim Child component checked');
    console.log('empClaim from child', this.empClaim);

    this.isCurrentClaimOldPolicy = moment(this.empClaim.claimDto.trgStartDate).isBefore(moment(NSPAppConstants.CLAIM_NEW_POLICY_CUT_OFF_DATE, 'DD-MM-YYYY'));

    this.initialiseFormGroup();
    this.loadSupportingDocument();
    this.getRealtimeCpf(String(this.empClaim.claimDto.trgStartDate));

    if (this.claimItemDetail) {
      this.claimItemDetail.forEach(item => {
        this.loadItemStatusDropdown(item);
      });
      this.loadItems();
    }

    let overlappingParams = new HttpParams();
    overlappingParams = overlappingParams.append('claimId', this.empClaim.claimDto.id);
    overlappingParams = overlappingParams.append('nric', this.empClaim.claimDto.nric);
    overlappingParams = overlappingParams.append('claimStartDt', this.empClaim.claimDto.trgStartDate);
    overlappingParams = overlappingParams.append('claimEndDt', this.empClaim.claimDto.trgEndDate);
    this.getOverlappingClaims(overlappingParams);

    this.viewSuppDocParams = {
      nric: this.empClaim?.claimDto?.nric,
      name: this.empClaim?.nsManName,
      caseId: this.empClaim?.claimDto?.caseId,
      claimId: this.empClaim?.claimDto?.id
    }

