  }

  ngOnChanges(changes: SimpleChanges): void{
    console.log('CLAIM SECTION NGONCHANGES - TRIGGERED', changes);
    if (changes['disableInput'].currentValue) {
      const disableInput = changes['disableInput'].currentValue;
      if (disableInput === true) {
        this.claimForm.get('halfDayInd').disable();
        this.claimForm.get('workHourStart').disable();
        this.claimForm.get('workHourEnd').disable();
        this.claimForm.get('startPayPeriod').disable();
        this.claimForm.get('restDay').disable();
      }
    }
  }

  loadItemStatusDropdown(item: ClaimItemDetail) {
    let itemType = item.itemType;
    let params = new HttpParams();
    params = params.append("itemStatus",item.latestStatus);
    params = params.append("claimType",'E');
    params = params.append("station",'V');
    if (item.itemType === 'DIA' && 
      (item.latestStatus === 'PVE' || item.latestStatus === 'PIN' || item.latestStatus === 'PAC' || item.latestStatus === 'RAP' || item.latestStatus === 'PRA' || item.latestStatus === 'RRA')) {
      params = params.append("dfInd", 1);
    }
    this.claimService.retrieveDropDownItemStatus({params}).subscribe((itemStatusResp:any) => {
      const itemStatusDropdownList = itemStatusResp.body.data?.itemStatusDropdown?.map((dropdown: any) => {
        return {
          label: String(dropdown.statusDesc).toUpperCase(),
          value: dropdown.statusCode
        }
      });

      if (item.itemType === 'ECO') {
        const ecoItemName = 'ECO'+String(item.itemName);
        itemType = ecoItemName;
      } else if (item.itemType === 'ONB') {
        const onbItemName = 'ONB'+String(item.itemName);
        itemType = onbItemName;
      }

      this.itemStatusOptionMap.set(itemType, itemStatusDropdownList);
      this.itemLatestStatusMap.set(itemType, item.latestStatus);
    });
  }

  isItemStatusDropdown(itemType: string, arrayIndex: number = null): boolean {
    let item = itemType;

    if (itemType === 'ECO' && arrayIndex !== null) {
      const ecoFormItem = this.ecoItemFormList[arrayIndex];
      item = 'ECO'+String(ecoFormItem?.get('itemName').value);
    } else if (itemType === 'ONB' && arrayIndex !== null) {
      const onbFormItem = this.onbItemFormList[arrayIndex];
      item = 'ONB'+String(onbFormItem?.get('itemName').value);
    }
    const itemStatusOption = this.itemStatusOptionMap.get(item);
    return (itemStatusOption && itemStatusOption.length > 0);
  }

  /*---------- Claim Section ----------*/
  initialiseFormGroup() {
    const suppDocRes = this.empClaim.claimSuppDocList?.map((sp:any): any => {
      return {
        name: sp.fileName
      };
    });
    const restDayList = this.empClaim?.restDay.map((date: any) => {return new Date(date)});

    this.minRestDate = (moment(String(this.empClaim?.claimDto.trgStartDate), 'YYYY-MM-DD').startOf('month').toDate());
    this.maxRestDate = (moment(String(this.empClaim?.claimDto.trgEndDate), 'YYYY-MM-DD').endOf('month').toDate());

    const isSameTrgDay =  moment(this.empClaim?.claimDto.trgStartDate, 'YYYY-MM-DD').isSame(moment(this.empClaim?.claimDto.trgEndDate, 'YYYY-MM-DD')) ? true : false;

    this.claimForm = this.formBuilder.group({
      trgPeriodFg: this.formBuilder.group({
        trgPeriodStartDt: [new Date(moment(String(this.empClaim?.claimDto?.trgStartDate), 'YYYY-MM-DD').toDate()), [Validators.required]],
        trgPeriodEndDt: [new Date(moment(String(this.empClaim?.claimDto?.trgEndDate), 'YYYY-MM-DD').toDate()), [Validators.required]],
      }, { validators: [DateValidator.dateLessThan('trgPeriodStartDt', 'trgPeriodEndDt'),
           DateValidator.dateRangeMoreThan('trgPeriodStartDt', 'trgPeriodEndDt', 60)
          ]
      }),
      workHourStart: [isSameTrgDay ? this.empClaim?.claimDto?.hrWorkStart : null, [CommonValidator.numbersOnlyValidator()]],
      workHourEnd: [isSameTrgDay ? this.empClaim?.claimDto?.hrWorkEnd : null, [CommonValidator.numbersOnlyValidator()]],
      halfDayInd: [(this.empClaim?.empClaimDto?.halfDayInd === 'Y') ? true : false],
      occupation:  [this.empClaim?.empClaimDto?.occupn],
      commencementDate: [this.empClaim?.empClaimDto?.commenceDate],
      resignationDate: [this.empClaim?.empClaimDto?.resignationDate],
      startPayPeriod: [this.empClaim?.empClaimDto?.startPayPeriod, [CommonValidator.numbersOnlyValidator(), CommonValidator.numberRangeValidator(2, 31)]],
      restDay: [restDayList],
      supportingDocuments: [suppDocRes]
    }, {validators: [
        CommonValidator.mustNotEqualValidator('workHourStart', 'workHourEnd')
      ]
    });

    if (this.disableInput === true) {
      this.claimForm.get('halfDayInd').disable();
      this.claimForm.get('workHourStart').disable();
      this.claimForm.get('workHourEnd').disable();
      this.claimForm.get('startPayPeriod').disable();
      this.claimForm.get('restDay').disable();
    }

    this.claimFormValueChanges();
    this.changeFormGroupValidation();
  }

  claimFormValueChanges() {
    this.claimForm.get('trgPeriodFg.trgPeriodStartDt').valueChanges.subscribe(startDt => {
      if (startDt) {

        // check changes between old and new policy claim, then reset the items accordingly
        if (this.isClaimOldPolicy() === true && this.isCurrentClaimOldPolicy=== false) {
          this.isCurrentClaimOldPolicy = true;
        } else if (this.isClaimOldPolicy() === false && this.isCurrentClaimOldPolicy=== true) {
          this.isCurrentClaimOldPolicy = false;
        }

        // check for Rest Day changes
        const startMonth = moment(startDt).startOf('month');
        this.minRestDate = startMonth.toDate();
        const startPP = this.claimForm.get('startPayPeriod').value;
        if (startPP) {
            if (new Date(startDt).getDate() < Number(startPP)) {
                this.minRestDate = startMonth.subtract(1, 'months').toDate();
            }
        }

        // check for public holiday
        const year = (startDt as Date).getFullYear();
        this.validateHasHoliday(year, this.claimForm.get('trgPeriodFg.trgPeriodStartDt'));

        // check for same trg date
        // if (moment(startDt).format('YYYY-MM-DD') === moment(endDt).format('YYYY-MM-DD')) {
        if (this.isTrgSameDay()) {
            console.log('IS TRG SAME DAY');
            const trgPeriodStartDtControl = this.claimForm.get('workHourStart');
            trgPeriodStartDtControl.setValidators([Validators.required]);
            trgPeriodStartDtControl.updateValueAndValidity();
            const trgPeriodEndDtControl = this.claimForm.get('workHourEnd');
            trgPeriodEndDtControl.setValidators([Validators.required]);
            trgPeriodEndDtControl.updateValueAndValidity();

            this.claimForm.get('halfDayInd').setValue(true);
            this.halfdayInd = true;
        } else {
            this.claimForm.get('workHourStart').clearValidators();
            this.claimForm.get('workHourStart').setValue(null);
            this.claimForm.get('workHourStart').updateValueAndValidity();
            this.claimForm.get('workHourEnd').clearValidators();
            this.claimForm.get('workHourEnd').setValue(null);
            this.claimForm.get('workHourEnd').updateValueAndValidity();

            this.claimForm.get('halfDayInd').setValue(false);
            this.halfdayInd = false;
        }

        this.onBlurTrgStartEndDt();

      }
  });

  this.claimForm.get('trgPeriodFg.trgPeriodEndDt').valueChanges.subscribe(endDt => {
      if (endDt) {

          // check for Rest Day changes
          const endMonth = moment(endDt).endOf('month');
          this.maxRestDate = endMonth.toDate();
          const startPP = this.claimForm.get('startPayPeriod').value;
          if (startPP) {
              if (new Date(endDt).getDate() > Number(startPP-1)) {
                  this.maxRestDate = endMonth.add(1, 'months').toDate();
              }
          }
          this.populateRestDayAltSat();

          // check for public holiday
          const year = (endDt as Date).getFullYear();
          this.validateHasHoliday(year, this.claimForm.get('trgPeriodFg.trgPeriodEndDt'));

          // check for same trg date
          // if (moment(startDt).format('YYYY-MM-DD') === moment(endDt).format('YYYY-MM-DD')) {
          if (this.isTrgSameDay()) {
              console.log('IS TRG SAME DAY');
              const trgPeriodStartDtControl = this.claimForm.get('workHourStart');
              trgPeriodStartDtControl.setValidators([Validators.required]);
              trgPeriodStartDtControl.updateValueAndValidity();
              const trgPeriodEndDtControl = this.claimForm.get('workHourEnd');
              trgPeriodEndDtControl.setValidators([Validators.required]);
              trgPeriodEndDtControl.updateValueAndValidity();

              this.claimForm.get('halfDayInd').setValue(true);
              this.halfdayInd = true;
          } else {
              this.claimForm.get('workHourStart').clearValidators();
              this.claimForm.get('workHourStart').setValue(null);
              this.claimForm.get('workHourStart').updateValueAndValidity();
              this.claimForm.get('workHourEnd').clearValidators();
              this.claimForm.get('workHourEnd').setValue(null);
              this.claimForm.get('workHourEnd').updateValueAndValidity();

              this.claimForm.get('halfDayInd').setValue(false);
              this.halfdayInd = false;
          }

          this.onBlurTrgStartEndDt();
      }
    });
  }

  changeFormGroupValidation() {

    if (moment(this.empClaim.claimDto.trgStartDate, 'YYYY-MM-DD').format('DD-MM-YYYY') === moment(this.empClaim.claimDto.trgEndDate, 'YYYY-MM-DD').format('DD-MM-YYYY')) {
      const trgPeriodStartDtControl = this.claimForm.get('workHourStart');
      trgPeriodStartDtControl.setValidators(Validators.required);
      trgPeriodStartDtControl.updateValueAndValidity();

      const trgPeriodEndDtControl = this.claimForm.get('workHourEnd');
      trgPeriodEndDtControl.setValidators(Validators.required);
      trgPeriodEndDtControl.updateValueAndValidity();
    }
  }

