  isClaimOldPolicy(): boolean {
    if (moment(this.claimForm.get('trgPeriodFg.trgPeriodStartDt').value).isBefore(moment(NSPAppConstants.CLAIM_NEW_POLICY_CUT_OFF_DATE, 'DD-MM-YYYY'))) {
        return true;
    } else return false;
  }

  isTrgSameDay(): boolean {
    return moment(this.claimForm?.get('trgPeriodFg.trgPeriodStartDt').value).isSame(moment(this.claimForm?.get('trgPeriodFg.trgPeriodEndDt').value)) ? true : false;
  }

  isLongPeriod(): boolean {
    if (this.claimForm.get('trgPeriodFg.trgPeriodStartDt').value && this.claimForm.get('trgPeriodFg.trgPeriodEndDt').value){
      const diffDays = this.dateUtilsService.getDaysBetween((this.claimForm.get('trgPeriodFg.trgPeriodStartDt').value as Date),(this.claimForm.get('trgPeriodFg.trgPeriodEndDt').value as Date));
      return diffDays>30 && diffDays<60;
    } else return false;
  }

  isComputeAmtZero(): boolean {
    // console.log('isComputeAmtZero', this.editItemForm.get('computeAmt')?.value && Number(this.editItemForm.get('computeAmt').value === 0));
    return this.editItemForm.get('computeAmt')?.value && Number(this.editItemForm.get('computeAmt').value) === 0;
  }

  isLateClaim(): boolean{
    if (this.claimForm.get('trgPeriodFg.trgPeriodStartDt').value) {
      const claimStart = this.claimForm.get('trgPeriodFg.trgPeriodStartDt')?.value;
      const dateNow  = new Date();
      const threshold = moment(dateNow).subtract(1, 'years').subtract(1, 'days').toDate();
      return claimStart<threshold;
    } else return false;
  }

  workHourStartMoreThanEnd() {
    if (this.claimForm.get('workHourStart').value && this.claimForm.get('workHourEnd').value){
        return this.claimForm.get('workHourStart').value > this.claimForm.get('workHourEnd').value;
    } else return false;
  }

