    this.claimService.getByItemStatusHistorical({params}).subscribe((resp:any) => {
      if(resp.status === 200 && resp.body.data){

        this.hasHistoricalPendApproval = resp.body.data;

    }},
    (error: any) => {
      console.log("error is" + String(error?.error?.error_desc));
      this.toastService.error(String(error?.error?.error_desc));
    });
  }

  saveClaimSection(): any{
    //construct save claim section payload
    const claimDto = this.empClaim.claimDto;
    const empClaimDto = this.empClaim.empClaimDto;
    claimDto.trgStartDate = moment(this.claimForm.get('trgPeriodFg.trgPeriodStartDt')?.value).format('YYYY-MM-DD');
    claimDto.trgEndDate = moment(this.claimForm.get('trgPeriodFg.trgPeriodEndDt')?.value).format('YYYY-MM-DD');
    claimDto.hrWorkStart = this.claimForm.get('workHourStart')?.value;
    claimDto.hrWorkEnd = this.claimForm.get('workHourEnd')?.value;
    claimDto.latestStatus = 'PRO';
    empClaimDto.halfDayInd = this.claimForm.get('halfDayInd')?.value === true ? 'Y' : 'N';
    empClaimDto.startPayPeriod = this.claimForm.get('startPayPeriod')?.value;
    empClaimDto.occupn = this.claimForm.get('occupation')?.value;
    empClaimDto.commenceDate = this.claimForm.get('commencementDate')?.value;
    empClaimDto.resignationDate = this.claimForm.get('resignationDate')?.value;
    const selectedRestDay = this.claimForm.get('restDay')?.value;
    this.empClaim.restDay = selectedRestDay.map((restDay: any) => {
      return moment(restDay).format('YYYY-MM-DD');
    });
    this.empClaim.claimSuppDocList = this.supportDocList;
  }

  checkClaimItemsInvalid(): string {
    if (this.owItemForm && this.owItemForm.invalid) {
      return 'Ordinary Wage';
    }
    if (this.awItemForm && this.awItemForm.invalid) {
      return 'Additional Wage';
    }
    if (this.dimItemForm && this.dimItemForm.invalid) {
      return 'Director Fee (Monthly)';
    }
    if (this.dioItemForm && this.dioItemForm.invalid) {
      return 'Director Fee (old policy)';
    }
    if (this.diaItemForm && this.diaItemForm.invalid) {
      return 'Director Fee (Annual)';
    }
    if (this.intItemForm && this.intItemForm.invalid) {
      return 'Intern';
    }
    if (this.minItemForm && this.minItemForm.invalid) {
      return 'Ministry/';
    }
    if (this.oveItemForm && this.oveItemForm.invalid) {
      return 'Overseas';
    }
    if (this.amdItemForm && this.amdItemForm.invalid) {
      return 'Amend';
    }
    if (this.ecoItemFormList && this.ecoItemFormList.length > 0) {
      if (this.ecoItemFormList.some(ecoItemForm => ecoItemForm.invalid)) {
        const ecoForm = this.ecoItemFormList.find(ecoItemForm => ecoItemForm.invalid);
        return String(ecoForm?.get('itemName').value);
      }
    }
    if (this.onbItemFormList && this.onbItemFormList.length > 0) {
      if (this.onbItemFormList.some(onbItemForm => onbItemForm.invalid)) {
        const onbForm = this.onbItemFormList.find(onbItemForm => onbItemForm.invalid);
        return String(onbForm?.get('itemName').value);
      }
    }
    if (this.obsItemForm && this.obsItemForm.invalid) {
      return 'Old Policy Basic Salary';
    }

    return null;
  }

  // REST DAY LOGIC FOR SPECIFIC FORMULA
  populateRestDayAltSat() {
    this.restDayAltSatRevert();
    const restDayFc = this.claimForm.get('restDay');
    if (restDayFc.value && this.getAllAvailableItemForms().length > 0) {
      const restDayValue = [...restDayFc.value];
      console.log('restDayValue', restDayValue);
      // identify if any item formula has 8a, 8b, 10a, 10b
      if (this.getAllAvailableItemForms().some(fg => this.restDayFormulaLogic.includes(fg.get('formulaCode').value))) {
        let firstSaturday: Date = null;
        restDayFc.value.forEach((restDay: Date) => {
           if (moment(restDay).format('ddd').toUpperCase() === 'SAT') {
            if (firstSaturday === null) {
              firstSaturday = restDay;
            } else if (moment(firstSaturday).isAfter(restDay)) {
              firstSaturday = restDay;
            }
           }
        });

        if (firstSaturday !== null) {
          this.altSaturdayList = [];
          // find difference in weeks
          const weekDiff = moment(this.claimForm.get('trgPeriodFg.trgPeriodEndDt').value).diff(moment(firstSaturday), 'weeks');
          if (weekDiff > 0) {
            const listOfSubSaturdays = []; // subsequent saturdays (not including the selected sat)
            for (let i = 0; i < weekDiff; i++) {
              const alternateSat = moment(firstSaturday).add((14*(i+1)), 'days');
              if (alternateSat.isSameOrBefore(moment(this.claimForm.get('trgPeriodFg.trgPeriodEndDt').value)) && !(restDayValue.some(value => moment(value).format('YYYY-MM-DD') === alternateSat.format('YYYY-MM-DD')))) {
                listOfSubSaturdays.push(alternateSat.toDate());
              }
            }
            console.log('listOfSubSaturdays', listOfSubSaturdays);
            this.altSaturdayList = [...listOfSubSaturdays];
            restDayValue.push(...listOfSubSaturdays); // concat new list into the current list
            restDayFc.setValue(restDayValue, { emitEvent: false });
          }
        }
      } else {
        this.restDayAltSatRevert();
      }
    }
  }

  // only show 1 saturday when showing calendar (remove alternate saturday)
  restDayAltSatRevert() {
    if (this.altSaturdayList.length > 0) {
      const restDayFc = this.claimForm.get('restDay');
      const restDayFcValue = restDayFc.value.filter((value: Date) => {
        return !this.altSaturdayList.includes( value )
      });
      restDayFc.setValue(restDayFcValue, { emitEvent: false });
    }
  }

  restDayCustomWidth(): string {
    if (this.claimForm) {
        const maxWidth = 30;
        let width = 14; // default
        const eachValueWidth = 7;

        const restDayControl = this.claimForm.get('restDay');
        if (restDayControl.value && restDayControl.value.length > 2) {
            for (let i=0; i<restDayControl.value.length-2; i++) {
                width += eachValueWidth;
            }
            if (width > maxWidth) {
                return String(maxWidth)+'rem';
            } else {
                return String(width)+'rem';
            }
        } else {
            return '14rem';
        }
    } else {
        return '14rem';
    }
  }

