  loadAWTable(item: ClaimItemDetail) {
    // 1st table
    const empClaimItemAWDto = item.empClaimItemAWDto;

    const cpfSnapshotDto = item.cpfSnapshotDto;
    // compute awSnapshot amount (total / divisor)
    let awTotalSnapshotAmt = 0;
    cpfSnapshotDto.cpfSnapshotInfoDtoList.forEach(info => {
      awTotalSnapshotAmt += (info.awAmount) ? info.awAmount : 0;
    });
    const awSnapshotAmt = awTotalSnapshotAmt / empClaimItemAWDto.snapshotDivisor;

    // table header title
    const prepopulatedStr = (cpfSnapshotDto.prepopulatedInd === 'N') ? '' :
      (cpfSnapshotDto.prepopulatedInd === 'Y') ? '(Prepopulated)' :
      (cpfSnapshotDto.prepopulatedInd === 'S') ? '(Self-Initiated)' : '';
    const awStartYearMonth = moment(new Date(empClaimItemAWDto.periodYearMonth)).format('MM-YYYY');
    const awEndYearMonth = moment(new Date(empClaimItemAWDto.periodEndYearMonth)).format('MM-YYYY');
    const retrievedDate = cpfSnapshotDto.cpfSnapDate ? moment(new Date(cpfSnapshotDto.cpfSnapDate)).format('DD-MM-YYYY') : "NA";
    const awTableHeader = awStartYearMonth + ' to ' + awEndYearMonth + ' (' + String(cpfSnapshotDto?.createdByInit) + ')' + ' (' + retrievedDate + ') ' + prepopulatedStr;

    const latestContriMth = cpfSnapshotDto.latestContributionYearMonth ? moment(cpfSnapshotDto.latestContributionYearMonth).format('MM-YYYY') : "NA";

    this.selectedAWLatestPeriod = [awStartYearMonth, awEndYearMonth, item?.cpfSnapshotDto?.cpfSnapshotId];

    this.awItemForm = this.formBuilder.group({
      claimItemId: [item?.claimItemId],
      itemStatus: [item?.latestStatus],
      itemName: ['AW'],
      itemType: [item.itemType],
      header: [awTableHeader],
      basis: [item?.apprBasis, [Validators.required]],
      latestContriMonth: [latestContriMth],
      snapshot: [awSnapshotAmt],
      apprAmt: [item.apprAmt],
      divisorSnapshot: [empClaimItemAWDto.snapshotDivisor],
      divisorUser: [item?.allwDivisor, [Validators.required]],
      formulaCode: [item.formulaCode, [Validators.required]],
      formulaLabel: [item.formulaLabel],
      computedFormula: [item.computedFormula],
      computeAmt: [item.computedAmt],
      periodAmt: this.formBuilder.array([])
    });

    // 2nd table
    // Snapshot Column
    const cpfSnapshotInfoDtoList = cpfSnapshotDto.cpfSnapshotInfoDtoList;
    const cpfSnapshotInfoMap = new Map();
    cpfSnapshotInfoDtoList.forEach(ss => {
      cpfSnapshotInfoMap.set(moment(ss.relevantContributionYearMonth).format('MM-YYYY'), ss);
    });
    // User Column
    const itemInfo = item.empClaimItemInfoDtoList;
    const itemInfoMap = new Map();
    itemInfo.forEach(info => {
      itemInfoMap.set(moment(info.yrMth).format('MM-YYYY'), info);
    });

    const monthsDiff = moment(new Date(empClaimItemAWDto.periodEndYearMonth)).diff(moment(new Date(empClaimItemAWDto.periodYearMonth)), 'months');
    const periodAmtFormArray = this.awItemForm.get('periodAmt') as FormArray;
    for (let i=0; i<=monthsDiff; i++) {
      const yrMth = moment(new Date(empClaimItemAWDto.periodYearMonth)).add(i, 'months').format('MM-YYYY');
      const fg = this.formBuilder.group({
        monthYear: [yrMth],
        monthSnapshot: [cpfSnapshotInfoMap.has(yrMth) ? cpfSnapshotInfoMap.get(yrMth).awAmount : null],
        monthUser: [itemInfoMap.has(yrMth) ? itemInfoMap.get(yrMth).amount : 0, [Validators.required]]
      });
      const rowData = {
        monthYear: yrMth,
        monthSnapshot: cpfSnapshotInfoMap.has(yrMth) ? cpfSnapshotInfoMap.get(yrMth).awAmount : null,
        monthUser: itemInfoMap.has(yrMth) ? itemInfoMap.get(yrMth).amount : 0,
      }
      periodAmtFormArray.push(fg);
      this.awTable.push(rowData); // HTML using this for display. 
      // **somehow using formarray value to display will cause monthUser's NA amt to automatically become 0 when you cancel editing. 
    }

    // sync with actual training section
    if (this.existingClaimItemMap.has(item.claimItemId)) {
      this.existingClaimItemMap.set(item.claimItemId, this.awItemForm);
    }

    this.itemFormList.push(this.awItemForm);
  }

