          this.emitChangesToActualTrgSection(this.owItemForm);
        }
        break;
      }
      case 'ADD': {
        if (this.existingClaimItemMap.has(this.awItemForm.get('claimItemId').value)) {
          this.emitChangesToActualTrgSection(this.awItemForm);
        }
        break;
      }
      case 'DIM': {
        if (this.existingClaimItemMap.has(this.dimItemForm.get('claimItemId').value)) {
          this.emitChangesToActualTrgSection(this.dimItemForm);
        }
        break;
      }
      case 'DIA': {
        if (this.existingClaimItemMap.has(this.diaItemForm.get('claimItemId').value)) {
          this.emitChangesToActualTrgSection(this.diaItemForm);
        }
        break;
      }
      case 'DIO': {
        if (this.existingClaimItemMap.has(this.dioItemForm.get('claimItemId').value)) {
          this.emitChangesToActualTrgSection(this.dioItemForm);
        }
        break;
      }
      case 'INT': {
        if (this.existingClaimItemMap.has(this.intItemForm.get('claimItemId').value)) {
          this.emitChangesToActualTrgSection(this.intItemForm);
        }
        break;
      }
      case 'MIN': {
        if (this.existingClaimItemMap.has(this.minItemForm.get('claimItemId').value)) {
          this.emitChangesToActualTrgSection(this.minItemForm);
        }
        break;
      }
      case 'OVE': {
        if (this.existingClaimItemMap.has(this.oveItemForm.get('claimItemId').value)) {
          this.emitChangesToActualTrgSection(this.oveItemForm);
        }
        break;
      }
      case 'ECO': {
        const otherItemFg = this.ecoItemFormList.find(oItemFg => oItemFg.get('itemName').value === itemName);
        if (otherItemFg) {
          const mapKey = 'ECO'+String(itemName);
          if (this.existingClaimItemMap.has(otherItemFg.get('claimItemId').value)) {
            this.emitChangesToActualTrgSection(otherItemFg);
          }
        }
        break;
      }
      case 'OBS': {
        if (this.existingClaimItemMap.has(this.obsItemForm.get('claimItemId').value)) {
          this.emitChangesToActualTrgSection(this.obsItemForm);
        }
        break;
      }
      case 'ONB': {
        const onbItemFg = this.onbItemFormList.find(itemFg => itemFg.get('itemName').value === itemName);
        if (onbItemFg) {
          const mapKey = 'ONB'+String(itemName);
          if (this.existingClaimItemMap.has(onbItemFg.get('claimItemId').value)) {
            this.emitChangesToActualTrgSection(onbItemFg);
          }
        }
        break;
      }
      default: {
        break;
      }
    }
  }

