  /*---------- INSERT/EDIT ITEMS ----------*/
  displayEditItemModal(insertNewItemType: boolean) {
    if (this.editItemForm) {
      this.editItemForm.reset();
      this.editItemForm = null;
    }

    this.itemTypeOptions = [];
    this.insertNewItemType = insertNewItemType;
    if (insertNewItemType === true) {
      this.selectedItemType = null;
      this.selectedBasis = 'PM '; // default to PM

      if (this.isCurrentClaimOldPolicy === false) {
        this.itemTypes.forEach((it: any) => {
          // check directed fee
          if (it.value === 'DF') {
            // DF have 2 types (monthly and annual)
            if (this.newItemInserted.has('DIM') || this.newItemInserted.has('DIA')) {
              return;
            }
          } else if (this.newItemInserted.has(it.value)) {
            // if map has key, check if its OTHERS type
            if (it.value === 'ECO' && !this.itemTypeOptions.includes(it)) {
              this.itemTypeOptions.push(it);
            }
            return;
          }
          this.itemTypeOptions.push(it);
        });
      } else {
        // old policy items
        this.oldItemTypes.forEach((it: any) => {
          if (this.newItemInserted.has(it.value)) {
            // if map has key, check if its OTHERS type
            if (it.value === 'ECO' && !this.itemTypeOptions.includes(it)) {
              this.itemTypeOptions.push(it);
            }
            return;
          }
          this.itemTypeOptions.push(it);
        });
      }
    }
    this.showEditItemModal = true;
  }

  exitEditItemModal() {
    this.showEditItemModal = false;
  }

  editItem(itemType: string, arrayIndex:number = null) {
    console.log('itemType', itemType);
    this.insertNewItemType = false;
    if (itemType === 'DIA' || itemType === 'DIM') {
      this.selectedItemType = 'DF';
      this.selecteddfFrequency = itemType;
      console.log('selecteddfFrequency', this.selecteddfFrequency);
    } else {
      this.selectedItemType = itemType;
    }
    // this.getItemName();
    this.initialiseItemFormGroup(arrayIndex);
    this.showEditItemModal = true;
  }

  dfHasApprovedBefore(): boolean {
    if (this.selectedItemType === 'DF' && this.existingDF === true) {
      const df = this.claimItemDetail.find(item => item.itemType === 'DIA' || item.itemType === 'DIM' || item.itemType === 'DIO');
      const dfStatusList = df.itemStatusDtoList;
      return dfStatusList.some(status => status.status === 'APP');
    }
    return false;
  }

  buttonLabelForEditItem(): string {
    if (this.insertNewItemType === true) {
      return 'Submit';
    } else {
      return 'Update';
    }
  }

  initialiseItemFormGroup(arrayIndex:number = null) {
    console.log('initialiseItemFormGroup - selectedItemType', this.selectedItemType);
    if (this.selectedItemType) {
      if (this.isCurrentClaimOldPolicy === false) {
        switch(this.selectedItemType) {
          case 'ORD':
            this.selectedFormula = this.owItemForm?.get('formulaCode').value;
            this.selectedBasis = this.owItemForm?.get('basis').value ? this.owItemForm?.get('basis').value : 'PM ';
            this.updateFormulaOptions('E', this.selectedBasis);
            this.getOWLatestMonthOption();
            break;
          case 'ADD':
            this.selectedFormula = this.awItemForm?.get('formulaCode').value;
            this.selectedBasis = this.awItemForm?.get('basis').value ? this.awItemForm?.get('basis').value : 'PM ';
            this.updateFormulaOptions('E', this.selectedBasis);
            this.getAWLatestMonthOption();
            break;
          case 'DF': // DIRECTOR FEE
            this.setDirectorFeeFrequency();
            if (this.selecteddfFrequency === 'DIM') {
              this.showFileUpload = false;
              this.selectedFormula = this.dimItemForm?.get('formulaCode').value;
              this.selectedBasis = this.dimItemForm?.get('basis').value ? this.dimItemForm?.get('basis').value : 'PM ';
              this.updateFormulaOptions('E', this.selectedBasis);
              this.initialiseDIMForm();
            } else if (this.selecteddfFrequency === 'DIA') {
              this.showFileUpload = false;
              this.selectedFormula = this.diaItemForm?.get('formulaCode').value;
              this.selectedBasis = this.diaItemForm?.get('basis').value ? this.diaItemForm?.get('basis').value : 'PM ';
              this.updateFormulaOptions('E', this.selectedBasis);
              this.initialiseDIAForm();
            }
            break;
          case 'DIO':
              this.selectedFormula = this.dioItemForm?.get('formulaCode').value;
              this.selectedBasis = this.dioItemForm?.get('basis').value ? this.dioItemForm?.get('basis').value : 'PM ';
              this.updateFormulaOptions('E', this.selectedBasis);
              this.initialiseDIOForm();
              break;
          case 'INT':
            console.log('initialiseItemFormGroup - INT');
            this.selectedFormula = this.intItemForm?.get('formulaCode').value;
            this.selectedBasis = this.intItemForm?.get('basis').value ? this.intItemForm?.get('basis').value : 'PM ';
            this.updateFormulaOptions('E', this.selectedBasis);
            this.initialiseINTForm();
            break;
          case 'MIN':
            console.log('initialiseItemFormGroup - MIN');
            this.selectedFormula = this.minItemForm?.get('formulaCode').value;
            this.selectedBasis = this.minItemForm?.get('basis').value ? this.minItemForm?.get('basis').value : 'PM ';
            this.updateFormulaOptions('E', this.selectedBasis);
            this.initialiseMINForm();
            break;
          case 'OVE':
            this.showFileUpload = false;
            this.selectedFormula = this.oveItemForm?.get('formulaCode').value;
            this.selectedBasis = this.oveItemForm?.get('basis').value ? this.oveItemForm?.get('basis').value : 'PM ';
            this.updateFormulaOptions('E', this.selectedBasis);
            this.initialiseOVEForm();
            this.claimService.getCountryList().subscribe((res: any) => {
              if (res.body.data) {
                this.countryOptions = res.body.data;
              }
            });
            break;
          case 'ECO':
            console.log('ECO arrayIndex', arrayIndex);
            if (arrayIndex !== null) {
              this.selectedOtherIndex = arrayIndex;
              const otherItemForm = this.ecoItemFormList[arrayIndex];
              this.selectedFormula = otherItemForm?.get('formulaCode').value;
              this.selectedBasis = otherItemForm?.get('basis').value ? otherItemForm?.get('basis').value : 'PM ';
              this.selectedPeriodECO = String(otherItemForm?.get('mth1').value) + ' to ' + String(otherItemForm?.get('mth6').value);
            }
            this.updateFormulaOptions('E', this.selectedBasis);
            this.initialiseECOForm();
            break;
          case 'AMD':
            this.selectedFormula = this.amdItemForm?.get('formulaCode').value;
            this.selectedBasis = this.amdItemForm?.get('basis').value ? this.amdItemForm?.get('basis').value : 'PM ';
            this.updateFormulaOptions('E', this.selectedBasis);
            this.initialiseAMDForm();
          break;
          default:
            break;
        }
      } else {
        console.log('old policy item type', this.selectedItemType);

        switch(this.selectedItemType) {
          case 'OBS':
            this.selectedFormula = this.obsItemForm?.get('formulaCode').value;
            this.selectedBasis = this.obsItemForm?.get('basis').value ? this.obsItemForm?.get('basis').value : 'PM ';
            this.updateFormulaOptions('E', this.selectedBasis);
            this.initialiseOBSForm();
            break;
          case 'DIO':
            this.selectedFormula = this.dioItemForm?.get('formulaCode').value;
            this.selectedBasis = this.dioItemForm?.get('basis').value ? this.dioItemForm?.get('basis').value : 'PM ';
            this.updateFormulaOptions('E', this.selectedBasis);
            this.initialiseDIOForm();
            break;
          case 'ECO':
            console.log('ECO arrayIndex', arrayIndex);
            if (arrayIndex !== null) {
              this.selectedOtherIndex = arrayIndex;
              const otherItemForm = this.ecoItemFormList[arrayIndex];
              this.selectedFormula = otherItemForm?.get('formulaCode').value;
              this.selectedBasis = otherItemForm?.get('basis').value ? otherItemForm?.get('basis').value : 'PM ';
              this.selectedPeriodECO = String(otherItemForm?.get('mth1').value) + ' to ' + String(otherItemForm?.get('mth6').value);
            }
            this.updateFormulaOptions('E', this.selectedBasis);
            this.initialiseECOForm();
            break;
          default:
            console.log('ONB arrayIndex', arrayIndex);
            if (arrayIndex !== null) {
              this.selectedOnbIndex = arrayIndex;
              const onbItemForm = this.onbItemFormList[arrayIndex];
              this.selectedFormula = onbItemForm?.get('formulaCode').value;
              this.selectedBasis = onbItemForm?.get('basis').value ? onbItemForm?.get('basis').value : 'PM ';
              this.selectedPeriodONB = String(onbItemForm?.get('mth1').value) + ' to ' + String(onbItemForm?.get('mth6').value);
            }
            this.updateFormulaOptions('E', this.selectedBasis);
            this.initialiseONBForm();
            break;
        }
      }
    }
  }

  deleteItem(itemType: string, arrayIndex:number = null) {

    if (itemType === 'ECO' && arrayIndex !== null) {
      const ecoFormItem = this.ecoItemFormList[arrayIndex];
      const itemTypeName = 'ECO'+String(ecoFormItem?.get('itemName').value);
      this.newItemInserted.delete(itemTypeName);
      this.itemStatusOptionMap.delete(itemTypeName);
      this.ecoItemFormList.splice(arrayIndex);
      const itemDetail = this.claimItemDetail.find(item => item?.itemType === 'ECO' &&
        item?.itemName === String(ecoFormItem?.get('itemName').value));
      if (itemDetail) {
        this.claimItemDetail.splice(this.claimItemDetail.indexOf(itemDetail));
      }
      if (this.ecoItemFormList.length === 0) {
        this.itemsCardCollapseMap.delete('ECO');
      }
    } else if (itemType === 'ONB' && arrayIndex !== null) {
      const onbFormItem = this.onbItemFormList[arrayIndex];
      const itemTypeName = 'ONB'+String(onbFormItem?.get('itemName').value);
      this.newItemInserted.delete(itemTypeName);
      this.itemStatusOptionMap.delete(itemTypeName);
      this.onbItemFormList.splice(arrayIndex);
      const itemDetail = this.claimItemDetail.find(item => item?.itemType === 'ONB' &&
        item?.itemName === String(onbFormItem?.get('itemName').value));
      if (itemDetail) {
        this.claimItemDetail.splice(this.claimItemDetail.indexOf(itemDetail));
      }
      if (this.onbItemFormList.length === 0) {
        this.itemsCardCollapseMap.delete('ONB');
      }
    } else {
      this.newItemInserted.delete(itemType);
      this.itemsCardCollapseMap.delete(itemType);
      this.itemStatusOptionMap.delete(itemType);
      const itemDetail = this.claimItemDetail.find(item => item?.itemType === itemType);
      if (itemDetail) {
        this.claimItemDetail.splice(this.claimItemDetail.indexOf(itemDetail));
      }
      
      switch (itemType) {
        case 'ORD':
          this.owItemForm = null;
          break;
        case 'ADD':
          this.awItemForm = null;
          break;
        case 'MIN':
          this.minItemForm = null;
          break;
        case 'INT':
          this.intItemForm = null;
          break;
        case 'DIA':
          this.diaItemForm = null;
          break;
        case 'DIM':
          this.dimItemForm = null;
          break;
        case 'DIO':
            this.dioItemForm = null;
          break;
        case 'OVE':
          this.oveItemForm = null;
          break;
        default:
          break;
      }
    }
  }

  checkNewItem(itemType: string, arrayIndex:number = null): boolean {
    if (itemType === 'ECO' && arrayIndex !== null) {
      const ecoFormItem = this.ecoItemFormList[arrayIndex];
      const itemTypeName = 'ECO'+String(ecoFormItem?.get('itemName').value);
      return this.newItemInserted.get(itemTypeName);
    } else if (itemType === 'ONB' && arrayIndex !== null) {
      const onbFormItem = this.onbItemFormList[arrayIndex];
      const itemTypeName = 'ONB'+String(onbFormItem?.get('itemName').value);
      return this.newItemInserted.get(itemTypeName);
    } else {
      return this.newItemInserted.get(itemType);
    }
  }

  disableUpdateForEdit() {
    return (this.editItemForm && this.editItemForm.valid !== true) ? true : false;
  }

  setDirectorFeeFrequency() {
    if (this.selecteddfFrequency === null || this.selecteddfFrequency === undefined) {
      // default to Monthly if no existing claim item
      this.selecteddfFrequency = 'DIM';
