  getOWLatestMonthOption() {
    this.owLatestMonthOptions = [];
    this.ecClaimService.getCpfSnapshotByEmpClaimId(this.empClaim?.empClaimDto.id).subscribe((res: any) => {
      if (res.body.data) {
        this.cpfSnapshot = res.body.data;
        if (this.cpfSnapshot.length > 0) {
          this.cpfSnapshot.forEach(cpfSs => {
            const cpfSnapDate = cpfSs.cpfSnapDate;
            this.owCpfSnapshotMap.set(cpfSs.cpfSnapshotId, []);

            // table header title
            if (cpfSs.cpfSnapshotInfoDtoList.length > 0) {
              const prepopulatedStr = (cpfSs.prepopulatedInd === 'N') ? '' :
                (cpfSs.prepopulatedInd === 'Y') ? '(Prepopulated)' :
                (cpfSs.prepopulatedInd === 'S') ? '(Self-Initiated)' : '';
              const retrievedDate = moment(String(cpfSnapDate)).format('DD-MM-YYYY');
              // if the snapShotInfo have 15 rev months = these are the 1st set of data, take past 3 months
              // 16 months are the previously saved realtime, take past 4 months (inclusive of trg start month)
              const optionSize = (cpfSs.cpfSnapshotInfoDtoList.length > 15) ? 4 : 3;
              for (let i=0; i<optionSize; i++) {
                const cpfSnapshotInfoDto = cpfSs.cpfSnapshotInfoDtoList[cpfSs.cpfSnapshotInfoDtoList.length - (i+1)];
                const owMonthStr = moment(new Date(cpfSnapshotInfoDto.relevantContributionYearMonth)).format('MM-YYYY');
                const cpfLatestMonth = String(owMonthStr) + ' (' + String(cpfSs?.createdByInit) + ')' + ' (' + retrievedDate + ') ' + prepopulatedStr;
                const tableValue = [owMonthStr, cpfSs.cpfSnapshotId];
                this.owLatestMonthOptions.push({
                  label: cpfLatestMonth, value: tableValue
                });
                const owCpfSnapshotInfoMap = new Map();
                owCpfSnapshotInfoMap.set(owMonthStr, cpfSnapshotInfoDto);
                this.owCpfSnapshotMap.get(cpfSs.cpfSnapshotId).push(owCpfSnapshotInfoMap);

                if (String(this.owItemForm?.get('header').value).trim() === cpfLatestMonth.trim()) {
                  this.selectedOWLatestMonth = tableValue;
                }
              }
            }
          });
          // add realtime
          if (this.realtimeCpf) {
            // table header title
            const prepopulatedStr = (this.realtimeCpf.prepopulatedInd === 'N') ? '' :
              (this.realtimeCpf.prepopulatedInd === 'Y') ? '(Prepopulated)' :
              (this.realtimeCpf.prepopulatedInd === 'S') ? '(Self-Initiated)' : '';
            const retrievedDate = moment(new Date()).format('DD-MM-YYYY');
            if (this.realtimeCpf?.cpfSnapshotInfoDtoList.length > 0) {
              this.owCpfSnapshotMap.set('realtime', []);
              // take past 3 months + current trg start month
              for (let i=0; i<4; i++) {
                const momentMonthCounter = moment(new Date(this.claimForm.get('trgPeriodFg.trgPeriodStartDt').value)).subtract(i, 'months');
                const cpfSnapshotInfoDto = this.realtimeCpf.cpfSnapshotInfoDtoList.find((info: any) => 
                  moment(new Date(info.relevantContributionYearMonth)).format('MM-YYYY') === momentMonthCounter.format('MM-YYYY'));
                if (cpfSnapshotInfoDto) {
                  const owMonthStr = moment(new Date(cpfSnapshotInfoDto.relevantContributionYearMonth)).format('MM-YYYY');
                  const cpfLatestMonth = String(owMonthStr) + ' (SYS)' + ' (' + retrievedDate + ') ' + prepopulatedStr;
                  const tableValue = [owMonthStr, 'realtime'];
                  this.owLatestMonthOptions.push({
                    label: cpfLatestMonth, value: tableValue
                  });
                  const owCpfSnapshotInfoMap = new Map();
                  owCpfSnapshotInfoMap.set(owMonthStr, cpfSnapshotInfoDto);
                  this.owCpfSnapshotMap.get('realtime').push(owCpfSnapshotInfoMap);
  
                  if (String(this.owItemForm?.get('header').value).trim() === cpfLatestMonth.trim()) {
                    this.selectedOWLatestMonth = tableValue;
                  }
                }
              }
            }
          }
        } else {
          if (this.realtimeCpf) {
            const cpfSnapDate = this.realtimeCpf.cpfSnapDate;
            // table header title
            const prepopulatedStr = (this.realtimeCpf.prepopulatedInd === 'N') ? '' :
              (this.realtimeCpf.prepopulatedInd === 'Y') ? '(Prepopulated)' :
              (this.realtimeCpf.prepopulatedInd === 'S') ? '(Self-Initiated)' : '';
            const retrievedDate = moment(String(cpfSnapDate)).format('DD-MM-YYYY');
            if (this.realtimeCpf?.cpfSnapshotInfoDtoList.length > 0) {
              this.owCpfSnapshotMap.set('realtime', []);
              for (let i=0; i<4; i++) {
                const momentCurrentMonth = moment(new Date(this.claimForm.get('trgPeriodFg.trgPeriodStartDt').value)).subtract(i, 'months');
                const cpfSnapshotInfoDto = this.realtimeCpf.cpfSnapshotInfoDtoList.find((info: any) => 
                  moment(new Date(info.relevantContributionYearMonth)).format('MM-YYYY') === momentCurrentMonth.format('MM-YYYY'));
                if (cpfSnapshotInfoDto) {
                  const owMonthStr = moment(new Date(cpfSnapshotInfoDto.relevantContributionYearMonth)).format('MM-YYYY');
                  const cpfLatestMonth = String(owMonthStr) + ' (SYS)' + ' (' + retrievedDate + ') ' + prepopulatedStr;

                  const tableValue = [owMonthStr, 'realtime'];
                  this.owLatestMonthOptions.push({
                    label: cpfLatestMonth, value: tableValue
                  });
                  const owCpfSnapshotInfoMap = new Map();
                  owCpfSnapshotInfoMap.set(owMonthStr, cpfSnapshotInfoDto);
                  this.owCpfSnapshotMap.get('realtime').push(owCpfSnapshotInfoMap);

                  if (String(this.owItemForm?.get('header').value).trim() === cpfLatestMonth.trim()) {
                    this.selectedOWLatestMonth = tableValue;
                  }
                }
              }
            }
          } else {
            const owMonthStr = moment(String(this.empClaim?.claimDto?.trgStartDate)).subtract(1, 'months').format('MM-YYYY');
            const cpfLatestMonth = String(owMonthStr) + ' (' + moment().format('DD-MM-YYYY') + ') ';
            this.owLatestMonthOptions.push({
              label: cpfLatestMonth, value: 0
            });
          }
        }

        // if no default value, set first option as default
        if (this.selectedOWLatestMonth === undefined || this.selectedOWLatestMonth === null) {
          this.selectedOWLatestMonth = this.owLatestMonthOptions[0].value;
        }
        console.log('owLatestMonthOptions', this.owLatestMonthOptions);
        this.initialiseOWForm();
      }
    });
  }

