    if (computeFormulaDto) {
      this.formulaService.computeFormula(computeFormulaDto, null).subscribe({
        next: (res: any) => {
          if (res.body.data) {
            let computationString = null;
            let amtPayable = null;
            if (this.editItemForm.get('formulaCode').value === '13x') {
              computationString = this.editItemForm.get('computedFormula').value;
              amtPayable = this.editItemForm.get('computeAmt').value;
            } else {
              computationString = res.body.data?.computationString;
              amtPayable = res.body.data?.amtPayable;
            }
            if (this.owItemForm) {
              // update existing item
              let itemStatus = this.owItemForm.get('itemStatus').value;
              // re-open claim if claim is completed and item is of closed or approved type
              if ((this.approveItemStatusList.includes(this.owItemForm.get('itemStatus').value) || 
                this.closedItemStatusList.includes(this.owItemForm.get('itemStatus').value))) {
                  itemStatus = 'PVE';
                  const ordItemDetail = new ClaimItemDetail();
                  ordItemDetail.itemType = 'ORD';
                  ordItemDetail.latestStatus = 'PVE';
                  this.loadItemStatusDropdown(ordItemDetail);
                  if (this.empClaim.claimDto.latestStatus === 'COM') {
                    this.reopenClaim();
                  }   
              }

              this.owItemForm.get('itemStatus').setValue(itemStatus);
              this.owItemForm.get('itemName').setValue(this.editItemForm.get('itemName').value);
              this.owItemForm.get('basis').setValue(this.selectedBasis);
              this.owItemForm.get('header').setValue(this.editItemForm.get('header').value);
              this.owItemForm.get('latestContriMonth').setValue(this.editItemForm.get('latestContriMonth').value);
              this.owItemForm.get('snapshot').setValue(this.editItemForm.get('snapshot').value);
              this.owItemForm.get('apprAmt').setValue(this.editItemForm.get('apprAmt').value);
              this.owItemForm.get('bsSnapshot').setValue(this.editItemForm.get('bsSnapshot').value);
              this.owItemForm.get('bsUser').setValue(this.editItemForm.get('bsUser').value);
              this.owItemForm.get('faSnapshot').setValue(this.editItemForm.get('faSnapshot').value);
              this.owItemForm.get('faUser').setValue(this.editItemForm.get('faUser').value);
              this.owItemForm.get('vaSnapshot').setValue(this.editItemForm.get('vaSnapshot').value);
              this.owItemForm.get('vaUser').setValue(this.editItemForm.get('vaUser').value);
              this.owItemForm.get('dedSnapshot').setValue(this.editItemForm.get('dedSnapshot').value);
              this.owItemForm.get('dedUser').setValue(this.editItemForm.get('dedUser').value);
              this.owItemForm.get('snapshotTotalAmt').setValue(this.editItemForm.value.snapshotTotalAmt);
              this.owItemForm.get('userTotalAmt').setValue(this.editItemForm.value.userTotalAmt);
              this.owItemForm.get('formulaCode').setValue(this.editItemForm.get('formulaCode').value);
              this.owItemForm.get('formulaLabel').setValue(this.editItemForm.get('formulaLabel').value);
              this.owItemForm.get('computedFormula').setValue(computationString);
              this.owItemForm.get('computeAmt').setValue(amtPayable);

              this.owItemForm.updateValueAndValidity();
              console.log('updateOW itemStatus after update', this.owItemForm.get('itemStatus').value);

              // sync with actual training section
              if (this.existingClaimItemMap.has(this.owItemForm.get('claimItemId').value)) {
                this.emitChangesToActualTrgSection(this.owItemForm);
                // this.existingClaimItemMap.set(this.owItemForm.get('claimItemId').value, this.owItemForm);
              }
            } else {
              // create new item
              this.owItemForm = this.formBuilder.group({
                claimItemId: [null],
                itemStatus: ['PVE'],
                itemName: [this.editItemForm.get('itemName').value],
                basis: [this.selectedBasis],
                header: [this.editItemForm.get('header').value],
                latestContriMonth: [this.editItemForm.get('latestContriMonth').value],
                snapshot: [this.editItemForm.get('snapshot').value],
                apprAmt: [this.editItemForm.get('apprAmt').value],
                bsSnapshot: [this.editItemForm.get('bsSnapshot').value],
                bsUser: [this.editItemForm.get('bsUser').value],
                faSnapshot: [this.editItemForm.get('faSnapshot').value],
                faUser: [this.editItemForm.get('faUser').value],
                vaSnapshot: [this.editItemForm.get('vaSnapshot').value],
                vaUser: [this.editItemForm.get('vaUser').value],
                dedSnapshot:[this.editItemForm.get('dedSnapshot').value],
                dedUser: [this.editItemForm.get('dedUser').value],
                snapshotTotalAmt: [this.editItemForm.get('snapshotTotalAmt').value],
                userTotalAmt: [this.editItemForm.get('userTotalAmt').value],
                formulaCode: [this.editItemForm.get('formulaCode').value],
                formulaLabel: [this.editItemForm.get('formulaLabel').value],
                computedFormula: [computationString],
