  initialiseAWForm() {
    if (this.awItemForm) {
      this.selectedFormula = this.awItemForm?.get('formulaCode').value;
      this.editItemForm = this.formBuilder.group({
        claimItemId: [this.awItemForm?.get('claimItemId').value],
        itemStatus: [this.awItemForm?.get('itemStatus').value],
        itemName: [this.awItemForm?.get('itemName').value],
        basis: [this.awItemForm?.get('basis').value, [Validators.required]],
        header: [this.awItemForm?.get('header').value],
        latestContriMonth: [this.awItemForm?.get('latestContriMonth').value],
        snapshot: [this.awItemForm?.get('snapshot').value],
        apprAmt: [this.awItemForm?.get('apprAmt').value],
        divisorSnapshot: [this.awItemForm?.get('divisorSnapshot').value],
        divisorUser: [this.awItemForm?.get('divisorUser').value, [Validators.required]],
        formulaCode: [this.awItemForm?.get('formulaCode').value, [Validators.required]],
        formulaLabel: [this.awItemForm?.get('formulaLabel').value],
        computedFormula: [this.awItemForm?.get('computedFormula').value],
        computeAmt: [this.awItemForm?.get('computeAmt').value],
        periodAmt: this.addEditPeriodAmtArr()
        // periodAmt: this.awItemForm?.get('periodAmt') as FormArray
      });

      // this.periodAmtArr.controls.forEach((fg: FormGroup) => {
      //   if (fg.get('monthUser').value === undefined || fg.get('monthUser').value === null) {
      //     fg.get('monthUser').setValue(0);
      //   }
      //   fg.get('monthUser').setValidators(Validators.required);
      //   fg.get('monthUser').updateValueAndValidity();
      // });
    } else {
      const awTableHeader = this.awLatestPeriodOptions.length > 0 ? this.awLatestPeriodOptions[0].label : '';
      const tableValue = this.awLatestPeriodOptions[0].value;
      const cpfSnapshot = (tableValue[2] !== 'realtime') ?
        this.cpfSnapshot.find(cpfSs => cpfSs.cpfSnapshotId === tableValue[2]) : this.realtimeCpf;

      // find snapshot divisor
      let firstNonNull = false;
      let snapshotDivisor = 0;
      let snapshotTotal = 0;
      let momentStartYrMth = moment(tableValue[0], 'MM-YYYY');
      const momentEndYrMth = moment(tableValue[1], 'MM-YYYY');
      while(this.dateUtilsService.isBefore(momentStartYrMth.toDate(), momentEndYrMth.toDate()) || this.dateUtilsService.isEquals(momentStartYrMth.toDate(), momentEndYrMth.toDate())) {
        const cpfSnapshotInfo = cpfSnapshot?.cpfSnapshotInfoDtoList.find((info : CpfSnapshotInfo) =>
          moment(info.relevantContributionYearMonth, 'YYYY-MM-DD').format('MM-YYYY') === momentStartYrMth.format('MM-YYYY'));
        if (cpfSnapshotInfo && cpfSnapshotInfo.awAmount !== null) {
          firstNonNull = true;
          snapshotDivisor += 1;
          snapshotTotal += cpfSnapshotInfo.awAmount;
        } else if (firstNonNull === true) {
          snapshotDivisor += 1;
          snapshotTotal += cpfSnapshotInfo.awAmount;
        }
        momentStartYrMth = momentStartYrMth.add(1, 'months');
      }

      let awSnapshotAmt = 1;
      if (snapshotDivisor !== 0) {
        awSnapshotAmt = snapshotTotal / snapshotDivisor;
      }

      this.editItemForm = this.formBuilder.group({
        claimItemId: [null],
        itemStatus: ['PVE'],
        itemName: ['AW'],
        basis: [this.selectedBasis, [Validators.required]],
        header: [awTableHeader],
        latestContriMonth: [cpfSnapshot?.latestContributionYearMonth],
        snapshot: [awSnapshotAmt],
        apprAmt: [null],
        divisorSnapshot: [snapshotDivisor],
        divisorUser: [null, [Validators.required]],
        formulaCode: [null, [Validators.required]],
        formulaLabel: [''],
        computedFormula: [''],
        computeAmt: [''],
        periodAmt: this.buildPeriodAmtFormArray(cpfSnapshot)
      });
    }

    this.awEditItemFormOnChanges();
  }

  buildPeriodAmtFormArray(cpfSnapshot: CpfSnapshot): FormArray {
    const cpfSnapshotMap = new Map();
    cpfSnapshot.cpfSnapshotInfoDtoList.forEach(ss => {
      cpfSnapshotMap.set(moment(ss.relevantContributionYearMonth).format('MM-YYYY'), ss);
    });

    const periodAmt = this.formBuilder.array([]) as FormArray;
    for (let i=12; i>0; i--) {
      const yrMth = moment(this.selectedAWLatestPeriod[1], 'MM-YYYY').subtract(i, 'months').format('MM-YYYY');
      const rowData = {
        monthYear: [yrMth],
        monthSnapshot: [cpfSnapshotMap.has(yrMth) ? cpfSnapshotMap.get(yrMth).awAmount : null],
        monthUser: [0, [Validators.required]],
      }
      periodAmt.push(this.formBuilder.group(rowData));
    }
    return periodAmt;
  }

  awEditItemFormOnChanges() {
    this.editItemForm.get('periodAmt').valueChanges.subscribe(valueArr => {
      let updatedApprAmt = 0;
      valueArr.forEach((value: any) => {
        updatedApprAmt += value.monthUser;
      });
      const divisor = this.editItemForm.get('divisorUser').value;
      if (divisor !== 0) {
        updatedApprAmt = updatedApprAmt/divisor;
      }
      this.editItemForm.get('apprAmt').setValue(updatedApprAmt);
      this.editItemForm.get('apprAmt').updateValueAndValidity();
    });

    this.editItemForm.get('divisorUser').valueChanges.subscribe(value => {
      if (value !== 0) {
        let updatedApprAmt = 0;
        const periodAmtArr = this.editItemForm.get('periodAmt').value;
        periodAmtArr.forEach((amt: any) => {
          updatedApprAmt += amt.monthUser;
        });
        updatedApprAmt = updatedApprAmt/value;
        this.editItemForm.get('apprAmt').setValue(updatedApprAmt);
        this.editItemForm.get('apprAmt').updateValueAndValidity();
      }
    });
  }

  getAWLatestMonthOption() {
    this.ecClaimService.getCpfSnapshotByEmpClaimId(this.empClaim?.empClaimDto.id).subscribe((res: any) => {
      if (res.body.data) {
        this.awLatestPeriodOptions = [];
        this.cpfSnapshot = res.body.data;
        if (this.cpfSnapshot.length > 0) {
          this.cpfSnapshot.forEach(cpfSs => {
            this.awCpfSnapshotMap.set(cpfSs.cpfSnapshotId, []);
            // table header title
            const prepopulatedStr = (cpfSs.prepopulatedInd === 'N') ? '' :
              (cpfSs.prepopulatedInd === 'Y') ? '(Prepopulated)' :
              (cpfSs.prepopulatedInd === 'S') ? '(Self-Initiated)' : '';
            // table header title
            if (cpfSs.cpfSnapshotInfoDtoList.length > 0) {
              // if the snapShotInfo have 15 rev months = these are the 1st set of data, take past 3 months
              // 16 months are the previously saved realtime, take past 4 months (inclusive of trg start month)
              const optionSize = (cpfSs.cpfSnapshotInfoDtoList.length > 15) ? 4 : 3;
              for (let i=0; i<optionSize; i++) {
                const awStartYearMonth = moment(cpfSs.endYearMonth, 'YYYY-MM-DD').subtract((11+i), 'months').format('MM-YYYY');
                const awEndYearMonth = moment(cpfSs.endYearMonth, 'YYYY-MM-DD').subtract((i), 'months').format('MM-YYYY');
                const retrievedDate = moment(cpfSs.cpfSnapDate, 'YYYY-MM-DD').format('DD-MM-YYYY');
                const awTableHeader = awStartYearMonth + ' to ' + awEndYearMonth + ' (' + String(cpfSs?.createdByInit) + ')' + ' (' + retrievedDate + ') ' + prepopulatedStr;
                const tableValue = [awStartYearMonth, awEndYearMonth, cpfSs.cpfSnapshotId];

                this.awLatestPeriodOptions.push({
                  label: awTableHeader, value: tableValue
                });

                this.awCpfSnapshotMap.get(cpfSs.cpfSnapshotId).push(tableValue);
                // if (String(this.awItemForm?.get('header').value).trim() === awTableHeader.trim()) {
                //   this.selectedAWLatestPeriod = tableValue;
                // }
              }
            }
          });
          // add realtime
          if (this.realtimeCpf) {
            // table header title
            const prepopulatedStr = '';
            if (this.realtimeCpf?.cpfSnapshotInfoDtoList.length > 0) {
              this.awCpfSnapshotMap.set('realtime', []);
              // const cpfSnapshotInfoList = this.realtimeCpf.cpfSnapshotInfoDtoList;
              // take past 3 latest months + current trg start month
              for (let i=0; i<4; i++) {
                const momentMonthCounter = moment(new Date(this.claimForm.get('trgPeriodFg.trgPeriodStartDt').value)).subtract(i, 'months');
                const endYrMthCpfSnapshotInfoDto = this.realtimeCpf.cpfSnapshotInfoDtoList.find((info: any) => 
                  moment(new Date(info.relevantContributionYearMonth)).format('MM-YYYY') === momentMonthCounter.format('MM-YYYY'));
                if (endYrMthCpfSnapshotInfoDto) {
                  const awEndYearMonth = moment(new Date(endYrMthCpfSnapshotInfoDto.relevantContributionYearMonth)).format('MM-YYYY');
                  const momentStartMonth = moment(new Date(this.claimForm.get('trgPeriodFg.trgPeriodStartDt').value)).subtract((11+i), 'months');
                  const startYrMthCpfSnapshotInfoDto = this.getRealtimeCpfInfo(momentStartMonth.format('MM-YYYY'));
                  const awStartYearMonth = moment(new Date(startYrMthCpfSnapshotInfoDto.relevantContributionYearMonth)).format('MM-YYYY');
                  // const awStartYearMonth = moment(new Date(cpfSnapshotInfoList[cpfSnapshotInfoList.length-(12+i)].relevantContributionYearMonth)).format('MM-YYYY');
                  // const awEndYearMonth = moment(new Date(cpfSnapshotInfoList[cpfSnapshotInfoList.length-(1+i)].relevantContributionYearMonth)).format('MM-YYYY');
                  const retrievedDate = moment(new Date(this.realtimeCpf.cpfSnapDate)).format('DD-MM-YYYY');
                  const awTableHeader = awStartYearMonth + ' to ' + awEndYearMonth + ' (SYS)' + ' (' + retrievedDate + ') ' + prepopulatedStr;
                  const tableValue = [awStartYearMonth, awEndYearMonth, 'realtime'];
                  this.awLatestPeriodOptions.push({
                    label: awTableHeader, value: tableValue
                  });
  
                  this.awCpfSnapshotMap.get('realtime').push(tableValue);
                }
              }
            }
          }
        } else {
          if (this.realtimeCpf) {
            // table header title
            const prepopulatedStr = '';
            // table header title
            if (this.realtimeCpf?.cpfSnapshotInfoDtoList.length > 0) {
              this.awCpfSnapshotMap.set('realtime', []);
              // take past 3 latest months + current trg start month
              for (let i=0; i<4; i++) {
                const momentMonthCounter = moment(new Date(this.claimForm.get('trgPeriodFg.trgPeriodStartDt').value)).subtract(i, 'months');
                const endYrMthCpfSnapshotInfoDto = this.realtimeCpf.cpfSnapshotInfoDtoList.find((info: any) => 
                  moment(new Date(info.relevantContributionYearMonth)).format('MM-YYYY') === momentMonthCounter.format('MM-YYYY'));
                if (endYrMthCpfSnapshotInfoDto) {
                  const awEndYearMonth = moment(new Date(endYrMthCpfSnapshotInfoDto.relevantContributionYearMonth)).format('MM-YYYY');
                  const momentStartMonth = moment(new Date(this.claimForm.get('trgPeriodFg.trgPeriodStartDt').value)).subtract(11, 'months');
                  const startYrMthCpfSnapshotInfoDto = this.getRealtimeCpfInfo(momentStartMonth.format('MM-YYYY'));
                  const awStartYearMonth = moment(new Date(startYrMthCpfSnapshotInfoDto.relevantContributionYearMonth)).format('MM-YYYY');
                  // const awStartYearMonth = moment(new Date(this.realtimeCpf.cpfSnapshotInfoDtoList[this.realtimeCpf.cpfSnapshotInfoDtoList.length-(12+i)].relevantContributionYearMonth)).format('MM-YYYY');
                  // const awEndYearMonth = moment(new Date(this.realtimeCpf.cpfSnapshotInfoDtoList[this.realtimeCpf.cpfSnapshotInfoDtoList.length-(1+i)].relevantContributionYearMonth)).format('MM-YYYY');
                  const retrievedDate = moment(new Date(this.realtimeCpf.cpfSnapDate)).format('DD-MM-YYYY');
                  const awTableHeader = awStartYearMonth + ' to ' + awEndYearMonth + ' (SYS)' + ' (' + retrievedDate + ') ' + prepopulatedStr;
                  const tableValue = [awStartYearMonth, awEndYearMonth, 'realtime'];
                  this.awLatestPeriodOptions.push({
                    label: awTableHeader, value: tableValue
                  });

                  this.awCpfSnapshotMap.get('realtime').push(tableValue);
                }
              }
            }
          }
        }
        console.log('awLatestPeriodOptions', this.awLatestPeriodOptions);
        // if no default value, set first option as default
        if (this.selectedAWLatestPeriod === undefined || this.selectedAWLatestPeriod === null) {
          this.selectedAWLatestPeriod = this.awLatestPeriodOptions[0].value;
        }
        this.initialiseAWForm();
      }
    });
  }

  updateAWSnapshot() {
    console.log('selectedAWLatestMonth', this.selectedAWLatestPeriod);
    console.log('awCpfSnapshotMap', this.awCpfSnapshotMap);
    if (this.selectedAWLatestPeriod) {
      // either from CpfSnapshot or Realtime
      const cpfSnapshot = (this.selectedAWLatestPeriod[2] !== 'realtime') ?
        this.cpfSnapshot.find(cpfSs => cpfSs.cpfSnapshotId === this.selectedAWLatestPeriod[2]) : this.realtimeCpf;
      const cpfSnapshotInfoList = cpfSnapshot.cpfSnapshotInfoDtoList;

      let firstNonNull = false;
      let snapshotDivisor = 0;
      let snapshotTotal = 0;
      let momentStartYrMth = moment(this.selectedAWLatestPeriod[0], 'MM-YYYY');
      const momentEndYrMth = moment(this.selectedAWLatestPeriod[1], 'MM-YYYY');
      while(this.dateUtilsService.isBefore(momentStartYrMth.toDate(), momentEndYrMth.toDate()) || this.dateUtilsService.isEquals(momentStartYrMth.toDate(), momentEndYrMth.toDate())) {
        const cpfSnapshotInfo = cpfSnapshotInfoList.find((info : CpfSnapshotInfo) =>
          moment(info.relevantContributionYearMonth, 'YYYY-MM-DD').format('MM-YYYY') === momentStartYrMth.format('MM-YYYY'));
        if (cpfSnapshotInfo && cpfSnapshotInfo.awAmount !== null) {
          firstNonNull = true;
          snapshotDivisor += 1;
          snapshotTotal += cpfSnapshotInfo.awAmount;
        } else if (firstNonNull === true) {
          snapshotDivisor += 1;
          snapshotTotal += cpfSnapshotInfo.awAmount;
        }
        momentStartYrMth = momentStartYrMth.add(1, 'months');
      }
      console.log('updateAWSnapshot - snapshotDivisor', snapshotDivisor);
      console.log('updateAWSnapshot - snapshotTotal', snapshotTotal);
      if (snapshotDivisor !== 0) {
        const awSnapshotAmt = snapshotTotal / snapshotDivisor;
        this.editItemForm.get('snapshot').setValue(awSnapshotAmt);
        this.editItemForm.get('snapshot').updateValueAndValidity();
        this.editItemForm.get('divisorSnapshot').setValue(snapshotDivisor);
        this.editItemForm.get('divisorSnapshot').updateValueAndValidity();
      } else {
        this.editItemForm.get('snapshot').setValue(null);
        this.editItemForm.get('snapshot').updateValueAndValidity();
        this.editItemForm.get('divisorSnapshot').setValue(snapshotDivisor);
        this.editItemForm.get('divisorSnapshot').updateValueAndValidity();
      }

      const headerLabel = this.awLatestPeriodOptions.find(option => option.value === this.selectedAWLatestPeriod);
      this.editItemForm.get('header').setValue(headerLabel.label);
      this.editItemForm.get('header').updateValueAndValidity();

      // change all snapshot column
      const tempCpfSnapshot = new Map();
      cpfSnapshotInfoList.forEach((ss: any) => {
        tempCpfSnapshot.set(moment(ss.relevantContributionYearMonth).format('MM-YYYY'), ss);
      });
      const periodSsAmt = this.editItemForm.get('periodAmt') as FormArray;
      let i = 0;
      for (const periodAmtControl of periodSsAmt.controls) {
        if (periodAmtControl instanceof FormGroup) {
          // is a FormGroup
          const yrMth = moment(this.selectedAWLatestPeriod[0], 'MM-YYYY').add(i, 'months').format('MM-YYYY');
          // const yrMth = moment(awCpfSnapshot.endYearMonth).subtract(i, 'months').format('MM-YYYY');
          periodAmtControl.get('monthSnapshot').setValue(tempCpfSnapshot.has(yrMth) ? tempCpfSnapshot.get(yrMth).awAmount : 0);
          periodAmtControl.get('monthYear').setValue(yrMth);
        }
        i += 1;
      }
      this.editItemForm.get('periodAmt').updateValueAndValidity();
    }
  }

  updateAW() {
    const computeFormulaDto:IComputeFormula = this.constructComputeFormulaDto();

    if (computeFormulaDto) {
      this.formulaService.computeFormula(computeFormulaDto, null).subscribe({
        next: (res: any) => {
          if (res.body.data) {
            let computationString = null;
            let amtPayable = null;
            if (this.editItemForm.get('formulaCode').value === '13x') {
              computationString = this.editItemForm.get('computedFormula').value;
              amtPayable = this.editItemForm.get('computeAmt').value;
            } else {
              computationString = res.body.data?.computationString;
              amtPayable = res.body.data?.amtPayable;
            }
            if (this.awItemForm) {
              // update existing item
              let itemStatus = this.awItemForm.get('itemStatus').value;
              // re-open claim if claim is completed and item is of closed or approved type
              if ((this.approveItemStatusList.includes(this.awItemForm.get('itemStatus').value) || 
                this.closedItemStatusList.includes(this.awItemForm.get('itemStatus').value))) {
                  itemStatus = 'PVE';
                  const addItemDetail = new ClaimItemDetail();
                  addItemDetail.itemType = 'ADD';
                  addItemDetail.latestStatus = 'PVE';
                  this.loadItemStatusDropdown(addItemDetail);
                  if (this.empClaim.claimDto.latestStatus === 'COM') {
                    this.reopenClaim();
                  }
              }
            
              this.awItemForm.get('itemStatus').setValue(itemStatus, {onlySelf: true, emitEvent:false});
              this.awItemForm.get('itemName').setValue(this.editItemForm.get('itemName').value);
              this.awItemForm.get('basis').setValue(this.editItemForm.get('basis').value);
              this.awItemForm.get('header').setValue(this.editItemForm.get('header').value);
              this.awItemForm.get('latestContriMonth').setValue(this.editItemForm.get('latestContriMonth').value);
              this.awItemForm.get('snapshot').setValue(this.editItemForm.get('snapshot').value);
              this.awItemForm.get('apprAmt').setValue(this.editItemForm.get('apprAmt').value);
              this.awItemForm.get('divisorSnapshot').setValue(this.editItemForm.get('divisorSnapshot').value);
              this.awItemForm.get('divisorUser').setValue(this.editItemForm.get('divisorUser').value);
              this.awItemForm.get('formulaCode').setValue(this.editItemForm.get('formulaCode').value);
              this.awItemForm.get('formulaLabel').setValue(this.editItemForm.get('formulaLabel').value);
              this.awItemForm.get('computedFormula').setValue(computationString);
              this.awItemForm.get('computeAmt').setValue(amtPayable);
              this.awItemForm.get('periodAmt').setValue(this.editItemForm.get('periodAmt').value);
              this.awItemForm.updateValueAndValidity();

              this.awTable = this.editItemForm.get('periodAmt').value;

              // sync with actual training section
              if (this.existingClaimItemMap.has(this.awItemForm.get('claimItemId').value)) {
                this.emitChangesToActualTrgSection(this.awItemForm);
                // this.existingClaimItemMap.set(this.awItemForm.get('claimItemId').value, this.awItemForm);
              }
            } else {
              // create new item
              this.awItemForm = this.formBuilder.group({
                claimItemId: [null],
                itemStatus: ['PVE'],
                itemName: [this.editItemForm.get('itemName').value],
                basis: [this.editItemForm.get('basis').value],
                header: [this.editItemForm.get('header').value],
                latestContriMonth: [this.editItemForm.get('latestContriMonth').value],
                snapshot: [this.editItemForm.get('snapshot').value],
                apprAmt: [this.editItemForm.get('apprAmt').value],
                divisorSnapshot: [this.editItemForm?.get('divisorSnapshot').value],
                divisorUser: [this.editItemForm?.get('divisorUser').value],
                formulaCode: [this.editItemForm.get('formulaCode').value],
                formulaLabel: [this.editItemForm.get('formulaLabel').value],
                computedFormula: [computationString],
                computeAmt: [amtPayable],
                periodAmt: this.editItemForm?.get('periodAmt') as FormArray
              });

              this.newItemInserted.set('ADD', true);
              this.itemsCardCollapseMap.set('ADD', true);

              const addItemDetail = new ClaimItemDetail();
              addItemDetail.itemType = 'ADD';
              addItemDetail.latestStatus = 'PVE';
              this.loadItemStatusDropdown(addItemDetail);
            }

            // check for specific formula and auto populate rest day
            this.populateRestDayAltSat();
          }
        }, error: (error) => this.errorRespHandler(error)
      });
    }
    this.exitEditItemModal();
  }
