  diaEditItemFormOnChanges() {

    this.editItemForm.get('divisor').valueChanges.subscribe(value => {
      const annualAmt = this.editItemForm.get('annualAmt');
      if (annualAmt.value && value !== 0) {
        const apprAmt = Number(annualAmt.value) / Number(value);
        this.editItemForm.get('apprAmt').setValue(apprAmt);
        this.editItemForm.get('apprAmt').updateValueAndValidity();
      }
    });

    this.editItemForm.get('annualAmt').valueChanges.subscribe(value => {
      const divisor = this.editItemForm.get('divisor');
      if (divisor.value) {
        const apprAmt = Number(value) / Number(divisor.value);
        this.editItemForm.get('apprAmt').setValue(apprAmt);
        this.editItemForm.get('apprAmt').updateValueAndValidity();
      }
    });

    this.editItemForm.get('financialStartDate').valueChanges.subscribe(value => {
      if (!this.isEmpty(value)) {
        console.log('financialStartDate is not empty');
        this.editItemForm.get('financialEndDate').setValidators(Validators.required);
        this.editItemForm.get('financialEndDate').updateValueAndValidity();
      } else {
        this.editItemForm.get('financialEndDate').clearValidators();
        this.editItemForm.get('financialEndDate').updateValueAndValidity();
      }
    })
  }

