  dimEditItemFormOnChanges() {
    this.editItemForm.get('financialStartDate').valueChanges.subscribe(value => {
      if (!this.isEmpty(value)) {
        console.log('financialStartDate is not empty');
        this.editItemForm.get('financialEndDate').setValidators(Validators.required);
        this.editItemForm.get('financialEndDate').updateValueAndValidity();
      } else {
        this.editItemForm.get('financialEndDate').clearValidators();
        this.editItemForm.get('financialEndDate').updateValueAndValidity();
      }
    });
  }

  updateDIM() {
    const computeFormulaDto:IComputeFormula = this.constructComputeFormulaDto();

    if (computeFormulaDto) {
      this.formulaService.computeFormula(computeFormulaDto, null).subscribe({
        next: (res: any) => {
          if (res.body.data) {
            let computationString = null;
            let amtPayable = null;
            if (this.editItemForm.get('formulaCode').value === '13x') {
              computationString = this.editItemForm.get('computedFormula').value;
              amtPayable = this.editItemForm.get('computeAmt').value;
            } else {
              computationString = res.body.data?.computationString;
              amtPayable = res.body.data?.amtPayable;
            }
            // patch data
            if (this.dimFileUpload?.verifiedFiles.length > 0) {
              const metaData = this.dimFileUpload?.verifiedFiles[0]?.meta_data;
              const createdDt = moment(metaData.createdDt).format('DD-MM-YYYY');
              const createdBy = this.subject?.userInit;
              const documentInfo = '(' + createdDt + ' by ' + String(createdBy) + ')'
              this.editItemForm.get('manualComputeDocId').setValue(metaData.id);
              this.editItemForm.get('manualComputeName').setValue(metaData.fileName);
              this.editItemForm.get('manualComputeInfo').setValue(documentInfo);

              this.dimHasManualCompute = true;
            }

            if (this.editItemForm.get('noa1').value && this.editItemForm.get('noa2').value) {
              const noaStr = String(this.editItemForm.get('noa1').value) + ',' + String(this.editItemForm.get('noa2').value);
              this.editItemForm.get('noaStr').setValue(noaStr);
            } else {
              const noaStr = (this.editItemForm.get('noa1').value) ? this.editItemForm.get('noa1').value : this.editItemForm.get('noa2').value;
              this.editItemForm.get('noaStr').setValue(noaStr);
            }

            if (this.dimItemForm) {
              // update existing item
              let itemStatus = this.dimItemForm.get('itemStatus').value;
              // re-open claim if claim is completed and item is of closed or approved type
              if ((this.approveItemStatusList.includes(this.dimItemForm.get('itemStatus').value) || 
                this.closedItemStatusList.includes(this.dimItemForm.get('itemStatus').value))) {
                  itemStatus = 'PVE';
                  const dimItemDetail = new ClaimItemDetail();
                  dimItemDetail.itemType = 'DIM';
                  dimItemDetail.latestStatus = 'PVE';
                  this.loadItemStatusDropdown(dimItemDetail);
                  if (this.empClaim.claimDto.latestStatus === 'COM') {
                    this.reopenClaim();
                  }   
              }
              this.dimItemForm.get('itemStatus').setValue(itemStatus);
              this.dimItemForm.get('itemName').setValue(this.editItemForm.get('itemName').value);
              this.dimItemForm.get('basis').setValue(this.selectedBasis);
              this.dimItemForm.get('financialStartDate').setValue(moment(this.editItemForm.get('financialStartDate').value).format('DD-MM-YYYY'));
              this.dimItemForm.get('financialEndDate').setValue(moment(this.editItemForm.get('financialEndDate').value).format('DD-MM-YYYY'));
              this.dimItemForm.get('apprAmt').setValue(this.editItemForm.get('apprAmt').value);
              this.dimItemForm.get('submittedAmt').setValue(this.editItemForm.get('submittedAmt').value);
              this.dimItemForm.get('type').setValue(this.editItemForm.get('type').value);
              this.dimItemForm.get('manualComputeDocId').setValue(this.editItemForm.get('manualComputeDocId').value);
              this.dimItemForm.get('manualComputeName').setValue(this.editItemForm.get('manualComputeName').value);
              this.dimItemForm.get('manualComputeInfo').setValue(this.editItemForm.get('manualComputeInfo').value);
              this.dimItemForm.get('noa1').setValue(this.editItemForm.get('noa1').value);
              this.dimItemForm.get('noa2').setValue(this.editItemForm.get('noa2').value);
              this.dimItemForm.get('noaStr').setValue(this.editItemForm.get('noaStr').value);
              this.dimItemForm.get('formulaCode').setValue(this.editItemForm.get('formulaCode').value);
              this.dimItemForm.get('formulaLabel').setValue(this.editItemForm.get('formulaLabel').value);
              this.dimItemForm.get('computedFormula').setValue(computationString);
              this.dimItemForm.get('computeAmt').setValue(amtPayable);
              this.dimItemForm.updateValueAndValidity();

              // sync with actual training section
              if (this.existingClaimItemMap.has(this.dimItemForm.get('claimItemId').value)) {
                this.emitChangesToActualTrgSection(this.dimItemForm);
                // this.existingClaimItemMap.set(this.dimItemForm.get('claimItemId').value, this.dimItemForm);
              }
            } else {
              // create new item
              this.dimItemForm = this.formBuilder.group({
                claimItemId: [null],
                itemStatus: ['PVE'],
                itemName: [this.editItemForm.get('itemName').value],
                basis: [this.selectedBasis],
                financialStartDate: [moment(this.editItemForm.get('financialStartDate').value).format('DD-MM-YYYY')],
                financialEndDate: [moment(this.editItemForm.get('financialEndDate').value).format('DD-MM-YYYY')],
                apprAmt: [this.editItemForm.get('apprAmt').value],
                submittedAmt: [this.editItemForm.get('submittedAmt').value],
                type: [this.editItemForm.get('type').value],
                manualComputeDocId: [this.editItemForm.get('manualComputeDocId').value],
                manualComputeName: [this.editItemForm.get('manualComputeName').value],
                manualComputeInfo: [this.editItemForm.get('manualComputeInfo').value],
                noa1: [this.editItemForm.get('noa1').value],
                noa2: [this.editItemForm.get('noa2').value],
                noaStr: [this.editItemForm.get('noaStr').value],
                formulaCode: [this.editItemForm.get('formulaCode').value],
                formulaLabel: [this.editItemForm.get('formulaLabel').value],
                computedFormula: [computationString],
                computeAmt: [amtPayable],
              });
              if (this.existingDF === true) {
                this.diaItemForm.reset();
                this.itemsCardCollapseMap.delete('DIA');
              } else {
                this.newItemInserted.set('DIM', true);
              }
              this.itemsCardCollapseMap.set('DIM', true);

              const dimItemDetail = new ClaimItemDetail();
              dimItemDetail.itemType = 'DIM';
              dimItemDetail.latestStatus = 'PVE';
              this.loadItemStatusDropdown(dimItemDetail);
            }
            this.showFileUpload = false;

            // check for specific formula and auto populate rest day
            this.populateRestDayAltSat();
          }
        }, error: (error) => this.errorRespHandler(error)
      });
    }
    this.exitEditItemModal();
  }

