  oveEditItemFormOnChanges() {
    this.editItemForm.get('bs').valueChanges.subscribe(value => {
      const fa = this.editItemForm.get('fa').value;
      const va = this.editItemForm.get('va').value;
      if (value && fa && va) {
        const apprAmt = Number(value) + Number(fa) + Number(va);
        this.editItemForm.get('apprAmt').setValue(apprAmt);
        this.editItemForm.get('apprAmt').updateValueAndValidity();
      }
    });

    this.editItemForm.get('fa').valueChanges.subscribe(value => {
      const bs = this.editItemForm.get('bs').value;
      const va = this.editItemForm.get('va').value;
      if (value && bs && va) {
        const apprAmt = Number(bs) + Number(value) + Number(va);
        this.editItemForm.get('apprAmt').setValue(apprAmt);
        this.editItemForm.get('apprAmt').updateValueAndValidity();
      }
    });

    this.editItemForm.get('va').valueChanges.subscribe(value => {
      const fa = this.editItemForm.get('fa').value;
      const bs = this.editItemForm.get('bs').value;
      if (value && fa && bs) {
        const apprAmt = Number(bs) + Number(fa) + Number(value);
        this.editItemForm.get('apprAmt').setValue(apprAmt);
        this.editItemForm.get('apprAmt').updateValueAndValidity();
      }
    });

    this.editItemForm.get('countryCode').valueChanges.subscribe(value => {
      if (value === 'OTH') {
        this.editItemForm.get('otherCountry').setValidators(Validators.required);
        this.editItemForm.get('otherCountry').updateValueAndValidity();
      } else {
        this.editItemForm.get('otherCountry').clearValidators();
        this.editItemForm.get('otherCountry').updateValueAndValidity();
      }
    })
  }

  updateOVE() {
    const computeFormulaDto:IComputeFormula = this.constructComputeFormulaDto();

    if (computeFormulaDto) {
      this.formulaService.computeFormula(computeFormulaDto, null).subscribe({
        next: (res: any) => {
          if (res.body.data) {
            let computationString = null;
            let amtPayable = null;
            if (this.editItemForm.get('formulaCode').value === '13x') {
              computationString = this.editItemForm.get('computedFormula').value;
              amtPayable = this.editItemForm.get('computeAmt').value;
            } else {
              computationString = res.body.data?.computationString;
              amtPayable = res.body.data?.amtPayable;
            }
            const selectedCountryOption = this.countryOptions.find(country => country.value === this.selectedCountry);

            // patch data
            if (this.oveFileUpload?.verifiedFiles.length > 0) {
              const metaData = this.oveFileUpload?.verifiedFiles[0]?.meta_data;
              const createdDt = moment(metaData.createdDt).format('DD-MM-YYYY');
              const createdBy = this.subject?.userInit;
              const documentInfo = '(' + createdDt + ' by ' + String(createdBy) + ')'
              this.editItemForm.get('manualComputeDocId').setValue(metaData.id);
              this.editItemForm.get('manualComputeName').setValue(metaData.fileName);
              this.editItemForm.get('manualComputeInfo').setValue(documentInfo);

              this.oveHasManualCompute = true;
            }

            if (this.oveItemForm) {
              // update existing item
              let itemStatus = this.oveItemForm.get('itemStatus').value;
              // re-open claim if claim is completed and item is of closed or approved type
              if ((this.approveItemStatusList.includes(this.oveItemForm.get('itemStatus').value) || 
                this.closedItemStatusList.includes(this.oveItemForm.get('itemStatus').value))) {
