              this.oveItemForm.get('apprAmt').setValue(this.editItemForm.get('apprAmt').value);
              this.oveItemForm.get('otherCountry').setValue(this.editItemForm.get('otherCountry').value);
              this.oveItemForm.get('countryCode').setValue(this.editItemForm.get('countryCode').value);
              this.oveItemForm.get('country').setValue(selectedCountryOption.label);
              this.oveItemForm.get('bs').setValue(this.editItemForm.get('bs').value);
              this.oveItemForm.get('fa').setValue(this.editItemForm.get('fa').value);
              this.oveItemForm.get('va').setValue(this.editItemForm.get('va').value);
              this.oveItemForm.get('total').setValue(this.editItemForm?.value?.total);
              this.oveItemForm.get('manualComputeDocId').setValue(this.editItemForm.get('manualComputeDocId').value);
              this.oveItemForm.get('manualComputeName').setValue(this.editItemForm.get('manualComputeName').value);
              this.oveItemForm.get('manualComputeInfo').setValue(this.editItemForm.get('manualComputeInfo').value);
              this.oveItemForm.get('formulaCode').setValue(this.editItemForm.get('formulaCode').value);
              this.oveItemForm.get('formulaLabel').setValue(this.editItemForm.get('formulaLabel').value);
              this.oveItemForm.get('computedFormula').setValue(computationString);
              this.oveItemForm.get('computeAmt').setValue(amtPayable);
              this.oveItemForm.updateValueAndValidity();

              // sync with actual training section
              if (this.existingClaimItemMap.has(this.oveItemForm.get('claimItemId').value)) {
                this.emitChangesToActualTrgSection(this.oveItemForm);
                // this.existingClaimItemMap.set(this.oveItemForm.get('claimItemId').value, this.oveItemForm);
              }
            } else {
              // create new item
              this.oveItemForm = this.formBuilder.group({
                claimItemId: [null],
                itemStatus: ['PVE'],
                itemName: [this.editItemForm.get('itemName').value],
                basis: [this.selectedBasis],
                apprAmt: [this.editItemForm?.get('apprAmt').value],
                otherCountry: [this.oveItemForm?.get('otherCountry').value],
                countryCode: [this.oveItemForm?.get('countryCode').value],
