  initialiseECOPeriod() {
    this.ecoPeriodOptions = [];
    const claimStartDate = this.claimForm.get('trgPeriodFg.trgPeriodStartDt').value;
    const period1 = moment(new Date(claimStartDate)).subtract(6, 'months').format('MM-YYYY') + ' to ' +  moment(String(claimStartDate)).subtract(1, 'months').format('MM-YYYY');
    const option1 = {
      label: period1, value: period1
    }
    this.ecoPeriodOptions.push(option1);

    const period2 = moment(new Date(claimStartDate)).subtract(7, 'months').format('MM-YYYY') + ' to ' +  moment(String(claimStartDate)).subtract(2, 'months').format('MM-YYYY');
    const option2 = {
      label: period2, value: period2
    }
    this.ecoPeriodOptions.push(option2);

    const period3 = moment(new Date(claimStartDate)).subtract(8, 'months').format('MM-YYYY') + ' to ' +  moment(String(claimStartDate)).subtract(3, 'months').format('MM-YYYY');
    const option3 = {
      label: period3, value: period3
    }
    this.ecoPeriodOptions.push(option3);

    if (this.selectedPeriodECO === null) {
      this.selectedPeriodECO = period1;

      this.editItemForm.get('mth1').setValue(moment(new Date(claimStartDate)).subtract(6, 'months').format('MM-YYYY'));
      this.editItemForm.get('mth2').setValue(moment(new Date(claimStartDate)).subtract(5, 'months').format('MM-YYYY'));
      this.editItemForm.get('mth3').setValue(moment(new Date(claimStartDate)).subtract(4, 'months').format('MM-YYYY'));
      this.editItemForm.get('mth4').setValue(moment(new Date(claimStartDate)).subtract(3, 'months').format('MM-YYYY'));
      this.editItemForm.get('mth5').setValue(moment(new Date(claimStartDate)).subtract(2, 'months').format('MM-YYYY'));
      this.editItemForm.get('mth6').setValue(moment(new Date(claimStartDate)).subtract(1, 'months').format('MM-YYYY'));
    }
  }

