  onbEditItemFormOnChanges() {
    this.editItemForm.valueChanges.subscribe(value => {
      if (value.allwDivisor && value.allwDivisor !== 0) {
        value.apprAmt = (Number(value.allwMth1) + Number(value.allwMth2) + Number(value.allwMth3) + Number(value.allwMth4) + Number(value.allwMth5) + Number(value.allwMth6)) / Number(value.allwDivisor);
      }
    });

    this.editItemForm.get('allwMth1').valueChanges.subscribe(value=> {
      if (this.selectedNature === 'F' && value) {
        this.editItemForm.get('allwMth2').setValue(value);
