    const period1 = moment(new Date(claimStartDate)).subtract(6, 'months').format('MM-YYYY') + ' to ' +  moment(new Date(claimStartDate)).subtract(1, 'months').format('MM-YYYY');
    const option1 = {
      label: period1, value: period1
    }
    this.onbPeriodOptions.push(option1);

    const period2 = moment(new Date(claimStartDate)).subtract(7, 'months').format('MM-YYYY') + ' to ' +  moment(new Date(claimStartDate)).subtract(2, 'months').format('MM-YYYY');
    const option2 = {
      label: period2, value: period2
    }
    this.onbPeriodOptions.push(option2);

    const period3 = moment(new Date(claimStartDate)).subtract(8, 'months').format('MM-YYYY') + ' to ' +  moment(new Date(claimStartDate)).subtract(3, 'months').format('MM-YYYY');
    const option3 = {
      label: period3, value: period3
    }
    this.onbPeriodOptions.push(option3);

    if (this.selectedPeriodONB === null) {
      this.selectedPeriodONB = period1;

      this.editItemForm.get('mth1').setValue(moment(new Date(claimStartDate)).subtract(6, 'months').format('MM-YYYY'));
