  /*---------- Formula ----------*/
  updateFormulaOptions(claimType: string, claimBasis: string) {
    console.log('onChange basis', claimBasis);
    console.log('selectedBasis', this.selectedBasis);
    this.selectedFormula = null;
    const startPayPeriod = this.claimForm.get('startPayPeriod').value;
    const restDayList = this.claimForm.get('restDay').value;
    let params = new HttpParams();
    params = params.append('claimType', claimType);
    params = params.append('claimBasisCode', claimBasis);
    if (startPayPeriod) {
      params = params.append('payPeriodStartInd', 1);
    }
    if (restDayList && restDayList.length > 0) {
      params = params.append('restDayInd', 1);

      const isSaturday = restDayList.some((day: any) => moment(day).format('ddd').toUpperCase() === 'SAT');
      if (isSaturday) {
        params = params.append('isSatInd', 1);
      }
    }
    this.claimFormulaOption = [];
    this.claimService.retrieveFormulaList({params}).subscribe((res: any) => {
      if (res.body.data) {
        const sortedData = res.body.data.sort(this.claimService.sortFormulaListByCode());
        sortedData.forEach((x:any)=>{
          this.claimFormulaOption.push({
            label: String(x.formulaCode) + ' ' + String(x.formulaDesc),
            value: x.formulaCode
          });
        })
      }
    });
  }

  formulaOnChange(formName: FormGroup, controlName: string) {
    if (this.selectedFormula) {
      const formula = this.claimFormulaOption.find(f => f.value === this.selectedFormula);
      formName.get(controlName).setValue(formula.label);
      formName.get(controlName).updateValueAndValidity();
    }

    if (this.selectedFormula === '13x') {
      formName.get('computedFormula').setValidators(Validators.required);
      formName.get('computedFormula').updateValueAndValidity();
      formName.get('computeAmt').setValidators(Validators.required);
      formName.get('computeAmt').updateValueAndValidity();
    } else {
      formName.get('computedFormula').clearValidators();
      formName.get('computedFormula').updateValueAndValidity();
      formName.get('computeAmt').clearValidators();
      formName.get('computeAmt').updateValueAndValidity();
    }

    const payPeriodFormulaRequired = ['16a', '16b', '16c', '16d'];
    const startPayPeriodControl = this.claimForm.get('startPayPeriod');
    if (payPeriodFormulaRequired.includes(this.selectedFormula)) {
      startPayPeriodControl.addValidators(Validators.required);
    } else if (startPayPeriodControl.hasValidator(Validators.required)){
      startPayPeriodControl.removeValidators(Validators.required);
      startPayPeriodControl.updateValueAndValidity();
    }

    const restDayFormulaRequired = ['8a ', '8b ', '10a', '10b', '15a', '16d'];
    const restDayControl = this.claimForm.get('restDay');
    if (restDayFormulaRequired.includes(this.selectedFormula)) {
      restDayControl.addValidators(Validators.required);
    } else if (restDayControl.hasValidator(Validators.required)){
      restDayControl.removeValidators(Validators.required);
      restDayControl.updateValueAndValidity();
    }
  }

  constructComputeFormulaDto(): IComputeFormula {
    if (this.editItemForm.get('formulaLabel').valid && this.editItemForm.get('apprAmt').valid) {
      const computeFormulaDto:IComputeFormula = {
        formulaCode: this.selectedFormula,
        dateFrom: moment(String(this.claimForm?.get('trgPeriodFg.trgPeriodStartDt').value)).format('DD-MM-YYYY'),
        dateTo: moment(String(this.claimForm?.get('trgPeriodFg.trgPeriodEndDt').value)).format('DD-MM-YYYY'),
        halfDayIndicator: ((this.claimForm?.get('halfDayInd').value) === true) ? 'Y' : 'N',
        approvedAmt: Number(this.editItemForm.value.apprAmt),
        // approvedAmt: Number(this.editItemForm?.get('apprAmt').value),
        restDays: this.claimForm?.get('restDay').value,
        payPeriodStart: Number(this.claimForm?.get('startPayPeriod').value)
      };
      return computeFormulaDto;
    }
    return null;
  }

  computeFormula() {
    const computeFormulaDto:IComputeFormula = this.constructComputeFormulaDto();

    if (computeFormulaDto) {
      this.formulaService.computeFormula(computeFormulaDto, null).subscribe((res: any) => {
        if (res.body.data) {
          this.editItemForm.get('computedFormula').setValue(res.body.data?.computationString);
          this.editItemForm.get('computedFormula').updateValueAndValidity();
          this.editItemForm.get('computeAmt').setValue(res.body.data?.amtPayable);
          this.editItemForm.get('computeAmt').updateValueAndValidity();
        }
      });
    }
  }

  disableRecompute():boolean {
    if (this.selectedFormula) {
      if (this.selectedFormula === '13x') {
        return true;
      }
      // this.canRecompute = true;
      return false;
    } else {
      // this.canRecompute = false;
      return true;
    }
  }
