/*---------- SAVE ITEMS ----------*/
saveClaimItemSection(): any {
  // save current items
  this.claimItemDetail.forEach((item: ClaimItemDetail) => {
    item.updatedBy = this.subject.subjectId;
    switch(item.itemType) {
      case 'ORD': {
        this.saveOWTable(item);
        break;
      }
      case 'ADD': {
        this.saveAWTable(item);
        break;
      }
      case 'DIM': {
        this.saveDIMTable(item);
        break;
      }
      case 'DIA': {
        this.saveDIATable(item);
        break;
      }
      case 'DIO': {
        this.saveDIOTable(item);
        break;
      }
      case 'INT': {
        this.saveINTTable(item);
        break;
      }
      case 'MIN': {
        this.saveMINTable(item);
        break;
      }
      case 'OVE': {
        this.saveOVETable(item);
        break;
      }
      case 'ECO': {
        this.saveECOTable(item);
        break;
      }
      case 'OBS': {
        this.saveOBSTable(item);
        break;
      }
      case 'ONB': {
        this.saveONBTable(item);
        break;
      }
      default: {
        break;
      }
    }
  });

  // save new items
  this.createNewItem();
}

createNewItem() {
  this.newItemInserted.forEach((value: boolean, key: string) => {
    if (value === true) {
      const item = new ClaimItemDetail();
      item.claimItemId = null;
      item.createdBy = this.subject.subjectId;
      item.caseId = this.empClaim.claimDto.caseId;
      item.claimId = this.empClaim.claimDto.id;
      switch(key) {
        case 'ORD': {
          item.itemType = 'ORD';
          this.saveOWTable(item);
          break;
        }
        case 'ADD': {
          item.itemType = 'ADD';
          this.saveAWTable(item);
          break;
        }
        case 'DIM': {
          item.itemType = 'DIM';
          this.saveDIMTable(item);
          break;
        }
        case 'DIA': {
          item.itemType = 'DIA';
          this.saveDIATable(item);
          break;
        }
        case 'DIO': {
          this.saveDIOTable(item);
          break;
        }
        case 'INT': {
          item.itemType = 'INT';
          this.saveINTTable(item);
          break;
        }
        case 'MIN': {
          item.itemType = 'MIN';
          this.saveMINTable(item);
          break;
        }
        case 'OVE': {
          item.itemType = 'OVE';
          this.saveOVETable(item);
          break;
        }
        case 'OBS': {
          item.itemType = 'OBS';
          this.saveOBSTable(item);
          break;
        }
        default: {
          const subStrItemType = key.substring(0,3);
          if (subStrItemType === 'ECO') {
            // OTHERS
            item.itemType = 'ECO';
            item.itemName = key.substring(3);
            this.saveECOTable(item);
          } else if (subStrItemType === 'ONB') {
            // NON-BASIC SALARY items
            item.itemType = 'ONB';
            this.saveONBTable(item);
          }
          break;
        }
      }
      this.claimItemDetail.push(item);
    }
  });
}

