  item.empClaimItemOWDto = owDto;

  if (this.selectedOWLatestMonth[1] === 'realtime') {
    item.cpfSnapshotDto = null;
    // ONLY save realtime if user selected realtime latest contri option
    item.newCpfSnapshotDto = this.realtimeCpf;
  } else {
    // if there are changes to snapshot
    if (item.cpfSnapshotDto && item.cpfSnapshotDto.cpfSnapshotId !== this.selectedOWLatestMonth[1]) {
      const cpfsnapshot = this.cpfSnapshot.find((ss: CpfSnapshot) => ss.cpfSnapshotId === this.selectedOWLatestMonth[1]);
      item.cpfSnapshotDto = cpfsnapshot;
    }
  }
 
}

saveAWTable(item: ClaimItemDetail) {
  const awDto = item.empClaimItemAWDto ? item.empClaimItemAWDto : new EmpClaimItemAW;
  awDto.snapshotDivisor = this.awItemForm.get('divisorSnapshot').value;
  if (this.selectedAWLatestPeriod && this.awCpfSnapshotMap.size > 0) {
    // if edited
    const startYrMth = '01-' + String(this.selectedAWLatestPeriod[0]);
    const endYrMth = '01-' + String(this.selectedAWLatestPeriod[1]);
    awDto.periodYearMonth = moment(startYrMth, 'DD-MM-YYYY').format('YYYY-MM-DD');
    awDto.periodEndYearMonth = moment(endYrMth, 'DD-MM-YYYY').format('YYYY-MM-DD');
  }

  const itemInfoList = item.empClaimItemInfoDtoList ? item.empClaimItemInfoDtoList : [];
  const periodAmtArr = this.awItemForm.get('periodAmt').value;
  if (itemInfoList.length > 0 && itemInfoList.length === periodAmtArr.length) {
    let i = 0;
    itemInfoList.forEach(itemInfo => {
      if (i < periodAmtArr.length) {
       itemInfo.amount = periodAmtArr[i].monthUser;
       const infoYrMth = '01-' + String(periodAmtArr[i].monthYear);
       itemInfo.yrMth = moment(infoYrMth, 'DD-MM-YYYY').format('YYYY-MM-DD');
       itemInfo.updatedBy = this.subject.subjectId;
       i+=1;
      }
    });
  } else {
    periodAmtArr.forEach((period: any) => {
      const itemInfo = new EmpClaimItemInfo;
      itemInfo.amount = period.monthUser;
      itemInfo.yrMth = moment(period.monthYear, 'MM-YYYY').format('YYYY-MM-DD');
      itemInfo.createdBy = this.subject.subjectId;
      itemInfoList.push(itemInfo);
    });
  }

  item.allwDivisor = this.awItemForm.get('divisorUser').value;
  item.apprBasis = this.awItemForm.get('basis').value;
  item.apprAmt = this.awItemForm.get('apprAmt').value;
  item.computedFormula = this.awItemForm.get('computedFormula').value;
  item.computedAmt = this.awItemForm.get('computeAmt').value;
  item.formulaCode = this.awItemForm.get('formulaCode').value;
  item.latestStatus = this.awItemForm.get('itemStatus').value;
  item.empClaimItemAWDto = awDto;
  item.empClaimItemInfoDtoList = itemInfoList;

  if (this.selectedAWLatestPeriod[2] === 'realtime') {
    item.cpfSnapshotDto = null;
    // ONLY save realtime if user selected realtime latest contri option
    item.newCpfSnapshotDto = this.realtimeCpf;
  } else {
    // if there are changes to snapshot
    if (item.cpfSnapshotDto && item.cpfSnapshotDto.cpfSnapshotId !== this.selectedAWLatestPeriod[2]) {
      const cpfsnapshot = this.cpfSnapshot.find((ss: CpfSnapshot) => ss.cpfSnapshotId === this.selectedAWLatestPeriod[2]);
      item.cpfSnapshotDto = cpfsnapshot;
    }
  }
}

