/*---------- Formatting ----------*/
  formatDate(dateValue: any, formatStr: string): string{
    if (dateValue) {
      return moment(new Date(dateValue)).format(formatStr);
    } else {
      return '';
    }
  }

  formatWorkHour(controlName: string) {
    const workHourControl = this.claimForm.get(controlName);
    if (workHourControl.value && workHourControl.value.length > 0) {
        const workHour = this.addLeadingZero(workHourControl.value, 4);
        workHourControl.setValue(workHour);
    }
  }

  formatMonthYear(dateStr: string, formatStr: string): string {
    if (dateStr && formatStr) {
      return moment(dateStr).format(formatStr);
    }
    return 'NA';
  }

  addLeadingZero(str: string, maxLength: number): string {
    const diff = maxLength - str.length;
    let zeroes = '';
    for (let i=0; i<diff; i++) {
        zeroes += '0';
    }
    return String(zeroes) + String(str);
  }

  formatTime(time: any): string {
    let result = '--:--';
    if(moment(time,['HH:mm']).isValid()) {
      result = moment(time,['HH:mm']).format('hh:mm A');
    }
    return result;
  }

  setUpperCase(ctrlName: string):void{
    this.claimForm?.get(ctrlName)?.setValue(this.claimForm?.get(ctrlName).value.toUpperCase());
  }

  onBlurTrgStartEndDt(): void{
    this.outsideCurYr =  this.fallOutsideCurrentYr(this.claimForm?.get('trgPeriodFg.trgPeriodStartDt')?.value)||
    this.fallOutsideCurrentYr(this.claimForm?.get('trgPeriodFg.trgPeriodEndDt')?.value);
  }

  onBlurStartPayPeriod() {
    const ppStartDtControl = this.claimForm.get('startPayPeriod');
    if (ppStartDtControl.value && ppStartDtControl.value.length === 1) {
        const formatPayPeriodDt = this.addLeadingZero(ppStartDtControl.value, 2);
        ppStartDtControl.setValue(formatPayPeriodDt);
        ppStartDtControl.updateValueAndValidity();
    }
  }

  onBlurGetOverlappingClaims() {
    const sub = this.claimForm.get('trgPeriodFg').statusChanges.subscribe((res) => {
      if (this.claimForm.get('trgPeriodFg').valid) {
        console.log('trgPeriodFg statusChanges', res);
        const startDt = this.claimForm.get('trgPeriodFg.trgPeriodStartDt').value;
        const endDt = this.claimForm.get('trgPeriodFg.trgPeriodEndDt').value;
        let params = new HttpParams();
        params = params.append('claimId', this.empClaim.claimDto.id);
        params = params.append('nric', this.empClaim.claimDto.nric);
        params = params.append('claimStartDt', this.formatDate(startDt, 'YYYY-MM-DD'));
        params = params.append('claimEndDt', this.formatDate(endDt, 'YYYY-MM-DD'));
        this.getOverlappingClaims(params);
      }
      sub.unsubscribe();
    });
  }

  onBeforeInput(event: InputEvent): boolean{
    if(event.inputType === "insertText" || event.inputType === "insertFromPaste") {
      return /^\d+$/.test(event.data);
    }
    return true;
  }

  getItemStatusDesc(code: string): string{
    return String(this.fullItemStatusMap.get(code)).toUpperCase();
  }
  
  //----------------------------

  /*---------- Validation ----------*/
  fallOutsideCurrentYr(date: any): boolean{
    const curYr = new Date().getFullYear();
    const inputDate = new Date(date);
    if(inputDate.getFullYear()< curYr || inputDate.getFullYear()> curYr){
      return true;
    }else return false;
  }

  isNotEmpty(obj: any): boolean{
    return obj!==undefined && obj!==null && obj!=="";
  }

  isEmpty(input: string): boolean {
      return input===null || input===undefined || input === "";
  }
  //----------------------------

  showErrorMsg(formGroup: FormGroup, controlName: string, errorName: string): boolean {
    return formGroup.get(controlName)?.hasError(errorName) && formGroup.get(controlName)?.touched;
  }

  errorRespHandler(error:any) {
    console.log("error is" + String(error?.error?.error_desc));
    this.toastService.error(error?.error?.error_desc);
  }

  checkRowData(rowData: any) {
    console.log(rowData);
  }

  consoleLog() {
    console.log('empClaim', this.empClaim);
    console.log('empClaimItem', this.claimItemDetail);
    console.log('claimForm', this.claimForm);
    console.log('itemsCardCollapseMap', this.itemsCardCollapseMap);
    console.log('itemStatusOptionMap', this.itemStatusOptionMap);
    console.log('itemLatestStatysMap', this.itemLatestStatusMap);
    console.log('newItemInserted', this.newItemInserted);
    console.log('editItemForm', this.editItemForm);
    console.log('fullItemStatusMap', this.fullItemStatusMap);
    console.log('checkClaimItemsInvalid', this.checkClaimItemsInvalid());
    const itemFormMap = new Map();
    if (this.owItemForm) {
      itemFormMap.set('ORD', this.owItemForm);
    }
    if (this.awItemForm) {
      itemFormMap.set('ADD', this.awItemForm);
    }
    if (this.dimItemForm) {
      itemFormMap.set('DIM', this.dimItemForm);
    }
    if (this.diaItemForm) {
      itemFormMap.set('DIA', this.diaItemForm);
    }
    if (this.intItemForm) {
      itemFormMap.set('INT', this.intItemForm);
    }
    if (this.minItemForm) {
      itemFormMap.set('MIN', this.minItemForm);
    }
    if (this.oveItemForm) {
      itemFormMap.set('OVE', this.oveItemForm);
    }
    if (this.ecoItemFormList) {
      itemFormMap.set('ECO', this.ecoItemFormList);
    }
    if (this.obsItemForm) {
      itemFormMap.set('OBS', this.obsItemForm);
    }
    if (this.onbItemFormList) {
      itemFormMap.set('ONB', this.onbItemFormList);
    }
    console.log('itemFormMap', itemFormMap);

    const selectedMap = new Map();
    selectedMap.set('isCurrentClaimOldPolicy', this.isCurrentClaimOldPolicy);
    selectedMap.set('selectedBasis', this.selectedBasis);
    selectedMap.set('selectedAWLatestPeriod', this.selectedAWLatestPeriod);
    selectedMap.set('selectedOWLatestMonth', this.selectedOWLatestMonth);
    selectedMap.set('selectedCountry', this.selectedCountry);
    selectedMap.set('selectedFormula', this.selectedFormula);
    selectedMap.set('selectedOtherIndex', this.selectedOtherIndex);
    selectedMap.set('selectedDocument', this.selectedDocument);
    selectedMap.set('selectedNature', this.selectedNature);
    selectedMap.set('selectedPeriodECO', this.selectedPeriodECO);
    selectedMap.set('selectedPeriodONB', this.selectedPeriodONB);
    selectedMap.set('selectedItemType', this.selectedItemType);
    selectedMap.set('selecteddfFrequency', this.selecteddfFrequency);
    selectedMap.set('selectedDIMType', this.selectedDIMType);
    selectedMap.set('altSaturdayList', this.altSaturdayList);
    selectedMap.set('disableInput', this.disableInput);
    console.log('selectedMap', selectedMap);

    const OAWmapping = new Map();
    OAWmapping.set('awCpfSnapshotMap', this.awCpfSnapshotMap);
    OAWmapping.set('owCpfSnapshotMap', this.owCpfSnapshotMap);
    OAWmapping.set('owCpfSnapshotInfoMap', this.owCpfSnapshotInfoMap);
    OAWmapping.set('realtimeCpf', this.realtimeCpf);
    OAWmapping.set('owTableRealtime', this.owTableRealtime);
    OAWmapping.set('awTableRealtime', this.awTableRealtime);
    OAWmapping.set('cpfSnapshot', this.cpfSnapshot);
    console.log('OAWmapping', OAWmapping);
  }
}
