<!-- INSERT/EDIT ITEMS -->
<p-dialog [(visible)]="showEditItemModal" [modal]="true" [style]="{width: '70vw'}" [contentStyle]="{overflow: 'auto'}"
        [draggable]="false" [resizable]="true">
        <ng-template pTemplate="header">
            <!-- <div class="col-12">Breakline 1</div> 
            <div class="col-12">Breakline 2</div>
            <div class="col-12">Breakline 3</div> -->
            <div class="relative">
                <div *ngIf="selectedFormula !== '13x'; else checkFormula13">
                    <div class="relative" style="min-height: 0em;"><h5>Item Details</h5></div>
                </div>
                <ng-template #checkFormula13>
                    <div *ngIf="!isComputeAmtZero()" class="relative" style="min-height: 0em;"><h5>Item Details</h5></div>
                    <div *ngIf="isComputeAmtZero()" class="relative" style="min-height: 6em;"><h5>Item Details</h5>
                        <div class="absolute bottom-10 left-0" style="width: 65vw">
                            <p-messages severity="warn">
                                <ng-template key="CLM-W008" pTemplate >
                                    <div class="ml-2">{{ claimJson.nspay2App.claim.warning['CLM-W008'] }}</div>
                                </ng-template>
                            </p-messages>
                        </div>
                    </div>
                </ng-template>
            </div>
        </ng-template>
        <hr>
        <div class="grid">
            <div class="col-1">
                <div *ngIf="selectedItemType === 'DF' && existingDF === true; then dfItemTypeLabel else normalItemTypeLabel"></div>
                <ng-template #dfItemTypeLabel>
                    <label *ngIf="dfHasApprovedBefore() === false" class="align-items-center justify-content-left col-form-label">Item Type:</label>
                    <label *ngIf="dfHasApprovedBefore() === true" class="align-items-center justify-content-left col-form-label">Item Desc:</label>
                </ng-template>
                <ng-template #normalItemTypeLabel>
                    <label *ngIf="insertNewItemType === true" class="align-items-center justify-content-left col-form-label">Item Type:</label>
                    <label *ngIf="insertNewItemType === false" class="align-items-center justify-content-left col-form-label">Item Desc:</label>
                </ng-template>
            </div>
            <div class="col-3 py-0" *ngIf="insertNewItemType;else getItemName">
                <p-dropdown appendTo="body" placeholder="&nbsp;" [options]="itemTypeOptions" autoWidth="false" [style]="{'width':'100%'}" [(ngModel)]="selectedItemType"
                    (onChange)="initialiseItemFormGroup()" class="ui-dropdown-l-size" [ngModelOptions]="{standalone: true}">
                </p-dropdown>
            </div>
            <ng-template #getItemName>
                <div class="col-3" *ngIf="editItemForm">
                    <label *ngIf="selectedItemType !== 'DF' && selectedItemType !== 'ECO'" class="align-items-center justify-content-left col-form-label">{{editItemForm.get('itemName').value}}</label>
                    <label *ngIf="selectedItemType === 'DF'" class="align-items-center justify-content-left col-form-label">
                        {{editItemForm.get('itemName').value}}
                        <span *ngIf="selecteddfFrequency === 'DIA' && dfHasApprovedBefore() === true">&nbsp;(Annual)</span>
                        <span *ngIf="selecteddfFrequency === 'DIM' && dfHasApprovedBefore() === true">&nbsp;(Monthly)</span>
                    </label>
                    <label *ngIf="selectedItemType === 'ECO'" class="align-items-center justify-content-left col-form-label">OTHERS</label>
                </div>
            </ng-template>
            <div class="col-2"></div>
            <div class="col-1" *ngIf="selectedItemType === 'DF' && dfHasApprovedBefore() === false">
                <label class="align-items-center justify-content-left col-form-label">Frequency:</label>
            </div>
            <div class="col-3 py-0" *ngIf="selectedItemType === 'DF' && dfHasApprovedBefore() === false">
                <p-dropdown appendTo="body" [options]="directorFeeFrequencyOption" autoWidth="false" [style]="{'width':'100%'}" [(ngModel)]="selecteddfFrequency"
                    (onChange)="onChangeDirectorFeeItem()" class="ui-dropdown-l-size" [ngModelOptions]="{standalone: true}">
                </p-dropdown>
            </div>
            <div class="col-1" *ngIf="selectedItemType === 'ECO' || (isCurrentClaimOldPolicy === true && selectedItemType && selectedItemType !== 'DIO')">
                <label class="align-items-center justify-content-left col-form-label">Nature:</label>
            </div>
            <div class="col-3" *ngIf="selectedItemType === 'OBS'; else otherNatureOption">
                <label class="align-items-center justify-content-left col-form-label">FIXED</label>
            </div>
            <ng-template #otherNatureOption>
                <div class="col-3 py-0" *ngIf="selectedItemType === 'ECO' || (isCurrentClaimOldPolicy === true && selectedItemType && selectedItemType !== 'DIO' && selectedItemType !== 'OBS')">
                    <p-dropdown appendTo="body" placeholder="&nbsp;" [options]="natureOption" autoWidth="false" [style]="{'width':'100%'}" [(ngModel)]="selectedNature"
                        (onChange)="onChangeNature()" class="ui-dropdown-l-size" [ngModelOptions]="{standalone: true}">
                    </p-dropdown>
                </div>
            </ng-template>            
        </div>
        <div class="col-12"></div>
        <div class="grid" *ngIf="selectedItemType === 'ECO'">
            <div class="col-1">
                <label class="align-items-center justify-content-left col-form-label">Item Name:</label>
            </div>
            <div class="col-3 py-0">
                <input type="input" pInputText [formControl]="$any(editItemForm.controls['itemName'])" [style]="{'max-width': '100%'}" oninput="this.value = this.value.toUpperCase()">
                <div>
                    <small class="p-error" *ngIf="showErrorMsg(editItemForm, 'itemName', 'required')" icTranslate="nspay2App.claim.validation.CLM-V001" [translateValues]="{ field: 'Item Name' }"></small>
                </div>
            </div>
        </div>
        <div class="col-12" *ngIf="selectedItemType === 'ECO'"></div>
        <form *ngIf="editItemForm" [formGroup]="editItemForm">
            <div class="grid" *ngIf="selectedItemType && editItemForm">
                <div class="col-1">
                    <label class="align-items-center justify-content-left col-form-label">Formula:</label>
                </div>
                <div class="col-3 py-0" *ngIf="claimFormulaOption.length">
                    <p-dropdown appendTo="body" placeholder="&nbsp;" [options]="claimFormulaOption" autoWidth="false" [style]="{'width':'100%'}" [(ngModel)]="selectedFormula" (onChange)="formulaOnChange(editItemForm, 'formulaLabel')"
                        formControlName="formulaCode" class="ui-dropdown-l-size" [autoDisplayFirst]=true>
                    </p-dropdown>
                    <small class="p-error" *ngIf="showErrorMsg(editItemForm, 'formulaCode', 'required')" icTranslate="nspay2App.claim.validation.CLM-V001" [translateValues]="{ field: 'Formula' }"></small> 
                </div>
                <div class="col-2"></div>
                <div class="col-1">
                    <label class="align-items-center justify-content-left col-form-label">Basis:</label>
                </div>
                <div class="col-3 py-0">
                    <p-dropdown appendTo="body" [options]="claimBasisOption" autoWidth="false" [style]="{'width':'100%'}"  [(ngModel)]="selectedBasis"
                        (onChange)="updateFormulaOptions('E', selectedBasis)" formControlName="basis" class="ui-dropdown-l-size">
                    </p-dropdown>
