                            <div *ngIf="getRealtimeCpfInfoAmount(selectedOWLatestMonth[0], 'ORD') === null; then owRealtimeNull else owRt"></div>
                            <ng-template #owRealtimeNull><td class="text-center">NA</td></ng-template>
                            <ng-template #owRt>
                                <td *ngIf="editItemForm?.get('snapshot').value === getRealtimeCpfInfoAmount(selectedOWLatestMonth[0], 'ORD'); else owRedAlertRt" class="text-center">
                                    Same
                                </td>
                                <ng-template #owRedAlertRt><td class="text-danger text-center font-semibold">{{ getRealtimeCpfInfoAmount(selectedOWLatestMonth[0], 'ORD') | number:'1.2-2' }}</td></ng-template>
