                        </tr>
                        <tr>
                            <td colspan="4" class="text-center font-medium" [ngStyle]="{backgroundColor:'var(--gray-50)'}">Breakdown</td>
                        </tr>
                        <tr>
                            <td [ngStyle]="{backgroundColor:'var(--gray-50)'}"></td>
                            <td class="text-center font-medium" [ngStyle]="{backgroundColor:'var(--gray-50)'}">Snapshot</td>
                            <td class="text-center font-medium" [ngStyle]="{backgroundColor:'var(--gray-50)'}">User</td>
                            <td rowspan="5" [ngStyle]="{backgroundColor:'var(--gray-50)'}"></td>
                        </tr>
                        <tr>
                            <td class="text-center" [ngStyle]="{backgroundColor:'var(--gray-50)'}">BS</td>
                            <td class="text-center"><p-inputNumber mode="decimal" [minFractionDigits]="2" [formControl]="$any(editItemForm?.controls['bsSnapshot'])"></p-inputNumber></td>
                            <td class="text-center"><p-inputNumber mode="decimal" [minFractionDigits]="2" [formControl]="$any(editItemForm?.controls['bsUser'])"></p-inputNumber></td>
                        </tr>
                        <tr>
                            <td class="text-center" [ngStyle]="{backgroundColor:'var(--gray-50)'}">FA</td>
                            <td class="text-center"><p-inputNumber mode="decimal" [minFractionDigits]="2" [formControl]="$any(editItemForm?.controls['faSnapshot'])"></p-inputNumber></td>
                            <td class="text-center"><p-inputNumber mode="decimal" [minFractionDigits]="2" [formControl]="$any(editItemForm?.controls['faUser'])"></p-inputNumber></td>
                        </tr>
                        <tr>
                            <td class="text-center" [ngStyle]="{backgroundColor:'var(--gray-50)'}">VA</td>
                            <td class="text-center"><p-inputNumber mode="decimal" [minFractionDigits]="2" [formControl]="$any(editItemForm?.controls['vaSnapshot'])" ></p-inputNumber></td>
                            <td class="text-center"><p-inputNumber mode="decimal" [minFractionDigits]="2" [formControl]="$any(editItemForm?.controls['vaUser'])"></p-inputNumber></td>
                        </tr>
                        <tr>
                            <td class="text-center" [ngStyle]="{backgroundColor:'var(--gray-50)'}">DED</td>
                            <td class="text-center"><p-inputNumber mode="decimal" [minFractionDigits]="2" [formControl]="$any(editItemForm?.controls['dedSnapshot'])"></p-inputNumber></td>
                            <td class="text-center"><p-inputNumber mode="decimal" [minFractionDigits]="2" [formControl]="$any(editItemForm?.controls['dedUser'])"></p-inputNumber></td>
                        </tr>
                        <tr>
                            <td class="text-center" [ngStyle]="{backgroundColor:'var(--gray-50)'}">Total</td>
                            <td class="text-center">{{editItemForm.value.snapshotTotalAmt | number:'1.2-2'}}</td>
                            <td class="text-center">{{editItemForm.value.userTotalAmt | number:'1.2-2'}}</td>
                            <td class="text-center" [ngStyle]="{backgroundColor:'var(--gray-50)'}">
                                <button type="button" pButton pRipple (click)="clearOWUser()">Clear User</button>
                            </td>
                        </tr>
                    </ng-template>
                </p-table>
            </div>
            <div class="col-3">
                <br/><br/><br/><br/>
                <label> Realtime </label>
                <p-table [value]="[[]]" styleClass="p-datatable-gridlines">
                    <ng-template pTemplate="header">
                        <tr>
                            <th colspan="4" class="text-center">Desired CPF 
                                <span *ngIf="!isEmpty(claimForm.get('trgPeriodFg.trgPeriodStartDt')?.value)">
                                    {{formatDate(this.dateUtilsService.minusMonths(claimForm.get('trgPeriodFg.trgPeriodStartDt')?.value, 1), 'MM-YYYY')}}
                                </span>
                            </th>
                        </tr>
                    </ng-template>
                    <ng-template pTemplate="body" let-products>
                        <tr>
                            <div *ngIf="this.dateUtilsService.isBefore(currentDate, this.dateUtilsService.convertStrToDate(realtimeCpf.latestContributionYearMonth, 'YYYY-MM-DD')); then owDesiredWait else owDesired"></div>
                            <ng-template #owDesiredWait><td class="text-center">Wait</td></ng-template>
                            <ng-template #owDesired>
                                <td *ngIf="!isEmpty(claimForm.get('trgPeriodFg.trgPeriodStartDt')?.value) && getOWDesiredCpf() !== null; else owDesiredNull" class="text-center">
                                    {{getOWDesiredCpf() | number:'1.2-2'}} (Found)
                                </td>
                                <ng-template #owDesiredNull><td class="text-center">NA</td></ng-template>
                            </ng-template> 
                        </tr>
                        <tr>
                            <td class="text-center font-medium" [ngStyle]="{backgroundColor:'var(--gray-50)'}">Latest Contribution Month</td>
                        </tr>
                        <tr>
                            <td *ngIf="realtimeCpf?.latestContributionYearMonth; else latestContriNA" class="text-center">{{formatDate(realtimeCpf.latestContributionYearMonth, 'MM-YYYY')}}</td>
                            <ng-template #latestContriNA>
                                <td class="text-center">NA</td>
                            </ng-template>
                        </tr>
                    </ng-template>
                </p-table>
            </div>
            <div class="col-12"></div>
            <div class="col-2">
                <label>Computation String:</label>
            </div>
            <div class="col-3" *ngIf="selectedFormula !== '13x';else inputComputeFormula">
                <label>{{editItemForm?.get('computedFormula').value}}</label>
            </div>
            <ng-template #inputComputeFormula>
                <div class="col-3">
                    <input type="input" pInputText formControlName="computedFormula" [style]="{'max-width': '100%'}">
                    <div><small class="p-error" *ngIf="showErrorMsg(editItemForm, 'computedFormula', 'required')" icTranslate="nspay2App.claim.validation.CLM-V001" [translateValues]="{ field: 'Computation string' }"></small></div>
                </div>
            </ng-template>
            <div class="col-12"></div>
            <div class="col-2" >
                <label>Computed Amount:</label>
            </div>
            <div class="col-3" *ngIf="selectedFormula !== '13x';else inputComputeAmt">
                <label>{{editItemForm?.get('computeAmt').value}}</label>
            </div>
            <ng-template #inputComputeAmt>
                <div class="col-3">
                    <input type="input" pInputText formControlName="computeAmt" [style]="{'max-width': '100%'}">
                    <div><small class="p-error" *ngIf="showErrorMsg(editItemForm, 'computeAmt', 'required')" icTranslate="nspay2App.claim.validation.CLM-V001" [translateValues]="{ field: 'Computed amount' }"></small></div>
                </div>
            </ng-template>
        </div>
        <!-- Edit OW END -->
        <!-- Edit AW START -->
        <div class="grid" *ngIf="selectedItemType === 'ADD'">
            <div class="col-6">
                <div class="field grid">
                    <label for="awLatestPeriod" class="col-12 mb-2 md:col-2 md:mb-0">Latest Month:</label>
                    <div class="col-12 md:col-10">
                        <p-dropdown id="awLatestPeriod" appendTo="body" [options]="awLatestPeriodOptions" autoWidth="false" [style]="{'width':'100%'}" class="ui-dropdown-l-size"
                            [(ngModel)]="selectedAWLatestPeriod" (onChange)="updateAWSnapshot()" [ngModelOptions]="{standalone: true}">
                        </p-dropdown>
                    </div>
                </div>
                <br/>
                <p-table [value]="[[]]" styleClass="p-datatable-gridlines" *ngIf="editItemForm">
                    <ng-template pTemplate="header">
                        <tr>
                            <th colspan="4" class="text-center">CPF {{this.editItemForm?.get('header').value}}</th>
                        </tr>
                    </ng-template>
                    <ng-template pTemplate="body" let-rowData>
                        <tr>
                            <td colspan="2" class="font-medium" [ngStyle]="{backgroundColor:'var(--gray-50)'}">Latest Contribution Month</td>
                            <td colspan="2">{{ this.editItemForm?.get('latestContriMonth').value }}</td>
                        </tr>
                        <tr>
                            <td [ngStyle]="{backgroundColor:'var(--gray-50)', 'width':'25%'}"></td>
                            <td class="text-center font-medium" [ngStyle]="{backgroundColor:'var(--gray-50)', 'width':'25%'}">Snapshot</td>
                            <td class="text-center font-medium" [ngStyle]="{backgroundColor:'var(--gray-50)', 'width':'25%'}">Appr Amt</td>
                            <td class="text-center font-medium" [ngStyle]="{backgroundColor:'var(--gray-50)', 'width':'25%'}">Realtime</td>
                        </tr>
                        <tr>
                            <td class="text-center" [ngStyle]="{backgroundColor:'var(--gray-50)'}">AW</td>
                            <td *ngIf="editItemForm?.get('snapshot').value !== null; else awSnapshotNull" class="text-center">
                                {{ editItemForm?.get('snapshot').value | number:'1.2-2'}}
                            </td>
                            <ng-template #awSnapshotNull><td class="text-center">NA</td></ng-template>

                            <td *ngIf="editItemForm?.get('snapshot').value === editItemForm?.value.apprAmt; else awRedAlert" class="text-center">
