                            </td>
                            <ng-template #awRedAlert><td class="text-danger text-center font-semibold">{{ editItemForm?.value.apprAmt | number:'1.2-2' }}</td></ng-template>
                            
                            <div *ngIf="editItemForm?.get('snapshot').value === computeRealtimeAW(); then awRealtimeSame else awRt"></div>
                            <ng-template #awRealtimeSame><td class="text-center">Same</td></ng-template>
                            <ng-template #awRt>
                                <td *ngIf="computeRealtimeAW() === null; else awRedAlertRt" class="text-center">
                                    NA
                                </td>
                                <ng-template #awRedAlertRt><td class="text-danger text-center font-semibold">{{ computeRealtimeAW() | number:'1.2-2' }}</td></ng-template>
                            </ng-template>  
                        </tr>
                        <tr>
                            <td class="text-center" [ngStyle]="{backgroundColor:'var(--gray-50)', 'width':'25%'}">Divisor</td>
                            <td class="text-center" [ngStyle]="{'width':'25%'}">{{editItemForm?.get('divisorSnapshot')?.value}}</td>
                            <td class="text-center" [ngStyle]="{'width':'25%'}">
                                <p-inputNumber mode="decimal" [useGrouping]="false" formControlName="divisorUser" [maxlength]="2" [max]="12"></p-inputNumber>
                                <span class="p-error" *ngIf="showErrorMsg(editItemForm, 'divisorUser', 'required')" icTranslate="nspay2App.claim.validation.CLM-V001" [translateValues]="{ field: 'Divisor' }"></span>
                            </td>
                            <div *ngIf="editItemForm?.get('divisorSnapshot').value === realtimeCpf.divisor; then awRealtimeDivisorSame else awRtDivisor"></div>
                            <ng-template #awRealtimeDivisorSame><td class="text-center" [ngStyle]="{'width':'25%'}">Same</td></ng-template>
                            <ng-template #awRtDivisor>
                                <td *ngIf="realtimeCpf.divisor === null; else awDivisorRed" class="text-center" [ngStyle]="{'width':'25%'}">
                                    NA
                                </td>
                                <ng-template #awDivisorRed><td class="text-danger text-center font-semibold" [ngStyle]="{'width':'25%'}">{{ realtimeCpf.divisor }}</td></ng-template>
                            </ng-template>
                        </tr>
                    </ng-template>
                </p-table>
            </div>

            <div class="col-6"></div>

            <div class="col-6">
                <div *ngIf="editItemForm" formArrayName="periodAmt">
                    <p-table [value]="periodAmtArr.controls" styleClass="p-datatable-gridlines">
                        <ng-template pTemplate="header">
                            <tr>
                                <th [ngStyle]="{'width':'25%'}"></th>
                                <th class="text-center" [ngStyle]="{'width':'25%'}">Snapshot</th>
                                <th class="text-center" [ngStyle]="{'width':'25%'}">User</th>
                                <th class="text-center" [ngStyle]="{'width':'25%'}">Realtime</th>
                            </tr>
                        </ng-template>
                        <ng-template pTemplate="body" let-rowData let-i="rowIndex">
                            <tr [formGroupName]="i">
                                <td class="text-center" [ngStyle]="{backgroundColor:'var(--gray-50)'}">{{rowData.value.monthYear}}</td>
                                <td *ngIf="rowData?.value.monthSnapshot !== null; else awMonthSnapshotNull" class="text-center">
                                    {{rowData.value.monthSnapshot | number:'1.2-2'}}
                                </td>
                                <ng-template #awMonthSnapshotNull><td class="text-center">NA</td></ng-template>

                                <td class="text-center">
                                    <p-inputNumber mode="decimal" [minFractionDigits]="2" formControlName="monthUser"></p-inputNumber>
                                    <!-- <p-inputNumber mode="decimal" [minFractionDigits]="2" [formControl]="rowData.controls['monthUser']"></p-inputNumber> -->
                                    <span class="p-error" *ngIf="showErrorMsg(rowData, 'monthUser', 'required')" icTranslate="nspay2App.claim.validation.CLM-V001" [translateValues]="{ field: 'Mth'.concat(i+1) }"></span>
                                </td>

                                <div *ngIf="rowData?.value.monthSnapshot === getRealtimeCpfInfoAmount(rowData?.value.monthYear, 'ADD'); then awMonthRealtimeSame else awMonthRt"></div>
                                <ng-template #awMonthRealtimeSame><td class="text-center">Same</td></ng-template>
                                <ng-template #awMonthRt>
                                    <td *ngIf="getRealtimeCpfInfoAmount(rowData?.value.monthYear, 'ADD') === null; else awMonthRedRt" class="text-center">
                                        NA
                                    </td>
                                    <ng-template #awMonthRedRt><td class="text-danger text-center font-semibold">{{ getRealtimeCpfInfoAmount(rowData?.value.monthYear, 'ADD') | number:'1.2-2' }}</td></ng-template>
                                </ng-template> 
                                <!-- <td *ngIf="rowData.value.monthRealtime !== 'NA'">{{rowData.value.monthRealtime | number:'1.2-2'}}</td>
                                <td *ngIf="rowData.value.monthRealtime === 'NA'">NA</td> -->
                            </tr>
                        </ng-template>
                    </p-table>
                </div>
            </div>
            <div class="col-12"></div>
            <div class="col-2">
                <label>Computation String:</label>
