                                </div>
                            </td>
                            <td class="text-center">
                                <p-inputNumber mode="decimal" [formControl]="$any(editItemForm?.controls['allwDivisor'])" [ngStyle]="{width:'9em'}" [inputStyle]="{width:'9em'}"></p-inputNumber>
                                <div>
                                    <small class="p-error" *ngIf="showErrorMsg(editItemForm, 'allwDivisor', 'required')" icTranslate="nspay2App.claim.validation.CLM-V001" [translateValues]="{ field: 'Divisor' }"></small>
                                </div>
                            </td>
                            <td class="text-center" [ngStyle]="{width:'9em'}">{{editItemForm?.value.apprAmt | number:'1.2-2'}}</td>
                        </tr>
                    </ng-template>
                </p-table>
            </div>
            <div class="col-6">
                <div class="field grid">
                    <label for="computedFormula" class="col-3 align-items-center justify-content-left col-form-label">Computation String:</label>
                    <div class="col-4" *ngIf="selectedFormula !== '13x';else inputComputeFormula">
                        {{editItemForm.get('computedFormula').value}}
                    </div>
                    <ng-template #inputComputeFormula>
                        <div class="col-4 input-group">
                            <input type="input" pInputText [formControl]="$any(editItemForm.controls['computedFormula'])" [style]="{'max-width': '100%'}">
                        </div>
                        <div class="col-5"></div>
                        <div class="col-3"></div>
                        <div class="col-4" style="margin-top: 0em">
                            <small class="p-error" *ngIf="showErrorMsg(editItemForm, 'computedFormula', 'required')" icTranslate="nspay2App.claim.validation.CLM-V001" [translateValues]="{ field: 'Computation string' }"></small>
                        </div>
                    </ng-template>
                </div>
            </div>
            <div class="col-6"></div>
            <div class="col-6">
                <div class="field grid">
                    <label for="computeAmt" class="col-3 align-items-center justify-content-left col-form-label">Computed Amount:</label>
                    <div class="col-4" *ngIf="selectedFormula !== '13x';else inputComputeAmt">
                        {{editItemForm.get('computeAmt').value | number:'1.2-2'}}
                    </div>
                    <ng-template #inputComputeAmt>
                        <div class="col-4 input-group">
                            <input type="input" pInputText [formControl]="$any(editItemForm.controls['computeAmt'])" [style]="{'max-width': '100%'}">
                        </div>
                        <div class="col-5"></div>
                        <div class="col-3"></div>
                        <div class="col-4" style="margin-top: 0em">
                            <small class="p-error" *ngIf="showErrorMsg(editItemForm, 'computeAmt', 'required')" icTranslate="nspay2App.claim.validation.CLM-V001" [translateValues]="{ field: 'Computed amount' }"></small>
                        </div>
                    </ng-template>
                </div>
            </div>
        </div>
        <!-- Edit OLD POLICY ONB END -->
    </form>
    <ng-template pTemplate="footer">
        <button pButton type="button" label="LOG" (click)="consoleLog()"></button>
        <button pButton type="button" class="p-button-danger" label="Cancel" (click)="exitEditItemModal()"></button>
        <button pButton type="button" label="Recompute" [disabled]="disableRecompute()" (click)="computeFormula()"></button>
        <button pButton type="button" *ngIf="selectedItemType === 'ORD'" [disabled]="disableUpdateForEdit()" (click)="updateOW()">{{buttonLabelForEditItem()}}</button>
        <button pButton type="button" *ngIf="selectedItemType === 'ADD'" [disabled]="disableUpdateForEdit()" (click)="updateAW()">{{buttonLabelForEditItem()}}</button>
        <button pButton type="button" *ngIf="selectedItemType === 'DF' && selecteddfFrequency === 'DIA'" 
            [disabled]="disableUpdateForEdit() || diaManualComputeFileForm?.get('file')?.hasError('invalidFileExtension') || diaManualComputeFileForm?.get('file')?.hasError('invalidMaxFileLimit')" (click)="updateDIA()">{{buttonLabelForEditItem()}}</button>
        <button pButton type="button" *ngIf="selectedItemType === 'DF' && selecteddfFrequency === 'DIM'" 
            [disabled]="disableUpdateForEdit() || dimManualComputeFileForm?.get('file')?.hasError('invalidFileExtension') || dimManualComputeFileForm?.get('file')?.hasError('invalidMaxFileLimit')" (click)="updateDIM()">{{buttonLabelForEditItem()}}</button>
        <button pButton type="button" *ngIf="selectedItemType === 'DIO'" [disabled]="disableUpdateForEdit()" (click)="updateDIO()">{{buttonLabelForEditItem()}}</button>
        <button pButton type="button" *ngIf="selectedItemType === 'INT'" [disabled]="disableUpdateForEdit()" (click)="updateINT()">{{buttonLabelForEditItem()}}</button>
        <button pButton type="button" *ngIf="selectedItemType === 'MIN'" [disabled]="disableUpdateForEdit()" (click)="updateMIN()">{{buttonLabelForEditItem()}}</button>
        <button pButton type="button" *ngIf="selectedItemType === 'OVE'" 
            [disabled]="disableUpdateForEdit() || oveManualComputeFileForm?.get('file')?.hasError('invalidFileExtension') || oveManualComputeFileForm?.get('file')?.hasError('invalidMaxFileLimit')" (click)="updateOVE()">{{buttonLabelForEditItem()}}</button>
        <button pButton type="button" *ngIf="selectedItemType === 'ECO'" [disabled]="disableUpdateForEdit()" (click)="updateECO()">{{buttonLabelForEditItem()}}</button>
        <button pButton type="button" *ngIf="isCurrentClaimOldPolicy === true && selectedItemType === 'OBS'" [disabled]="disableUpdateForEdit()" (click)="updateOBS()">{{buttonLabelForEditItem()}}</button>
        <button pButton type="button" *ngIf="isCurrentClaimOldPolicy === true 
            && selectedItemType !== 'OBS' && selectedItemType !== 'ECO' && selectedItemType !== 'DIO'" [disabled]="disableUpdateForEdit()" (click)="updateONB()">{{buttonLabelForEditItem()}}</button>
    </ng-template>
</p-dialog>
