import { ValidatorFn, AbstractControl } from '@angular/forms';

export class EitherOrValidator {

  static eitherOr(field1: string, field2: string): ValidatorFn {
    return (c: AbstractControl): { [key: string]: boolean } | null => {
      const firstField = c.get(field1).value;
      const secondField = c.get(field2).value;
      if ((firstField === null && secondField === null) || (firstField === '' && secondField === '')) {
        return { invalidEitherOr: true };
      }
      return null;
    };
  }
  
}
