import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpResponse } from '@angular/common/http';
import { Subscription, Observable } from 'rxjs';
import { EventManager } from 'app/core/util/event-manager.service';
import { CodeType } from '../code-type/code-type.model';
import { CodeTypeService } from '../code-type/code-type.service';
import { CodeService } from './code.service';
import { Code, CodeValid } from './code.model';
import { DatePipe } from '@angular/common';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { CodeDeleteDialogComponent } from '../code/code-delete-dialog.component';

@Component({
  selector: 'ic-code-manage',
  templateUrl: './code-manage.component.html',
})
export class CodeManageComponent implements OnInit, OnDestroy {
  eventSubscriber?: Subscription;
  subscription?: Subscription;
  routeData: any;
  codeType: CodeType;
  codes: Code[] = [];
  code: Code;
  parentCodeTypes: CodeType[] = [];
  parentCodes?: Code[];
  statuses: any[];
  effectiveDtDp: any;
  expiryDtDp: any;
  codeIdEn?: string;
  codeDescEn?: string;
  codeIdCn?: string;
  codeId?: string;
  codeDescCn?: string;
  seqInValidMsg: string;
  isChange: boolean;
  isFirst: boolean;
  isLast: boolean;

  constructor(
    private codeTypeService: CodeTypeService,
    private codeService: CodeService,
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private eventManager: EventManager,
    private datePipe: DatePipe,
    protected modalService: NgbModal
  ) {
    this.codeType = new CodeType();
    this.code = new Code();
    this.statuses = [
      { codeId: 'A', codeDesc: 'Active' },
      { codeId: 'I', codeDesc: 'Inactive' },
    ];
    this.seqInValidMsg = 'Code Seq is not valid or is existed';
    this.isFirst = false;
    this.isLast = false;
    this.isChange = false;
  }

  ngOnInit(): void {
    this.subscription = this.activatedRoute.params.subscribe(params => {
      this.findCodeType(params['codeTypePk']);
      this.getCodesByCodeTypePk(params['codeTypePk']);
    });

    this.registerChangeInCodes();
  }

  ngOnDestroy(): void {
    if (this.eventSubscriber) {
      this.eventManager.destroy(this.eventSubscriber);
    }
  }

  registerChangeInCodes(): void {
    this.eventSubscriber = this.eventManager.subscribe('codeListModification', () => this.getCodesByCodeTypePk(this.codeType.codeTypePk));
  }

  isFirstOrLast(codeId: string): void {
    if (codeId === this.codes[0].codeId) {
      this.isFirst = true;
      this.isLast = false;
    } else if (codeId === this.codes[this.codes.length - 1].codeId) {
      this.isLast = true;
      this.isFirst = false;
    } else {
      this.isFirst = false;
      this.isLast = false;
    }
  }

  saveChangeSeq(): void {
    let seq = 1;
    this.codes.forEach((item: Code, index: number) => {
      if (index !== 0 && item.codeId === this.codes[index - 1].codeId) {
        item.codeSeq = this.codes[index - 1].codeSeq;
      } else {
        item.codeSeq = seq++;
      }
    });
    this.codeService.changeCodeSeq(this.codes).subscribe(() => {
      this.registerChangeInCodes();
      this.selectCode(this.codeId);
      this.isChange = false;
    });
  }

  changeSeq(type: string): void {
    if (this.codeId === undefined) {
      this.showErrorMsg('There is no Code has been select');
    } else if (this.codes.length <= 1) {
      this.showErrorMsg('The amount of data is too small to change the sort');
    } else {
      this.isChange = true;
      const itemCode: Code[] = new Array<Code>();
      this.codes.forEach((item: Code, index: number) => {
        if (item.codeId === this.codeId) {
          if (itemCode.length === 0) {
            item.codeSeq = this.setSeqByType(type, index);
          } else {
            item.codeSeq = this.codes[index - 1].codeSeq;
          }
          itemCode.push(item);
        } else if (index !== 0 && item.codeId === this.codes[index - 1].codeId) {
          item.codeSeq = this.codes[index - 1].codeSeq;
        }
      });
      this.codes.sort(this.sortBySeq);
      this.isFirstOrLast(this.codeId);
    }
  }

  sort(): void {
    this.codes.forEach((item: Code, index: number) => {
      if (index !== 0 && item.codeId === this.codes[index - 1].codeId) {
        item.codeSeq = this.codes[index - 1].codeSeq;
      }
    });
    this.codes.sort(this.sortBySeq);
  }

  sortBySeq(a: any, b: any): any {
    if (a.codeId === b.codeId) {
      return 0;
    } else {
      return a.codeSeq - b.codeSeq;
    }
  }

  setSeqByType(type: string, index: number): any {
    switch (type) {
      case 'top':
        for (let i = 0; i < this.codes.length; i++) {
          if (this.codes[i].codeId === this.codes[0].codeId && this.codes[i].codeSeq != null) {
            return (this.codes[i] as any).codeSeq - 0.5;
          }
        }
        break;
      case 'up':
        // eslint-disable-next-line no-case-declarations
        let seq: any;
        this.codes.forEach((item: Code) => {
          if (item.codeId === this.codes[index - 1].codeId && item.codeSeq != null) {
            seq = item.codeSeq - 0.5;
          }
        });
        return Number(seq);
      case 'down':
        for (let i = index + 1; i < this.codes.length; i++) {
          if (this.codes[index].codeId !== this.codes[i].codeId && this.codes[i].codeSeq != null) {
            return Number((this.codes[i] as any).codeSeq) + 0.5;
          }
        }
        break;
      case 'bottom':
        for (let i = this.codes.length - 1; i >= 0; i--) {
          if (this.codes[i].codeId === this.codes[this.codes.length - 1].codeId && this.codes[i].codeSeq != null) {
            return Number((this.codes[i] as any).codeSeq) + 0.5;
          }
        }
        break;
    }
  }

  findCodeType(codeTypePk: any): void {
    this.codeTypeService.find(codeTypePk).subscribe(codeType => {
      this.codeType = codeType;
      this.getParentCodeTypes();
    });
  }

  getCodesByCodeTypePk(codeTypePk: any): void {
    this.codeService.getCodesByCodeTypePk({ codeTypePk, isExternal: false }).subscribe((data: any) => {
      this.codes = data.body;
      this.sort();
      this.init();
    });
  }

  getParentCodeTypes(): void {
    const appId = this.codeType.appId ? this.codeType.appId : 'NA';
    this.codeService.getInternalCodeTypesByAppIdAndCodeTypePkNot({ appId, codeTypePk: this.codeType.codeTypePk }).subscribe((data: any) => {
      this.parentCodeTypes = data.body;
    });
  }

  getParentCodes(): void {
    this.codeService.getCodesByCodeTypePk({ codeTypePk: this.code.parentCodeTypePk, isExternal: false }).subscribe((data: any) => {
      this.parentCodes = data.body;
    });
  }

  selectCode(codeId: any): void {
    if (codeId) {
      this.init();
      this.codeId = codeId;
      this.codeService.getCodesByCodeTypeIdAndCodeId({ codeTypePk: this.codeType.codeTypePk, codeId }).subscribe((data: any) => {
        const codes = data.body;
        if (codes) {
          this.code = codes[0];

          if (this.code.effectiveDt) {
            this.code.effectiveDt = this.datePipe.transform(this.code.effectiveDt, 'yyyy-MM-dd');
          }
          if (this.code.expiryDt) {
            this.code.expiryDt = this.datePipe.transform(this.code.expiryDt, 'yyyy-MM-dd');
          }

          for (let i = 0; i < codes.length; i++) {
            if (codes[i].locale === 'en') {
              this.codeIdEn = codes[i].id;
              this.codeDescEn = codes[i].codeDesc;
            }
            if (codes[i].locale === 'cn') {
              this.codeIdCn = codes[i].id;
              this.codeDescCn = codes[i].codeDesc;
            }
          }
          this.getParentCodeTypes();
          this.getParentCodes();
        }
        this.isFirstOrLast(codeId);
      });
    }
  }

  init(): void {
    this.code = new Code();
    this.codeIdEn = null as any;
    this.codeDescEn = null as any;
    this.codeIdCn = null as any;
    this.codeDescCn = null as any;
    this.getParentCodeTypes();
    this.getParentCodes();
  }

  save(): void {
    const validResult = this.isValid(this.code);
    if (validResult.valid) {
      this.subscribeToSaveResponse(this.code.id ? this.updateCode() : this.createCode());
    } else {
      this.showErrorMsg(validResult.errorMsg);
    }
  }

  goToSearchPage(): void {
    this.router.navigate(['/admin/ic-codetype']);
  }

  cancel(): void {
    this.goToSearchPage();
  }

  delete(code: Code): void {
    const modalRef = this.modalService.open(CodeDeleteDialogComponent, { size: 'lg', backdrop: 'static' });
    modalRef.componentInstance.code = code;
  }

  private createCode(): any {
    this.code.codeTypePK = this.codeType.codeTypePk;
    this.code.codeDescs = [];

    if (!this.codeDescEn) {
      this.codeDescEn = this.code.codeId;
    }
    if (this.codeDescEn) {
      this.code.codeDescs.push({ locale: 'en', codeDesc: this.codeDescEn });
    }
    if (this.codeDescCn) {
      this.code.codeDescs.push({ locale: 'cn', codeDesc: this.codeDescCn });
    }

    return this.codeService.create(this.code);
  }

  private updateCode(): any {
    this.code.codeDescs = [];
    if (!this.codeDescEn) {
      this.codeDescEn = this.code.codeId;
    }
    this.code.codeDescs.push({ id: this.codeIdEn, locale: 'en', codeDesc: this.codeDescEn });
    this.code.codeDescs.push({ id: this.codeIdCn, locale: 'cn', codeDesc: this.codeDescCn });

    return this.codeService.update(this.code);
  }

  private subscribeToSaveResponse(result: Observable<Code>): void {
    result.subscribe(
      (res: Code) => this.onSaveSuccess(res),
      (res: HttpResponse<any>) => this.onSaveError(res)
    );
  }

  private onSaveSuccess(result: Code): void {
    this.getCodesByCodeTypePk(this.codeType.codeTypePk);
    this.selectCode(result.codeId);
  }

  private onSaveError(error: any): void {
    const appDialogErrorMsg = error.headers.get('X-nspay2App-alert');
    document.getElementById('appDialogErrorMsg')!.innerHTML = appDialogErrorMsg;
    document.getElementById('appDialogErrorMsg')!.style.display = 'block';

    window.scrollTo(0, 0);
  }
  private isValid(newCode: Code): any {
    const isValidResult = new CodeValid();
    isValidResult.valid = true;
    const errorMsg = this.seqInValidMsg;
    if ((newCode as any).codeSeq <= 0) {
      isValidResult.valid = false;
      isValidResult.errorMsg = errorMsg;
      return isValidResult;
    }
    this.codes.forEach((code: Code) => {
      if (code.codeSeq === newCode.codeSeq && newCode.codeId !== code.codeId) {
        isValidResult.valid = false;
        isValidResult.errorMsg = errorMsg;
      }
    });
    return isValidResult;
  }

  private showErrorMsg(errorMsg: string): void {
    document.getElementById('appDialogErrorMsg')!.innerHTML = errorMsg;
    document.getElementById('appDialogErrorMsg')!.style.display = 'block';
    window.setTimeout(function (): void {
      document.getElementById('appDialogErrorMsg')!.style.display = 'none';
    }, 3000);
    window.scrollTo(0, 0);
  }
}
<div>
  <h2>Mange Code</h2>
  <p style="display: none" id="appDialogErrorMsg" class="alert-dismissible alert-danger alert"></p>
  <ic-alert></ic-alert>
  <br />
  <div style="position: absolute; top: 250px; left: -10px; z-index: 1">
    <span
      [ngStyle]="
        isFirst
          ? { 'font-size': '40px', 'font-weight': 'bold', 'margin-top': '30px', cursor: 'not-allowed', color: '#80808085' }
          : { 'font-size': '40px', 'font-weight': 'bold', 'margin-top': '30px', cursor: 'pointer', color: '#3e8acc' }
      "
      (click)="isFirst ? '' : changeSeq('top')"
      ><fa-icon [icon]="'angle-double-up'"></fa-icon
    ></span>
    <br />
    <span
      [ngStyle]="
        isFirst
          ? { 'font-size': '40px', 'font-weight': 'bold', 'margin-top': '30px', cursor: 'not-allowed', color: '#80808085' }
          : { 'font-size': '40px', 'font-weight': 'bold', 'margin-top': '30px', cursor: 'pointer', color: '#3e8acc' }
      "
      (click)="isFirst ? '' : changeSeq('up')"
      ><fa-icon [icon]="'angle-up'"></fa-icon
    ></span>
    <br />
    <span
      [ngStyle]="
        isLast
          ? { 'font-size': '40px', 'font-weight': 'bold', 'margin-top': '30px', cursor: 'not-allowed', color: '#80808085' }
          : { 'font-size': '40px', 'font-weight': 'bold', 'margin-top': '30px', cursor: 'pointer', color: '#3e8acc' }
      "
      (click)="isLast ? '' : changeSeq('down')"
      ><fa-icon [icon]="'angle-down'"></fa-icon
    ></span>
    <br />
    <span
      [ngStyle]="
        isLast
          ? { 'font-size': '40px', 'font-weight': 'bold', 'margin-top': '30px', cursor: 'not-allowed', color: '#80808085' }
          : { 'font-size': '40px', 'font-weight': 'bold', 'margin-top': '30px', cursor: 'pointer', color: '#3e8acc' }
      "
      (click)="isLast ? '' : changeSeq('bottom')"
      ><fa-icon [icon]="'angle-double-down'"></fa-icon
    ></span>
  </div>
  <div class="row" style="margin-left: 0">
    <div class="col-sm-2">
      <button type="button" class="btn btn-primary btn-sm" (click)="init()"><fa-icon [icon]="'plus'"></fa-icon>&nbsp;Add</button>
      <button [disabled]="!isChange" class="btn btn-primary btn-sm" (click)="saveChangeSeq()">
        <fa-icon [icon]="'archive'"></fa-icon>&nbsp;Save Sequence
      </button>
      <select
        multiple
        class="form-control"
        style="width: 110%; height: 595px; margin: 10px 0; overflow-x: auto"
        (click)="selectCode($any($event.target).value); isFirstOrLast($any($event.target).value)"
      >
        <option style="padding: 5px" *ngFor="let code of codes" [value]="code.codeId" title="{{ code.codeDesc }}">{{ code.codeId }}</option>
      </select>

      <button type="submit" (click)="delete(code)" class="btn btn-danger btn-sm">
        <fa-icon icon="times"></fa-icon>
        <span class="hidden-md-down" icTranslate="entity.action.delete">Delete</span>
      </button>
    </div>

    <div class="col-sm-10">
      <form name="editForm" role="form"  #editForm="ngForm">
        <div class="card" style="margin-top: 42px">
          <div class="card-header">Code Detail</div>
          <div class="card-block" style="padding: 1.5%">
            <div class="rounded-pill row">
              <label class="col-sm-2 form-control-label" icTranslate="codeAdmin.codeType.application" for="application">Application</label>
              <div class="col-sm-4">
                <input id="application" type="text" class="form-control" [(ngModel)]="codeType.appId" name="appId" [readonly]="true" />
              </div>

              <label class="col-sm-2 form-control-label" icTranslate="codeAdmin.codeType.main" for="codeType">Code Type</label>
              <div class="col-sm-4">
                <input
                  id="codeType"
                  type="text"
                  class="form-control"
                  [(ngModel)]="codeType.codeTypeId"
                  name="codeTypeId"
                  [readonly]="true"
                />
              </div>
            </div>

            <div class="rounded-pill row">
              <label class="col-sm-2 form-control-label" icTranslate="codeAdmin.code.maintain.parentCodeType" for="parentCodeTypePk"
                >Parent Code Type</label
              >
              <div class="col-sm-4" *ngIf="!code.id">
                <select
                  class="form-control"
                  [(ngModel)]="code.parentCodeTypePk"
                  id="parentCodeTypePk"
                  name="parentCodeTypePk"
                  (change)="getParentCodes()"
                >
                  <option [value]=""></option>
                  <option *ngFor="let codeType of parentCodeTypes" [value]="codeType.codeTypePk">{{ codeType.codeTypeDesc }}</option>
                </select>
              </div>
              <div class="col-sm-4" *ngIf="code.id">
                <select
                  class="form-control"
                  [(ngModel)]="code.parentCodeTypePk"
                  name="parentCodeTypePk"
                  (change)="getParentCodes()"
                  disabled
                >
                  <option [value]=""></option>
                  <option *ngFor="let codeType of parentCodeTypes" [value]="codeType.codeTypePk">{{ codeType.codeTypeDesc }}</option>
                </select>
              </div>

              <label class="col-sm-2 form-control-label" icTranslate="codeAdmin.code.maintain.parentCodeId" for="parentCodePk"
                >Parent Code Id</label
              >
              <div class="col-sm-4" *ngIf="!code.id">
                <select class="form-control" [(ngModel)]="code.parentCodePk" id="parentCodePk" name="parentCodePk">
                  <option [value]=""></option>
                  <option *ngFor="let code of parentCodes" [value]="code.id">{{ code.codeDesc }}</option>
                </select>
              </div>
              <div class="col-sm-4" *ngIf="code.id">
                <select class="form-control" [(ngModel)]="code.parentCodePk" name="parentCodePk" disabled>
                  <option [value]=""></option>
                  <option *ngFor="let code of parentCodes" [value]="code.id">{{ code.codeDesc }}</option>
                </select>
              </div>
            </div>

            <div class="rounded-pill row">
              <label class="col-sm-2 form-control-label" icTranslate="codeAdmin.code.id" for="codeId">Code Id</label>
              <div class="col-sm-4" *ngIf="!code.id">
                <input id="codeId" type="text" class="form-control" [(ngModel)]="code.codeId" name="codeId" required />
                <div [hidden]="!(editForm.controls.codeId?.dirty && editForm.controls.codeId?.invalid)">
                  <small
                    class="form-text text-danger"
                    [hidden]="!editForm.controls.codeId?.errors?.required"
                    icTranslate="entity.validation.required"
                  >
                    This field is required.
                  </small>
                </div>
              </div>
              <div class="col-sm-4" *ngIf="code.id">
                <input type="text" class="form-control" [(ngModel)]="code.codeId" name="codeId" [readonly]="true" />
              </div>

              <label class="col-sm-2 form-control-label" icTranslate="codeAdmin.code.maintain.status" for="status">Status</label>
              <div class="col-sm-4">
                <select class="form-control" [(ngModel)]="code.status" id="status" name="status" required>
                  <option *ngFor="let status of statuses" [value]="status.codeId">{{ status.codeDesc }}</option>
                </select>
                <div [hidden]="!(editForm.controls.status?.dirty && editForm.controls.status?.invalid)">
                  <small
                    class="form-text text-danger"
                    [hidden]="!editForm.controls.status?.errors?.required"
                    icTranslate="entity.validation.required"
                  >
                    This field is required.
                  </small>
                </div>
              </div>
            </div>

            <div class="rounded-pill row">
              <label class="col-sm-2 form-control-label" icTranslate="codeAdmin.code.maintain.effectiveDate" for="effectiveDt"
                >Effective Date</label
              >
              <div class="col-sm-4">
                <div class="input-group">
                  <input type="date" class="form-control" [(ngModel)]="code.effectiveDt" id="effectiveDt" name="effectiveDt" />
                </div>
              </div>

              <label class="col-sm-2 form-control-label" icTranslate="codeAdmin.code.maintain.expiryDate" for="expiryDt">Expiry Date</label>
              <div class="col-sm-4">
                <div class="input-group">
                  <input type="date" class="form-control" [(ngModel)]="code.expiryDt" id="expiryDt" name="expiryDt" />
                </div>
              </div>
            </div>
            <div class="rounded-pill row">
              <label class="col-sm-2 form-control-label" icTranslate="codeAdmin.code.seq" for="codeSeq">Code Seq</label>
              <div class="col-sm-4">
                <input id="codeSeq" type="number" class="form-control" [(ngModel)]="code.codeSeq" name="codeSeq" />
              </div>
            </div>
          </div>
        </div>

        <div class="card" style="margin-top: 23px">
          <div class="card-header">Code International</div>
          <div class="card-block" style="padding: 1.5%">
            <table class="table">
              <thead>
                <tr>
                  <th scope="col" style="width: 16%">Locale</th>
                  <th scope="col">Translation</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>en</td>
                  <td>
                    <input id="codeDescEn" type="text" class="form-control" [(ngModel)]="codeDescEn" name="codeDescEn" />
                  </td>
                </tr>
                <tr>
                  <td>cn</td>
                  <td>
                    <input type="text" class="form-control" [(ngModel)]="codeDescCn" name="codeDescCn" />
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>

        <div style="margin-top: 23px; text-align: right">
          <button class="btn btn-primary btn-sm" [disabled]="editForm.form.invalid" (click)="save()">
            <fa-icon [icon]="'save'"></fa-icon>&nbsp;<span icTranslate="codeAdmin.btn.save">Save</span>
          </button>
          <button class="btn btn-secondary btn-sm" (click)="cancel()">
            <fa-icon [icon]="'ban'"></fa-icon>&nbsp;<span icTranslate="codeAdmin.btn.cancel">Cancel</span>
          </button>
        </div>
      </form>
    </div>
  </div>
</div>
