import { ScheduleService } from './schedule.service';
import {
  Component,
  OnInit,
  OnDestroy,
  ChangeDetectorRef,
  ViewChild,
  AfterViewInit,
  Inject,
  LOCALE_ID
} from '@angular/core';
import { ITEMS_PER_PAGE } from 'app/config/pagination.constants';
import { HttpErrorResponse, HttpHeaders, HttpParams, HttpResponse } from '@angular/common/http';
import { Job, JobEnabled, JobGroup, JobStatus} from './schedule.model';
import { Alert, AlertService } from 'app/core/util/alert.service';
import { ParseLinks } from 'app/core/util/parse-links.service';
import { Subscription, combineLatest, of } from 'rxjs';
import { ActivatedRoute, Router } from '@angular/router';
import { formatDate } from '@angular/common';
import { ConfirmationService, SelectItemGroup } from 'primeng/api';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { ScheduleAdhocDialogComponent } from './schedule-adhoc-dialog.component';
import { CodeView, IcCodeService } from 'app/ng-connect';
import { Table } from 'primeng/table';
import { FilterMetadata, LazyLoadEvent } from 'primeng/api';
import { Ns2HttpService } from 'app/shared/service/ns2-http.service';
import { ExportUtilsService } from 'app/core/util/export-utils.service';

@Component({
  selector: 'ic-schedule',
  templateUrl: './schedule.component.html',
  styleUrls: ['./schedule.component.scss'],
})
export class ScheduleComponent implements OnInit, OnDestroy {
  public RUNNING = 'running';
  public STANDBY = 'standby';
  public ERROR = 'error';
  public WAIT = 'waiting';
  public EXPIRED = 'expired';
  public ENABLED = 'Enabled';
  public DISABLED = 'Disabled';

  public DEACTIVATED = 'D';
  public ACTIVATED = 'A';

  public searchCriteria?: any;
  public jobs?: Job[];
  public totalItems?: number;
  public queryCount?: number;
  public itemsPerPage = ITEMS_PER_PAGE;
  public page!: number;
  public ascending!: boolean;
  public predicate!: string;
  public reverse?: boolean;
  public jobList?: CodeView[];
  public jobGroupList = JobGroup;
  public statusList = JobStatus;
  public enabledList = JobEnabled;

  public lazyLoadEvent?: LazyLoadEvent;
  public loading = false;
  public totalRecords = 0;
  public pageData: { currentPage: number } = {
    currentPage: 0
  }
  public pageSize = ITEMS_PER_PAGE;

  private links?: any;
  private routeData?: Subscription;
  private previousPage?: number;

  constructor(
    private router: Router,
    private confirmationService: ConfirmationService,
    private scheduleService: ScheduleService,
    private parseLinks: ParseLinks,
    private alertService: AlertService,
    private activatedRoute: ActivatedRoute,
    private codeService: IcCodeService,
    private changeDetectorRef: ChangeDetectorRef,
    protected modalService: NgbModal,
    private n2httpService: Ns2HttpService,
    private exportUtilsService: ExportUtilsService,
    @Inject(LOCALE_ID) protected localeId: string
  ) {
    this.resetSearchCriteria();
  }

  ngAfterContentChecked(): void {
    this.changeDetectorRef.detectChanges();
  }

  load(event: LazyLoadEvent): void {
    this.loading = true;
    this.lazyLoadEvent = event;

    const options = this.n2httpService.convertLazyEvent(event, this.pageData, this.pageSize);

    // this.scheduleService.search(options).subscribe(
    //   (res: HttpResponse<any>) => this.onSuccess(res.body, res.headers),
    //   (res: HttpResponse<any>) => this.onError(res.body)
    // );

    this.scheduleService.search(options).subscribe(
      data => {
        console.log("data",data);
        this.jobs = data.body?.data?.content;
        this.totalRecords = data.body?.data?.totalElements;
        this.loading = false;
      },
      error => {
        console.log(error.error?.error_desc);
        this.loading = false;
      })
  }

  onPage(event: any): void {
    this.pageSize = event.rows;
    this.pageData.currentPage = event.first / event.rows;
  }

  exportCSV(dataTable: Table): void {
    const options = this.n2httpService.convertLazyEvent(this.lazyLoadEvent, 0, this.totalRecords);

    this.scheduleService.search(options).subscribe(
      resp => {
        if(resp.body?.data?.content) {

          const data = resp.body?.data?.content;

          const columns =
            [
              {field: 'sn', header: 'SN', index: 0},
              {field: 'id', header: 'Job Schedule ID', index: 1},
              {field: 'name', header: 'Job Name', index: 2},
              {field: 'group', header: 'Job Group', index: 3},
              {field: 'description', header: 'Job Description', index: 4},
              {field: 'cron', header: 'Schedule Cron', index: 5},
              {field: 'status', header: 'Status', index: 6},
              {field: 'enabled', header: 'Enabled', index: 7}
            ];

          const dtoList:any[] = [];
          console.log("data", data);
          data.forEach((d: Job, index: number) => {
            const dto = {
              sn: index + 1,
              id: d.id,
              name: d.name,
              group: d.group,
              description: d.description,
              cron: d.cron,
              status: d.status,
              enabled: d.enabled
            }
            dtoList.push(dto);
          });
          this.exportUtilsService.exportCSV(dataTable, dtoList,resp.body.data.total_no_of_elems, columns,
            "data_".concat(formatDate(new Date(),"dd-MM-YYYY", this.localeId)));
        }
      },
      error => {
        console.log(error.error?.error_desc);
        this.loading = false;
      })
  }

  public stopJob(name: string, group: string): void {
    this.scheduleService.stopJob(name, group).subscribe(
      () => {
        // this.loadAll();
      },
      error => {
        this.onError(error);
      }
    );
  }

  public startJob(name: string, group: string): void {
    this.scheduleService.startJob(name, group).subscribe(
      () => {
        // this.loadAll();
      },
      error => {
        this.onError(error);
      }
    );
  }

