  runAdhocJob(jobId: string): void {
    const modalRef = this.modalService.open(ScheduleAdhocDialogComponent, { size: 'lg', backdrop: 'static' });
    modalRef.componentInstance.jobId = jobId;
  }

  ngOnInit(): void {
