<div>
  <h2>
    <span icTranslate="schedule.home.title">Schedule</span>
  </h2>
  <ic-alert></ic-alert>
  <br />

  <div class="card">
    <p-table #jobTable [value]="jobs" dataKey="id"
             styleClass="p-datatable-gridlines"
             [rowsPerPageOptions]="[10,25,50]"
             [rows]="pageSize"
             [autoLayout]="true"
             [showCurrentPageReport]="true"
             currentPageReportTemplate="Showing {first} to {last} of {totalRecords} entries"
             [lazy]="true"
             [totalRecords]="totalRecords"
             [paginator]="true"
             [loading]="loading"
             (onLazyLoad)="load($event)"
             (onPage)="onPage($event)">
      <ng-template pTemplate="caption">
        <div class="flex">
          <button pButton label="All" class="p-button-outlined" (click)="clearFilter(jobTable)"></button>
          <div class='justify-content-start align-items-center'>
            <button type='button' pButton pRipple icon='pi pi-file-o' (click)='exportCSV(jobTable)' pTooltip='Export as CSV'
                    tooltipPosition='bottom'></button>
          </div>
          <button id="createSchedule" class="btn btn-primary btn-sm" style="margin-left: auto;" [routerLink]="['/admin/schedule/maintain']">
            <fa-icon [icon]="'plus'"></fa-icon>
            <span icTranslate="schedule.btn.createLabel">Create the new Schedule</span>
          </button>
        </div>
      </ng-template>
      <ng-template pTemplate="header">
        <tr>
          <th scope="col"><span icTranslate="schedule.table.sn">S/No.</span></th>
          <th scope="col" pSortableColumn="id">
            <div icTranslate="schedule.table.id">
              ID
            </div>
            <p-sortIcon field="id"></p-sortIcon>
            <p-columnFilter [showAddButton]="false" [showOperator]="false" [showMatchModes]="false" type="text" field="id" display="menu" matchMode="contains"></p-columnFilter>
          </th>
          <th scope="col" pSortableColumn="name">
            <div icTranslate="schedule.table.name">
              Name
            </div>
            <p-sortIcon field="name"></p-sortIcon>
            <p-columnFilter [showAddButton]="false" [showOperator]="false" [showMatchModes]="false" type="text" field="name" display="menu" matchMode="in">
              <ng-template pTemplate="filter" let-value let-filter="filterCallback">
                <p-multiSelect [ngModel]="value" [options]="jobList" placeholder="Any"
                               (onChange)="filter($event.value)" optionLabel="codeId" optionValue="codeId">
                </p-multiSelect>
              </ng-template>
            </p-columnFilter>
          </th>
          <th scope="col"  pSortableColumn="group">
            <div icTranslate="schedule.table.group">
              Group
            </div>
            <p-sortIcon field="group"></p-sortIcon>
            <p-columnFilter [showAddButton]="false" [showOperator]="false" [showMatchModes]="false" type="text" field="group" display="menu" matchMode="in">
              <ng-template pTemplate="filter" let-value let-filter="filterCallback">
                <p-multiSelect [ngModel]="value" [options]="jobGroupList" placeholder="Any"
                               (onChange)="filter($event.value)" optionLabel="label" optionValue="value">
                </p-multiSelect>
              </ng-template>
            </p-columnFilter>
          </th>
          <th scope="col" pSortableColumn="description">
            <div icTranslate="schedule.table.desc">
              Description
            </div>
            <p-sortIcon field="description"></p-sortIcon>
            <p-columnFilter [showAddButton]="false" [showOperator]="false" [showMatchModes]="false" type="text" field="description" display="menu" matchMode="contains"></p-columnFilter>
          </th>
          <th scope="col" pSortableColumn="cron">
            <div icTranslate="schedule.table.cron">
              Cron
            </div>
            <p-sortIcon field="cron"></p-sortIcon>
            <p-columnFilter [showAddButton]="false" [showOperator]="false" [showMatchModes]="false" type="text" field="cron" display="menu" matchMode="equals"></p-columnFilter>
          </th>
          <th scope="col" pSortableColumn="status">
            <div icTranslate="schedule.table.status">
              Status
            </div>
            <p-sortIcon field="status"></p-sortIcon>
            <p-columnFilter [showAddButton]="false" [showOperator]="false" [showMatchModes]="false" type="text" field="status" display="menu" matchMode="in">
              <ng-template pTemplate="filter" let-value let-filter="filterCallback">
                <p-multiSelect [ngModel]="value" [options]="statusList" placeholder="Any"
                               (onChange)="filter($event.value)" optionLabel="label" optionValue="value">
                </p-multiSelect>
              </ng-template>
            </p-columnFilter>
          </th>
          <th scope="col" pSortableColumn="status">
            <div icTranslate="schedule.table.enabled">
              Enabled
            </div>
            <p-sortIcon field="status"></p-sortIcon>
            <p-columnFilter [showAddButton]="false" [showOperator]="false" [showMatchModes]="false" type="text" field="status" display="menu" matchMode="equals">
              <ng-template pTemplate="filter" let-value let-filter="filterCallback">
                <p-dropdown  [ngModel]="value" [options]="enabledList" placeholder="Any"
                               (onChange)="filter($event.value)" optionLabel="label" optionValue="value">
                </p-dropdown >
              </ng-template>
            </p-columnFilter>
          </th>
          <th scope="col">
            <div icTranslate="schedule.table.action">
              Action
            </div>
          </th>
        </tr>
      </ng-template>
      <ng-template pTemplate="body" let-job let-rowIndex="rowIndex">
        <tr>
          <td>{{rowIndex + 1}}</td>
          <td>
            {{job.id}}
          </td>
          <td>
            {{job.name}}
          </td>
          <td>
            {{job.group}}
          </td>
          <td>
            {{ job.description }}
          </td>
          <td>
            {{ job.cron }}
          </td>
          <td>
            {{job.status}}
          </td>
          <td>
            <div [ngSwitch]="job.enabled">
              <span *ngSwitchCase="ACTIVATED">
                <label>Enabled</label>
              </span>
              <span *ngSwitchCase="DEACTIVATED">
                <label>Disabled</label>
              </span>
            </div>
          </td>
          <td>
            <div class="btn-group flex-btn-group-container">
              <span *ngIf="job.status === RUNNING || job.status === WAIT || job.status === EXPIRED; else disableAdhoc">
                <button class="btn btn-info btn-sm" (click)="runAdhocJob(job.id)">
                  <fa-icon [icon]="'play'"></fa-icon>
                  <span class="hidden-md-down" icTranslate="schedule.btn.adhoc">Adhoc</span>
                </button>&nbsp;&nbsp;&nbsp;&nbsp;
              </span>
              <ng-template #disableAdhoc>
                <span>
                  <button class="btn btn-info btn-sm" [disabled]="true">
                    <fa-icon [icon]="'play'"></fa-icon>
                    <span class="hidden-md-down" icTranslate="schedule.btn.adhoc">Adhoc</span>
                  </button>&nbsp;&nbsp;&nbsp;&nbsp;
                </span>
              </ng-template>
              <span *ngIf="job.status === STANDBY || job.status === ERROR || job.status === RUNNING || job.status === WAIT || job.status === EXPIRED">
                <button
                  type="submit"
                  [routerLink]="['/admin/schedule/maintain/' + job.id + '/0']"
                  [replaceUrl]="true"
                  class="btn btn-primary btn-sm">
                  <fa-icon [icon]="'pencil-alt'"></fa-icon>
                  <span class="hidden-md-down" icTranslate="schedule.btn.view">View</span>
                </button>
              </span>
            </div>
          </td>
        </tr>
      </ng-template>
      <ng-template pTemplate="emptymessage">
        <tr>
          <td colspan="7">No schedule found.</td>
        </tr>
      </ng-template>
    </p-table>
  </div>
</div>
<p-confirmDialog header="Confirmation" [style]="{ width: '50vw' }" icon="pi pi-exclamation-triangle"></p-confirmDialog>
