import {Injectable} from '@angular/core';
import {HttpClient, HttpResponse} from '@angular/common/http';
import {Observable} from 'rxjs';
import {ApplicationConfigService} from 'app/core/config/application-config.service';
import {IHolidayApproval} from './public-holiday-approval.model';
import {IPublicHoliday} from 'app/administration/black-out-date/public-holiday/public-holiday.model';

export type EntityResponseType = HttpResponse<IHolidayApproval>;
export type EntityArrayResponseType = HttpResponse<IHolidayApproval[]>;

@Injectable({ providedIn: 'root' })
export class PublicHolidayApprovalService {
  public resourceUrl = this.applicationConfigService.getEndpointFor('api/public-holiday');

  constructor(protected http: HttpClient, private applicationConfigService: ApplicationConfigService) {}

  getCurrentPending(): Observable<EntityArrayResponseType> {
    return this.http.get<IPublicHoliday[]>(this.resourceUrl + "/currentPending", { observe: 'response' });
  }

  approve(publicHolidayList: IPublicHoliday[]): Observable<EntityArrayResponseType> {
    return this.http.post<IPublicHoliday[]>(this.resourceUrl + "/approve", publicHolidayList, { observe: 'response' });
  }

  reject(publicHolidayList: IPublicHoliday[]): Observable<EntityArrayResponseType> {
    return this.http.post<IPublicHoliday[]>(this.resourceUrl + "/reject", publicHolidayList, { observe: 'response' });
  }
}
