import { Component, OnInit, OnDestroy } from '@angular/core';
import { HttpErrorResponse, HttpHeaders, HttpResponse } from '@angular/common/http';
import { ActivatedRoute, Router } from '@angular/router';
import { Subscription, combineLatest } from 'rxjs';
import { AlertService } from 'app/core/util/alert.service';
import { EventManager } from 'app/core/util/event-manager.service';
import { ParseLinks } from 'app/core/util/parse-links.service';
import { IEmailTemplate } from './email-template.model';
import { AccountService } from 'app/core/auth/account.service';
import { ITEMS_PER_PAGE } from 'app/config/pagination.constants';
import { EmailTemplateService } from './email-template.service';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { EmailTemplateDeleteDialogComponent } from './email-template-delete-dialog.component';

@Component({
  selector: 'ic-email-template',
  templateUrl: './email-template.component.html',
})
export class EmailTemplateComponent implements OnInit, OnDestroy {
  currentAccount: any;
  emailTemplates?: IEmailTemplate[];
  error: any;
  success: any;
  eventSubscriber?: Subscription;
  routeData: any;
  links: any;
  totalItems: any;
  itemsPerPage = ITEMS_PER_PAGE;
  page!: number;
  ascending!: boolean;
  predicate!: string;
  previousPage: any;
  reverse: any;

  constructor(
    protected emailTemplateService: EmailTemplateService,
    protected parseLinks: ParseLinks,
    protected alertService: AlertService,
    protected accountService: AccountService,
    protected activatedRoute: ActivatedRoute,
    protected router: Router,
    protected eventManager: EventManager,
    protected modalService: NgbModal
  ) {}

  loadAll(): void {
    this.emailTemplateService
      .query({
        page: this.page - 1,
        size: this.itemsPerPage,
        sort: this.sort(),
      })
      .subscribe(
        (res: HttpResponse<IEmailTemplate[]>) => this.paginateEmailTemplates(res.body as any, res.headers),
        (res: HttpErrorResponse) => this.onError(res.message)
      );
  }

  loadPage(page: number): void {
    if (page !== this.previousPage) {
      this.previousPage = page;
      this.transition();
    }
  }

  transition(): void {
    this.router.navigate(['admin/ic-email-template'], {
      queryParams: {
        page: this.page,
        size: this.itemsPerPage,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    });
  }

  clear(): void {
    this.page = 0;
    this.router.navigate([
      'admin/ic-email-template',
      {
        page: this.page,
        sort: this.predicate + ',' + (this.reverse ? 'asc' : 'desc'),
      },
    ]);
  }

  ngOnInit(): void {
    this.accountService.identity().subscribe(account => (this.currentAccount = account));
    this.handleNavigation();
    this.registerChangeInEmailTemplates();
  }

  ngOnDestroy(): void {
    if (this.eventSubscriber) {
      this.eventManager.destroy(this.eventSubscriber);
    }
  }

  trackId(index: number, item: IEmailTemplate): string {
    return item.id as string;
  }

  registerChangeInEmailTemplates(): void {
    this.eventSubscriber = this.eventManager.subscribe('emailTemplateListModification', () => this.loadAll());
  }

  sort(): any {
    const result = [this.predicate + ',' + (this.reverse ? 'asc' : 'desc')];
    if (this.predicate !== 'id') {
      result.push('id');
    }
    return result;
  }

  delete(emailTemplate: IEmailTemplate): void {
    const modalRef = this.modalService.open(EmailTemplateDeleteDialogComponent, { size: 'lg', backdrop: 'static' });
    modalRef.componentInstance.emailTemplate = emailTemplate;
  }

  protected paginateEmailTemplates(data: IEmailTemplate[], headers: HttpHeaders): void {
    this.links = this.parseLinks.parse(headers.get('link') as any);
    this.totalItems = parseInt(headers.get('X-Total-Count') as any, 10);
    this.emailTemplates = data;
  }

  protected onError(errorMessage: string): void {
    this.alertService.addAlert({ type: 'danger', translationKey: errorMessage });
  }

  private handleNavigation(): void {
    combineLatest([this.activatedRoute.data, this.activatedRoute.queryParamMap]).subscribe(([data, params]) => {
      const page = params.get('page');
      this.page = page !== null ? +page : 1;
      const sort = (params.get('sort') ?? data['defaultSort']).split(',');
      this.predicate = sort[0];
      this.ascending = sort[1] === 'asc';
      this.loadAll();
    });
  }
}
<div>
  <h2 id="page-heading">
    <span icTranslate="emailTemplateAdmin.emailTemplate.home.title">Email Templates</span>
    <button
      id="jh-create-entity"
      class="btn btn-primary float-end jh-create-entity create-email-template"
      [routerLink]="['/admin/ic-email-template/new']"
    >
      <fa-icon [icon]="'plus'"></fa-icon>
      <span icTranslate="emailTemplateAdmin.emailTemplate.home.createLabel"> Create new Email Template </span>
    </button>
  </h2>
  <ic-alert></ic-alert>
  <br />
  <div class="alert alert-warning" *ngIf="emailTemplates?.length! === 0">
    <span icTranslate="emailTemplateAdmin.emailTemplate.home.notFound">No emailTemplates found</span>
  </div>
  <div class="table-responsive" *ngIf="emailTemplates?.length! > 0">
    <table class="table table-striped">
      <thead>
        <tr icSort [(predicate)]="predicate" [(ascending)]="reverse" [callback]="transition.bind(this)">
          <th scope="col" icSortBy="emailTemplateName">
            <span icTranslate="emailTemplateAdmin.emailTemplate.emailTemplateName">Email Template Name</span>
            <fa-icon [icon]="'sort'"></fa-icon>
          </th>
          <th scope="col" icSortBy="fromEmail">
            <span icTranslate="emailTemplateAdmin.emailTemplate.fromEmail">From Email</span> <fa-icon [icon]="'sort'"></fa-icon>
          </th>
          <th scope="col" icSortBy="fromName">
            <span icTranslate="emailTemplateAdmin.emailTemplate.fromName">From Name</span> <fa-icon [icon]="'sort'"></fa-icon>
          </th>
          <th scope="col" icSortBy="emailSubject">
            <span icTranslate="emailTemplateAdmin.emailTemplate.emailSubject">Email Subject</span> <fa-icon [icon]="'sort'"></fa-icon>
          </th>
          <th scope="col" icSortBy="createdBy">
            <span icTranslate="emailTemplateAdmin.emailTemplate.createdBy">Created By</span> <fa-icon [icon]="'sort'"></fa-icon>
          </th>
          <th scope="col" icSortBy="createdDate">
            <span icTranslate="emailTemplateAdmin.emailTemplate.createdDate">Created Date</span> <fa-icon [icon]="'sort'"></fa-icon>
          </th>
          <th scope="col" icSortBy="updatedBy">
            <span icTranslate="emailTemplateAdmin.emailTemplate.updatedBy">Updated By</span> <fa-icon [icon]="'sort'"></fa-icon>
          </th>
          <th scope="col" icSortBy="updatedDate">
            <span icTranslate="emailTemplateAdmin.emailTemplate.updatedDate">Updated Date</span> <fa-icon [icon]="'sort'"></fa-icon>
          </th>
          <th scope="col"></th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let emailTemplate of emailTemplates; trackBy: trackId">
          <td>{{ emailTemplate.emailTemplateName }}</td>
          <td>{{ emailTemplate.fromEmail }}</td>
          <td>{{ emailTemplate.fromName }}</td>
          <td>{{ emailTemplate.emailSubject }}</td>
          <td>{{ emailTemplate.createdBy }}</td>
          <td>{{ emailTemplate.createdDate | formatMediumDatetime }}</td>
          <td>{{ emailTemplate.updatedBy }}</td>
          <td>{{ emailTemplate.updatedDate | formatMediumDatetime }}</td>
          <td class="text-end">
            <div class="btn-group">
              <button type="submit" [routerLink]="['./', emailTemplate.id, 'view']" class="btn btn-info btn-sm">
                <fa-icon [icon]="'eye'"></fa-icon>
                <span class="d-none d-md-inline" icTranslate="entity.action.view">View</span></button
              >&nbsp;
              <button type="submit" [routerLink]="['./', emailTemplate.id, 'edit']" class="btn btn-primary btn-sm">
                <fa-icon [icon]="'pencil-alt'"></fa-icon>
                <span class="d-none d-md-inline" icTranslate="entity.action.edit">Edit</span></button
              >&nbsp;
              <button type="submit" (click)="delete(emailTemplate)" class="btn btn-danger btn-sm">
                <fa-icon icon="times"></fa-icon>
                <span class="d-none d-md-inline" icTranslate="entity.action.delete">Delete</span>
              </button>
            </div>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
  <div [hidden]="emailTemplates?.length! === 0">
    <div class="row justify-content-center">
      <ic-item-count [page]="page" [total]="totalItems" [maxSize]="5" [itemsPerPage]="itemsPerPage"></ic-item-count>
    </div>
    <div class="row justify-content-center">
      <ngb-pagination
        [collectionSize]="totalItems"
        [pageSize]="itemsPerPage"
        [(page)]="page"
        [pageSize]="itemsPerPage"
        [maxSize]="5"
        [rotate]="true"
        [boundaryLinks]="true"
        (pageChange)="loadPage(page)"
      ></ngb-pagination>
    </div>
  </div>
</div>
