import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { CustomToastService } from 'app/layouts/service/customtoast.service';
import { DateValidator } from 'app/shared/validator/date-validator.directive';
import { EitherOrValidator } from 'app/shared/validator/either-or-field.directive';
import { EnqCompany } from './model/company.model';
// import { EnquiryCompanyService } from './service/enquiry-company.service';

@Component({
  selector: 'n2-enquiry-company',
  templateUrl: './enquiry-company.component.html',
  styleUrls: ['./enquiry-company.component.scss']
})
export class EnquiryCompanyComponent implements OnInit {

  searchForm: FormGroup = this.fb.group({
    csnInput1: new FormControl('', Validators.pattern('[0-9a-zA-Z]{9,10}')),
    csnInput2: new FormControl(''),
    csnInput3: new FormControl(''),
    companyNameInput: new FormControl(''),
    startDateInput: new FormControl(),
    endDateInput: new FormControl(),
    allRecord: new FormControl(false)
  }, { 
    validators: [
      EitherOrValidator.eitherOr('csnInput1', 'companyNameInput'),
      DateValidator.dateLessThanOrEquals('startDateInput', 'endDateInput')
    ]
  });

  // boolean variables to determine if card/table/panels are shown
  isCollapsed = false;
  isByCsn: boolean;
  isByCompanyName: boolean;
  isCompanyFound: boolean;
  isPartialCsnFound: boolean;

  company: EnqCompany = null;
  resultList: EnqCompany[] = [];

  constructor(
    private fb: FormBuilder,
    private toastService: CustomToastService
  ) {
    //
  }

  ngOnInit(): void {
    //
  }

  patchDefaultDates(): void {
    if (!this.searchForm.controls['csnInput1'].value && !this.searchForm.controls['companyNameInput'].value) {
      // if no search input then clear default dates
      this.searchForm.controls['startDateInput'].reset();
      this.searchForm.controls['endDateInput'].reset();
    } else {
      const defaultStartDate = new Date();
      defaultStartDate.setMonth(defaultStartDate.getMonth() - 3);
  
      const defaultEndDate = new Date();
      defaultEndDate.setMonth(defaultEndDate.getMonth() + 6);
  
      this.searchForm.patchValue({
        startDateInput: defaultStartDate,
        endDateInput: defaultEndDate
      });
    }
  }

  onBlurCsn(): void{
    // if first portion of csn is empty, onBlur set 2 and 3 empty
    if (this.searchForm.controls['csnInput1'].value === "") {
      this.searchForm.patchValue({
        csnInput2: "",
        csnInput3: ""
      })
    }
  }

  onKeyupCsn(): void {
    // if user types in csn input then clear company name
    this.searchForm.controls['companyNameInput'].reset("");

    // patch default dates if all record is unchecked
    if (!this.searchForm.controls['allRecord'].value) {
      this.patchDefaultDates();
    }
  }

  onKeyupCompanyName(): void {
    // if user types in company name then clear csn inputs
    this.searchForm.patchValue({
      csnInput1: "",
      csnInput2: "",
      csnInput3: ""
    })

    // patch default dates if all record is unchecked
    if (!this.searchForm.controls['allRecord'].value) {
      this.patchDefaultDates();
    }
  }

  onInputOrSelectPeriod(): void {
    // if user types in or selects date then uncheck all record
    this.searchForm.controls['allRecord'].reset(false);
  }

  retrieve(): void {
    this.isByCsn = false;
    this.isByCompanyName = false;

    this.isCompanyFound = false;
    this.isPartialCsnFound = false;

    if (this.searchForm.controls['csnInput1'].value) {
      this.isByCsn = true;
    } else if (this.searchForm.controls['companyNameInput'].value) {
      this.isByCompanyName = true;
    }

    if (this.isByCsn) {
      const csnInput = this.searchForm.controls['csnInput1'].value.concat(this.searchForm.controls['csnInput2'].value, this.searchForm.controls['csnInput3'].value)

      if (csnInput.length <= 10) {
        if (csnInput === "12345678E") {
          // show sample partial csn search result
          this.isPartialCsnFound = true;
        } else {
          this.toastService.add({
            severity: 'error',
            summary: 'Unable to find Company with the partial CSN'
          });
        }
      } else {
        if (csnInput === "12345678EPTE01") {
          // show sample company particular && default tab
          this.isCompanyFound = true;
        } else {
          this.toastService.add({
            severity: 'error',
            summary: 'Unable to find Company with the specified CSN'
          });
        }
      }
    } else if (this.isByCompanyName) {
      if (this.searchForm.controls['companyNameInput'].value === "NCS Pte Ltd") {
        // show sample company particular && default tab
        this.isCompanyFound = true;
      } else {
        this.toastService.add({
          severity: 'error',
          summary: 'Unable to find Company with the specified name'
        });
      }
    }
  }

}
