import { ValidatorFn, AbstractControl } from '@angular/forms';
import { validateEmail } from '../../ng-connect/email/email-validate';

export function EmailValidator(): ValidatorFn {

  return (c: AbstractControl) : { [key: string]: boolean } | null => {
    let isValid = false;
    
    if (c.value) {
        isValid = validateEmail(c.value);
        if (isValid) {
          return {};
        } else {
          return { email: true };
        }
      } else {
        return {};
      }
  }
}
