import { Component, OnInit, ViewChild } from '@angular/core';
import { NSPAppConstants } from 'app/shared/constants/nspapp.constants';
import { FileUpload } from 'primeng/fileupload';
import { MessageService } from 'primeng/api';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { FileUploadComponent } from 'app/shared/component/file-upload/file-upload.component';
import { FileUploadValidator2 } from 'app/shared/validator/fileupload2.directive';

@Component({
  selector: 'n2-deduction-file-upload',
  templateUrl: './deduction-file-upload.component.html',
  styleUrls: ['./deduction-file-upload.component.scss']
})
export class DeductionFileUploadComponent implements OnInit {

  fileUploadFileLimit = 1;
  fileUploadForm: FormGroup = this.fb.group({
    file: new FormControl([], [ 
      FileUploadValidator2.maxFileLimit(this.fileUploadFileLimit),
      FileUploadValidator2.allowableExtension(".txt,.csv")])
  });

  @ViewChild("fileUpload")
  private n2FileUpload: FileUploadComponent

  constructor(
    private messageService: MessageService, 
    private fb: FormBuilder) { }

  ngOnInit(): void {
    //
  }

  isFormValid(): boolean {
    return this.fileUploadForm.valid && this.n2FileUpload.verifiedFiles.length > 0;
  }

  getVerifiedFiles(): any[] {
    return this.n2FileUpload.verifiedFiles;
  }

  cancel(): void {
    this.n2FileUpload.onClose();
    this.fileUploadForm.reset({file: []});
  }

  clearAndReset(): void {
    this.n2FileUpload.clearAndReset();
  }
}
